INSERT INTO `creature_text` VALUES (52204, 0, 0, 'Wow, wow, wow! I can\'t believe Sputtervalve actually let me drive this baby! Have I mentioned this is my first time flying? Don\'t worry though. I have everything under control.', 12, 0, 100, 0, 0, 0, 0, 51020, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 1, 0, 'Man, oh man...Just look at that amazing view! Are those LIONS? Oops, almost let go of the steering wheel there.', 12, 0, 100, 0, 0, 0, 0, 51021, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 2, 0, 'My dream is to be as brilliant as Sputtervalve someday. You should see his design for a two-wheeled tricycle!', 12, 0, 100, 0, 0, 0, 0, 51023, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 3, 0, 'Well here we are, back at the Crossroads, safe and sound.', 12, 0, 100, 0, 0, 0, 0, 51031, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 4, 0, 'Huh? Hold on, the engine stalled...', 12, 0, 100, 5, 0, 0, 0, 51024, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 5, 0, 'Whoa! What\'s going on?', 12, 0, 100, 432, 0, 0, 0, 51025, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 6, 0, 'No, no, no...this can\'t be happening. This wasn\'t part of the travel plan!', 12, 0, 100, 5, 0, 0, 0, 51026, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 7, 0, 'Auuugh! Looks like we\'re taking an unexpected detour! Hold on tight!', 12, 0, 100, 432, 0, 0, 0, 51027, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (52204, 8, 0, 'Maybe if I press this green button. No, what about this blue one? Hold on, let me try resplicing some wires. Ow!', 12, 0, 100, 432, 0, 0, 0, 51028, 0, 'Gyrotilt');
INSERT INTO `creature_text` VALUES (33127, 0, 0, 'The moonstalker rouses from its slumber with a deep growl.', 16, 0, 100, 0, 0, 0, 0, 34436, 0, 'Moonstalker');
