INSERT INTO `creature_text` VALUES (34602, 0, 0, 'Ahahahaha, you\'ve fallen into my trap, $n!', 14, 0, 100, 0, 0, 0, 0, 34837, 0, 'Kaliva');
INSERT INTO `creature_text` VALUES (61796, 0, 0, 'Walk with me. All three of you.', 12, 0, 100, 1, 0, 29574, 0, 61424, 0, '');
INSERT INTO `creature_text` VALUES (61796, 1, 0, 'I understand you want to join the Alliance.', 12, 0, 100, 0, 0, 29575, 0, 61425, 0, '');
INSERT INTO `creature_text` VALUES (61796, 2, 0, 'We always have need of allies. I\'d be a fool to turn you away, especially at a time of war.', 12, 0, 100, 1, 0, 29576, 0, 61426, 0, '');
INSERT INTO `creature_text` VALUES (61796, 3, 0, 'But there are a number of things I need to make clear to you.', 12, 0, 100, 0, 0, 29577, 0, 61427, 0, '');
INSERT INTO `creature_text` VALUES (61796, 4, 0, 'First: the races of the Alliance look out for one another.', 12, 0, 100, 0, 0, 29578, 0, 61429, 0, '');
INSERT INTO `creature_text` VALUES (61796, 5, 0, 'You are expected to provide aid to Alliance members in need, whether they be human, gnome, draenei... or pandaren like yourselves.', 12, 0, 100, 1, 0, 29579, 0, 61430, 0, '');
INSERT INTO `creature_text` VALUES (61796, 6, 0, 'It is a simple guideline, but a meaningful one. Do you understand?', 12, 0, 100, 0, 0, 29580, 0, 61432, 0, '');
INSERT INTO `creature_text` VALUES (61796, 7, 0, 'Good. Because that brings me to my second point.', 12, 0, 100, 273, 0, 29581, 0, 61434, 0, '');
INSERT INTO `creature_text` VALUES (61796, 8, 0, 'Not all pandaren have chosen the same path as you three.', 12, 0, 100, 0, 0, 29582, 0, 61435, 0, '');
INSERT INTO `creature_text` VALUES (61796, 9, 0, 'Some have sided with the Horde.', 12, 0, 100, 0, 0, 29583, 0, 61436, 0, '');
INSERT INTO `creature_text` VALUES (61796, 10, 0, 'They, along with the other barbarian clans of the Horde, are your new enemies.', 12, 0, 100, 1, 0, 29584, 0, 61437, 0, '');
INSERT INTO `creature_text` VALUES (61796, 11, 0, 'Those who you once considered friends, or even those you might have loved, are now your sworn adversaries.', 12, 0, 100, 274, 0, 29585, 0, 61438, 0, '');
INSERT INTO `creature_text` VALUES (61796, 12, 0, 'I am deeply sorry, but the battle lines have been drawn. I will not tolerate any fraternizing with the enemy, as you could expose our Alliance to danger.', 12, 0, 100, 5, 0, 29586, 0, 61439, 0, '');
INSERT INTO `creature_text` VALUES (61796, 13, 0, 'Do I make myself clear?', 12, 0, 100, 0, 0, 29587, 0, 61440, 0, '');
INSERT INTO `creature_text` VALUES (61792, 0, 0, 'Yes. Go on.', 12, 0, 100, 273, 0, 27390, 0, 61428, 0, '');
INSERT INTO `creature_text` VALUES (61792, 1, 0, 'Certainly.', 12, 0, 100, 273, 0, 27391, 0, 58137, 0, '');
INSERT INTO `creature_text` VALUES (61792, 2, 0, 'Yes... of course.', 12, 0, 100, 24, 0, 27392, 0, 61441, 0, '');
INSERT INTO `creature_text` VALUES (61315, 0, 0, 'The binding should only take a moment, but I\'ll be completely entranced.', 12, 0, 100, 0, 0, 0, 0, 60713, 0, '');
INSERT INTO `creature_text` VALUES (61315, 1, 0, 'Watch my back - I trust you can handle any interruptions.', 12, 0, 100, 0, 0, 0, 0, 60714, 0, '');
INSERT INTO `creature_text` VALUES (61315, 2, 0, 'All done. Did I miss anything?', 12, 0, 100, 0, 0, 0, 0, 60718, 0, '');
INSERT INTO `creature_text` VALUES (61305, 0, 0, 'LOREWALKER! You have something of mine.', 14, 0, 100, 0, 0, 0, 0, 60715, 0, '');
INSERT INTO `creature_text` VALUES (58572, 0, 0, 'You have given much of your time, patience, and love, $n. Behold your serpent now.', 12, 0, 100, 0, 0, 0, 0, 57338, 0, '');
INSERT INTO `creature_text` VALUES (58420, 0, 0, 'Your serpent will arrive in just a moment. Good luck!', 12, 0, 100, 0, 0, 0, 0, 57213, 0, '');
INSERT INTO `creature_text` VALUES (62087, 0, 0, 'You\'ve made a wise decision to join my Horde.', 12, 0, 100, 273, 0, 30746, 0, 61586, 0, '');
INSERT INTO `creature_text` VALUES (62087, 1, 0, 'There are some that would call us barbarians... the \"mongrel races\" of Azeroth.', 12, 0, 100, 396, 0, 30747, 0, 61587, 0, '');
INSERT INTO `creature_text` VALUES (62087, 2, 0, 'They are ignorant, and blind to reality. Look around you, pandaren.', 12, 0, 100, 274, 0, 30748, 0, 61588, 0, '');
INSERT INTO `creature_text` VALUES (62087, 3, 0, 'You will find no better partner in battle than an orc.', 12, 0, 100, 397, 0, 30749, 0, 61589, 0, '');
INSERT INTO `creature_text` VALUES (62087, 4, 0, 'The tauren have made themselves useful. Look - this one is a shaman.', 12, 0, 100, 6, 0, 30750, 0, 61590, 0, '');
INSERT INTO `creature_text` VALUES (62087, 5, 0, 'Even a blood elf can hold a sword.', 12, 0, 100, 396, 0, 30751, 0, 61592, 0, '');
INSERT INTO `creature_text` VALUES (62087, 6, 0, 'Do you see it? Everyone in my Horde earns their keep. You and your friends will be no exception.', 12, 0, 100, 396, 0, 30752, 0, 61593, 0, '');
INSERT INTO `creature_text` VALUES (62087, 7, 0, 'Do you understand this, pandaren?', 12, 0, 100, 6, 0, 30753, 0, 61594, 0, '');
INSERT INTO `creature_text` VALUES (62087, 8, 0, 'Hm. Good.', 12, 0, 100, 0, 0, 30754, 0, 61596, 0, '');
INSERT INTO `creature_text` VALUES (62087, 9, 0, 'Moving on. I know that you are not the only pandaren to escape from that island.', 12, 0, 100, 396, 0, 30755, 0, 61597, 0, '');
INSERT INTO `creature_text` VALUES (62087, 10, 0, 'Some of your kind chose to join the Alliance.', 12, 0, 100, 14, 0, 30756, 0, 61598, 0, '');
INSERT INTO `creature_text` VALUES (62087, 11, 0, 'They are now your enemies.', 12, 0, 100, 397, 0, 30757, 0, 61599, 0, '');
INSERT INTO `creature_text` VALUES (62087, 12, 0, 'You may have had friends and kin who chose to cast their lot with the enemies of the Horde. They are no longer your friends... no longer your family.', 12, 0, 100, 396, 0, 30758, 0, 61600, 0, '');
INSERT INTO `creature_text` VALUES (62087, 13, 0, 'The minute they put on that Alliance tabard, they died. I will NOT tolerate any lingering ties across enemy lines. Traitors to the Horde will die a traitor\'s death!', 12, 0, 100, 274, 0, 30759, 0, 61601, 0, '');
INSERT INTO `creature_text` VALUES (62087, 14, 0, 'Do I make myself clear?', 12, 0, 100, 5, 0, 30760, 0, 61440, 0, '');
INSERT INTO `creature_text` VALUES (62087, 15, 0, 'Good.', 12, 0, 100, 0, 0, 30761, 0, 61604, 0, '');
INSERT INTO `creature_text` VALUES (62087, 16, 0, 'Meet me in the Valley of Honor. I have a gift for you.', 12, 0, 100, 396, 0, 30762, 0, 61605, 0, '');
INSERT INTO `creature_text` VALUES (62081, 0, 0, 'Yes, warchief.', 12, 0, 100, 2, 0, 27290, 0, 61595, 0, '');
INSERT INTO `creature_text` VALUES (62081, 1, 0, 'I... I believe so. Yes.', 12, 0, 100, 273, 0, 27291, 0, 61603, 0, '');
INSERT INTO `creature_text` VALUES (64347, 0, 0, 'Wow! I never knew this was back here.', 12, 0, 100, 0, 0, 0, 0, 64246, 0, '');
INSERT INTO `creature_text` VALUES (64347, 1, 0, 'Jumping jackhoppers! We found it! We really found it! It\'s Old Man Thistle\'s hidden treasure! Oh boy oh boy oh boy!!!', 12, 0, 100, 0, 0, 0, 0, 64251, 0, '');
INSERT INTO `creature_text` VALUES (66138, 0, 0, 'As a monk, your reflexes are among your greatest resources.  Often, the ability to think and move quickly can save your life.', 12, 0, 100, 0, 0, 0, 0, 67434, 0, '');
INSERT INTO `creature_text` VALUES (66138, 1, 0, 'Bow to me and we shall begin.', 12, 0, 100, 0, 0, 0, 0, 67144, 0, '');
INSERT INTO `creature_text` VALUES (66138, 2, 0, 'Master Cheng is burning you alive!  Move!', 41, 0, 100, 0, 0, 0, 0, 67311, 0, '');
INSERT INTO `creature_text` VALUES (66138, 3, 0, 'Well done.  Remember that swiftness of mind and body can keep you alive.', 12, 0, 100, 0, 0, 0, 0, 67386, 0, '');
INSERT INTO `creature_text` VALUES (64528, 0, 0, 'Ah, another stranger from beyond the mists.', 12, 0, 100, 396, 0, 29030, 0, 65214, 0, '');
INSERT INTO `creature_text` VALUES (64536, 0, 0, 'Soon they will overwhelm this continent! Where do we draw the line?', 12, 0, 100, 396, 0, 28417, 0, 65216, 0, '');
INSERT INTO `creature_text` VALUES (64540, 0, 0, 'We\'re here to lend our aid. We can help you rebuild the Vale.', 12, 0, 100, 396, 0, 38017, 0, 75749, 0, '');
INSERT INTO `creature_text` VALUES (64536, 1, 0, 'We have witnessed your \"help\" in the Jade Forest. This \"Horde,\" this \"Alliance\" of yours - you have no control over your own nature. You leave misery in your wake.', 12, 0, 100, 396, 0, 28418, 0, 65217, 0, '');
INSERT INTO `creature_text` VALUES (64528, 1, 0, 'Ten thousand years ago, the last Pandaren Emperor came seeking my advice. He, too, was filled with conflict. Let us see if you are correct, Taran. Let us see how this stranger deals with turmoil of the soul.', 12, 0, 100, 396, 0, 29031, 0, 65215, 0, '');
INSERT INTO `creature_text` VALUES (64536, 2, 0, 'Violence. See? They are filled with it.', 12, 0, 100, 396, 0, 28419, 0, 65248, 0, '');
INSERT INTO `creature_text` VALUES (64540, 1, 0, 'Yes... we are fighters. But we come from a violent land. We fought off demons, we fought off death, we even fought against the very end of the world!', 12, 0, 100, 396, 0, 27589, 0, 65251, 0, '');
INSERT INTO `creature_text` VALUES (64537, 0, 0, 'It is true. They are strong. There is much they can teach us.', 12, 0, 100, 5, 0, 29050, 0, 65258, 0, '');
INSERT INTO `creature_text` VALUES (64528, 2, 0, 'We can agree it is noble to fight for a righteous cause: to defend home or family. But what of vengeance? What of anger? I sense these emotions run deep in this one.', 12, 0, 100, 396, 0, 29033, 0, 65273, 0, '');
INSERT INTO `creature_text` VALUES (64540, 2, 0, 'Anger is a part of us all, yes. But we value control. The orcs were once driven solely by anger - they lived in a state of bloodlust - but they conquered it.', 12, 0, 100, 396, 0, 27590, 0, 65322, 0, '');
INSERT INTO `creature_text` VALUES (64536, 3, 0, 'All you seek to conquer is each other!', 12, 0, 100, 396, 0, 28420, 0, 65323, 0, '');
INSERT INTO `creature_text` VALUES (64537, 1, 0, 'To live without anger is not to live at all. It is ours to overcome. In defeating our anger, we draw great strength.', 12, 0, 100, 5, 0, 29051, 0, 65324, 0, '');
INSERT INTO `creature_text` VALUES (64528, 3, 0, 'Well spoken, Zhi. The strangers from beyond the mists have no shortage of passions, but let us see if they indeed control them. Beyond anger lies hatred, the wellspring of all misery. What is this? Your hatred has a face!', 12, 0, 100, 396, 0, 29035, 0, 65326, 0, '');
INSERT INTO `creature_text` VALUES (64540, 3, 0, 'That... that looks just like Garrosh Hellscream!', 12, 0, 100, 396, 0, 27592, 0, 65341, 0, '');
INSERT INTO `creature_text` VALUES (64536, 4, 0, 'See? See how these outsiders hate one another! They wear their hatred to battle and carry it to their graves!', 12, 0, 100, 396, 0, 28421, 0, 65327, 0, '');
INSERT INTO `creature_text` VALUES (64540, 4, 0, 'No, that\'s not true - there is so much more to us than this!', 12, 0, 100, 396, 0, 27593, 0, 65328, 0, '');
INSERT INTO `creature_text` VALUES (64537, 2, 0, 'How interesting. They bring much to our land, but they also bring the shadow of war.', 12, 0, 100, 5, 0, 29052, 0, 65329, 0, '');
INSERT INTO `creature_text` VALUES (64540, 5, 0, 'Peace will come. I swear it. And old hatreds will go away. You have to believe me.', 12, 0, 100, 396, 0, 27594, 0, 65330, 0, '');
INSERT INTO `creature_text` VALUES (64528, 4, 0, 'This one has indeed proven mastery over anger. Between the young cub\'s words and the actions of this hero, I think I understand. Those from beyond the mists are hardened by battle. They are scarred, yes, but they have learned much. I trust them.', 12, 0, 100, 396, 0, 29037, 0, 65350, 0, '');
INSERT INTO `creature_text` VALUES (66797, 0, 0, 'Who holds this swarm? Show yourself! Answer to the Klaxxi.', 14, 0, 100, 6, 0, 30910, 0, 66024, 0, '');
INSERT INTO `creature_text` VALUES (66791, 0, 0, 'Treasonous Klaxxi... her magnificence was right about you.', 14, 0, 100, 1, 0, 29295, 0, 67838, 0, '');
INSERT INTO `creature_text` VALUES (66797, 1, 0, 'Speak your name puppet, that our annals will be complete.', 14, 0, 100, 396, 0, 30911, 0, 66025, 0, '');
INSERT INTO `creature_text` VALUES (66791, 1, 0, 'Grand Vizier Zor\'lok, voice of the Empress.', 14, 0, 100, 6, 0, 29296, 0, 67840, 0, '');
INSERT INTO `creature_text` VALUES (66797, 2, 0, 'Your \"Empress\" has fallen. The Klaxxi have pronounced judgment. Stand aside or be brushed away.', 14, 0, 100, 0, 0, 30912, 0, 66026, 0, '');
INSERT INTO `creature_text` VALUES (66791, 2, 0, 'The Klaxxi are to be disbanded, by the will of the Empress.', 14, 0, 100, 0, 0, 29297, 0, 67842, 0, '');
INSERT INTO `creature_text` VALUES (66797, 3, 0, 'Blaspheme!', 14, 0, 100, 0, 0, 30913, 0, 66027, 0, '');
INSERT INTO `creature_text` VALUES (66791, 3, 0, 'The desperate Klaxxi have resorted to digging up fossils. Very well: let us finish this.', 14, 0, 100, 1, 0, 29298, 0, 67844, 0, '');
INSERT INTO `creature_text` VALUES (66791, 4, 0, '\"Unscathed,\" indeed.', 14, 0, 100, 1, 0, 29299, 0, 67846, 0, '');
INSERT INTO `creature_text` VALUES (66791, 5, 0, 'Forward! The Klaxxi reveal themselves as traitors. Decimate them, and leave nothing behind.', 14, 0, 100, 1, 0, 29300, 0, 67847, 0, '');
INSERT INTO `creature_text` VALUES (66800, 0, 0, 'Wakener, your deeds have earned you the trust of the Klaxxi.', 12, 0, 100, 0, 0, 30312, 0, 67848, 0, '');
INSERT INTO `creature_text` VALUES (66800, 1, 0, 'You are to be rewarded. Walk with me.', 12, 0, 100, 0, 0, 30313, 0, 66038, 0, '');
INSERT INTO `creature_text` VALUES (66800, 2, 0, 'We mantid are an elder race. The pandaren you associate with - they are but children. They have their role to play.', 12, 0, 100, 0, 0, 30314, 0, 66040, 0, '');
INSERT INTO `creature_text` VALUES (66800, 3, 0, 'Each cycle, our young swarm their walls. The pandaren slay the weak. The strong return.', 12, 0, 100, 0, 0, 30315, 0, 66041, 0, '');
INSERT INTO `creature_text` VALUES (66800, 4, 0, 'With each generation, we grow ever stronger.', 12, 0, 100, 0, 0, 30316, 0, 66042, 0, '');
INSERT INTO `creature_text` VALUES (66800, 5, 0, 'Before your history began, our empire was vast. We shared this world with our sister kingdoms, Ahn\'Qiraj and Azjol-Nerub.', 12, 0, 100, 0, 0, 30317, 0, 66044, 0, '');
INSERT INTO `creature_text` VALUES (66800, 6, 0, 'Our Gods were many, and powerful. We mantid worshipped the seven heads of Y\'shaarj.', 12, 0, 100, 0, 0, 30318, 0, 66045, 0, '');
INSERT INTO `creature_text` VALUES (66800, 7, 0, 'Great was the Old One, and terrible was His wrath. He consumed hope and begat despair; He inhaled courage and breathed fear.', 12, 0, 100, 0, 0, 30319, 0, 66046, 0, '');
INSERT INTO `creature_text` VALUES (66800, 8, 0, 'When the usurpers came - the ones you call \"Titans\" - Y\'shaarj was destroyed.', 12, 0, 100, 0, 0, 30320, 0, 66047, 0, '');
INSERT INTO `creature_text` VALUES (66800, 9, 0, 'His last terrible breath has haunted this land ever since, but the shadows he left behind are mere whispers of his former glory.', 12, 0, 100, 0, 0, 30321, 0, 66048, 0, '');
INSERT INTO `creature_text` VALUES (66800, 10, 0, 'I tell you now, because you have earned this warning.', 12, 0, 100, 0, 0, 30322, 0, 66049, 0, '');
INSERT INTO `creature_text` VALUES (66800, 11, 0, 'Your gods are not our gods, outsider.', 12, 0, 100, 0, 0, 30323, 0, 66050, 0, '');
INSERT INTO `creature_text` VALUES (66800, 12, 0, 'If the Old Ones ever return, we mantid will once again stand by their side.', 12, 0, 100, 0, 0, 30324, 0, 66051, 0, '');
INSERT INTO `creature_text` VALUES (66800, 13, 0, 'The wisest among you will do the same.', 12, 0, 100, 0, 0, 30325, 0, 66052, 0, '');
INSERT INTO `creature_text` VALUES (54404, 0, 0, 'Very well, $n.', 12, 0, 100, 0, 0, 0, 0, 7649, 0, '');
INSERT INTO `creature_text` VALUES (54404, 1, 0, 'Impudent whelps! You\'ve rushed headlong to your own deaths! See now, the master stirs!\n', 14, 0, 100, 0, 0, 0, 0, 7655, 0, '');
INSERT INTO `creature_text` VALUES (54404, 2, 0, 'Behold Ragnaros - the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', 14, 0, 100, 0, 0, 0, 0, 7657, 0, '');
INSERT INTO `creature_text` VALUES (54404, 3, 0, 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', 14, 0, 100, 0, 0, 0, 0, 7661, 0, '');
INSERT INTO `creature_text` VALUES (63754, 0, 0, 'I don\'t know about this \"Alliance\", but you look trustworthy. I\'ll gather everyone up and meet you when we\'re ready to rebuild!', 12, 0, 100, 1, 0, 0, 0, 67234, 0, '');
INSERT INTO `creature_text` VALUES (63542, 0, 0, 'Someone has come to save us? Perhaps all is not lost...', 12, 0, 100, 1, 0, 0, 0, 67232, 0, '');
INSERT INTO `creature_text` VALUES (63751, 0, 0, 'I don\'t know about this \"Horde\", but you look trustworthy. I\'ll gather everyone up and meet you when we\'re ready to rebuild!', 12, 0, 100, 1, 0, 0, 0, 67236, 0, '');
