INSERT INTO `creature_text` VALUES (55694, 0, 0, 'Hey $p! This way to Morning Breeze Village.', 12, 0, 100, 0, 0, 27294, 0, 54196, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (57670, 0, 0, 'These are the densest stones across all of Shen-zin Su, yet they are as nothing before my mighty head.', 12, 0, 100, 1, 0, 0, 0, 56355, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (54786, 0, 0, 'Welcome, Huo. The people have missed your warmth.', 12, 0, 100, 2, 0, 27788, 0, 60608, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 1, 0, 'You have conquered every challenge I put before you, $n. You have found Huo and brought him safely to the temple.', 12, 0, 100, 1, 0, 27789, 0, 60600, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 2, 0, 'There is a much larger problem we face now, my students. Shen-zin Su is in pain. If we do not act, the very land on which we stand could die, and all of us with it.', 12, 0, 100, 1, 0, 27790, 0, 60601, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 3, 0, 'We need to speak to Shen-zin Su and discover how to heal it. And to do that, we need the four elemental spirits returned. Huo was the first.', 12, 0, 100, 1, 0, 27791, 0, 60602, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (61127, 0, 0, 'On it!', 12, 0, 100, 0, 0, 27306, 0, 60606, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (61126, 0, 0, 'Yes master.', 12, 0, 100, 2, 0, 27406, 0, 60607, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60250, 0, 0, 'Hey there! Are you the one that brought back the fire spirit?', 12, 0, 100, 3, 0, 0, 0, 59875, 0, 'Cai');
INSERT INTO `creature_text` VALUES (60249, 0, 0, 'Was he hot?  I bet he was hot!', 12, 0, 100, 6, 0, 0, 0, 59876, 0, 'Deng');
INSERT INTO `creature_text` VALUES (60250, 1, 0, 'Of course he was hot! Don\'t be dumb, Deng.', 12, 0, 100, 5, 0, 0, 0, 59877, 0, 'Cai');
INSERT INTO `creature_text` VALUES (60249, 1, 0, 'I bet you\'re really strong, huh?  You could probably kick that bridge right in half if you wanted to!', 12, 0, 100, 6, 0, 0, 0, 59879, 0, 'Deng');
INSERT INTO `creature_text` VALUES (60250, 2, 0, 'Nuh uh. Bet they could for sure kick you over that hill, though! Hah!', 12, 0, 100, 274, 0, 0, 0, 59880, 0, 'Cai');
INSERT INTO `creature_text` VALUES (60249, 2, 0, 'Bye!', 12, 0, 100, 3, 0, 0, 0, 59884, 0, 'Deng');
INSERT INTO `creature_text` VALUES (60250, 3, 0, 'Okay bye.', 12, 0, 100, 3, 0, 0, 0, 59878, 0, 'Cai');
INSERT INTO `creature_text` VALUES (57638, 1, 0, 'Many have tested my claim, and I yet stand proven.', 12, 0, 100, 2, 0, 0, 0, 56334, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (57638, 0, 0, 'The reeds of the Singing Pools are the hardest in all of the land, but they are as air before my mighty brow.', 12, 0, 100, 1, 0, 0, 0, 56333, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (54975, 0, 0, 'I have to admit, that looked pretty fun!', 12, 0, 100, 0, 0, 27394, 0, 66056, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (54975, 1, 0, 'And it looks to me like you made a new friend.', 12, 0, 100, 0, 0, 27395, 0, 66057, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (57712, 0, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Dai-Lo Farmstead.', 12, 0, 100, 3, 0, 0, 0, 56406, 0, 'Delivery Cart Tender');
INSERT INTO `creature_text` VALUES (57712, 1, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Temple of Five Dawns.', 12, 0, 100, 3, 0, 0, 0, 56415, 0, 'Delivery Cart Tender');
INSERT INTO `creature_text` VALUES (57669, 0, 0, 'Our crafters build the finest wooden planks.  They would weather the most brutal of storms.  But they are as water before the storm of my crushing skull.', 12, 0, 100, 1, 0, 0, 0, 56343, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (57669, 1, 0, 'Nothing made by pandaren hands can withstand me.', 12, 0, 100, 2, 0, 0, 0, 56344, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (55539, 0, 0, '%s snorts loudly, and continues sleeping.', 16, 0, 100, 0, 0, 0, 0, 54991, 0, 'Wugou');
INSERT INTO `creature_text` VALUES (55477, 0, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 0, 9464, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (57692, 1, 0, 'There is only one thing to do.  I must smash it!  Once and for all, all shall know the power of my mighty brow.', 12, 0, 100, 1, 0, 0, 0, 56361, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (57692, 2, 0, 'Oh... my head... it hurts so badly!  I\'ve... the pillar must contain enormous power... perhaps stronger even than my brow...', 12, 0, 100, 18, 0, 0, 0, 56362, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (57692, 3, 0, 'I\'m... I\'m just going to lie down a while... until this headache goes away.', 12, 0, 100, 0, 0, 0, 0, 56364, 0, 'Jojo Ironbrow');
INSERT INTO `creature_text` VALUES (34692, 0, 3, 'I was wondering when I\'d see you again, $n.', 12, 0, 100, 0, 0, 0, 0, 36726, 0, '');
INSERT INTO `creature_text` VALUES (34692, 0, 4, 'It\'s not much, but it just might keep your green skin intact.', 12, 0, 100, 0, 0, 0, 0, 36727, 0, '');
INSERT INTO `creature_text` VALUES (34692, 0, 5, 'Hope you don\'t mind the extra fortitude, boss-$g man : lady;.', 12, 0, 100, 0, 0, 0, 0, 36724, 0, '');
INSERT INTO `creature_text` VALUES (34692, 0, 6, 'Just wanted to let you know that we\'re all counting on you!', 12, 0, 100, 0, 0, 0, 0, 36728, 0, '');
INSERT INTO `creature_text` VALUES (34692, 0, 7, 'Light be with you... for the very low price of a one silver tithe!', 12, 0, 100, 0, 0, 0, 0, 36722, 0, '');
INSERT INTO `creature_text` VALUES (55744, 0, 0, 'Wait!', 12, 0, 100, 5, 0, 27400, 0, 20520, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (55744, 1, 0, 'We need to wait for the winds to settle, then make a break for the cover of the far hallway.', 12, 0, 100, 1, 0, 27401, 0, 54356, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (55744, 2, 0, 'Wait for another opening. I\'ll meet you on the far side.', 12, 0, 100, 1, 0, 27402, 0, 54357, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (55595, 0, 0, 'Dafeng! What\'s wrong? Why are you hiding back here?', 12, 0, 100, 1, 0, 27403, 0, 54362, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (64506, 0, 0, 'That\'s the way to do it!', 12, 0, 100, 0, 0, 27380, 0, 64766, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (64506, 1, 0, 'You\'ve done it!  Now quickly, lets attack it while it\'s grounded.', 12, 0, 100, 0, 0, 27381, 0, 64767, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (64506, 1, 1, 'It\'s down!  Let\'s finish it off.', 12, 0, 100, 0, 0, 27382, 0, 64768, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56686, 5, 0, 'You\'ve come far, my young student. I see within you a great hero. I leave the fate of this land to you.', 12, 0, 100, 396, 0, 27812, 0, 55533, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (56661, 5, 0, 'Of course, Shen-zin Su!  But your shell is large, and I do not know where this thorn could be.', 14, 0, 100, 1, 0, 27436, 0, 55571, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56661, 6, 0, 'We will find it, and we will remove it.  You have our word!', 14, 0, 100, 1, 0, 27437, 0, 55573, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56661, 7, 0, 'We\'ll know soon enough.', 12, 0, 100, 1, 0, 27438, 0, 55576, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56661, 8, 0, 'It is a boat... a whole airship!  That\'s a bigger thorn than I was expecting.', 12, 0, 100, 1, 0, 27439, 0, 55578, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56661, 9, 0, 'Someone has crashed into our island.  Removing this thorn may be more complicated than we thought.', 12, 0, 100, 1, 0, 27440, 0, 55581, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56661, 10, 0, 'We should let Elder Shaopai know and then plan our next move.', 12, 0, 100, 1, 0, 27441, 0, 55582, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56676, 0, 0, 'I am in pain, but it warms my heart that Liu Lang\'s grandchildren have not forgotten me.', 12, 0, 100, 0, 0, 27822, 0, 55550, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (56676, 1, 0, 'There is a thorn in my side.  I cannot remove it.', 12, 0, 100, 0, 0, 27823, 0, 55568, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (56676, 2, 0, 'The pain is unbearable, and I can no longer swim straight.', 12, 0, 100, 0, 0, 27824, 0, 55569, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Please grandchildren, can you remove this thorn?  I cannot do so on my own.', 12, 0, 100, 0, 0, 27825, 0, 55570, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (56676, 4, 0, 'It is in the forest where your feet do not walk.  Continue along the mountains and you will find it.', 12, 0, 100, 0, 0, 27826, 0, 55572, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (56676, 5, 0, 'Thank you, grandchildren.', 12, 0, 100, 0, 0, 27827, 0, 63407, 0, 'Shen-zin Su');
INSERT INTO `creature_text` VALUES (59986, 3, 0, 'The Alliance? If the crashed ship is theirs, we need to contact them. Jojo, would you come with me?', 12, 0, 100, 6, 0, 27419, 0, 59583, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59988, 3, 0, 'We need to dislodge that ship, Korga.  If we help your crewmates, can you help us?', 12, 0, 100, 1, 0, 27333, 0, 59584, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (59988, 4, 0, 'Well, we\'ve got a master person-finder here, eh $p?  We\'re on it.', 12, 0, 100, 1, 0, 27334, 0, 59586, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (60042, 0, 0, 'Wei, are these friends of yours?', 12, 0, 100, 6, 0, 28120, 0, 54956, 0, 'Korga Strongmane');
INSERT INTO `creature_text` VALUES (60042, 1, 0, 'Korga Strongmane, once prisoner of the Alliance, now free and woefully unarmed tauren. Good to meet you, pandaren.', 12, 0, 100, 66, 0, 28121, 0, 59555, 0, 'Korga Strongmane');
INSERT INTO `creature_text` VALUES (60042, 2, 0, 'Gladly, my new friend. Our engineer is still in the wilds though - we\'ll need to rescue him first.', 12, 0, 100, 1, 0, 28122, 0, 59585, 0, 'Korga Strongmane');
INSERT INTO `creature_text` VALUES (55943, 0, 0, 'Never met them before in my life. Pandaren don\'t really come in any bad flavors though.', 12, 0, 100, 1, 0, 28090, 0, 59554, 0, 'Wei Palerage');
INSERT INTO `creature_text` VALUES (58506, 0, 0, 'All right, ladies! Watch this!', 14, 0, 100, 0, 0, 0, 0, 67149, 0, '');
INSERT INTO `creature_text` VALUES (58506, 1, 0, 'Impressive! I\'m going to have to watch out for you.', 12, 0, 100, 0, 0, 0, 0, 67150, 0, '');
INSERT INTO `creature_text` VALUES (58508, 0, 0, 'Let\'s both do our best!', 14, 0, 100, 0, 0, 0, 0, 67161, 0, '');
INSERT INTO `creature_text` VALUES (58508, 1, 0, 'I lost again! I\'d better keep training.', 12, 0, 100, 0, 0, 0, 0, 67174, 0, '');
INSERT INTO `creature_text` VALUES (58510, 0, 0, 'I feel a rush of energy coming on. Let\'s do this!', 14, 0, 100, 0, 0, 0, 0, 67183, 0, '');
INSERT INTO `creature_text` VALUES (58510, 1, 0, 'Darnit, I lost! Oof... I feel tired...', 12, 0, 100, 0, 0, 0, 0, 67184, 0, '');
INSERT INTO `creature_text` VALUES (58511, 0, 0, 'Let us begin. Good luck.', 14, 0, 100, 0, 0, 0, 0, 67193, 0, '');
INSERT INTO `creature_text` VALUES (58511, 1, 0, 'You have bested me fairly. Well done.', 12, 0, 100, 0, 0, 0, 0, 67195, 0, '');
INSERT INTO `creature_text` VALUES (50780, 0, 0, 'The thundering depths call to you...', 12, 0, 100, 0, 0, 0, 0, 66130, 0, 'Sahn Tidehunter');
INSERT INTO `creature_text` VALUES (60244, 0, 0, 'You\'re departing so soon?  I am envious of your prowess, $c. Good luck to you.', 12, 0, 100, 2, 0, 0, 0, 59711, 0, 'Trainee Guang');
INSERT INTO `creature_text` VALUES (54567, 0, 0, 'A challenge in compassion and humility. By helping Merchant Lorvo, it would seem you\'ve passed, $c. Master Shang would be pleased.', 12, 0, 100, 2, 0, 27404, 0, 60599, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59652, 0, 0, 'Meet me up in the cave if you would, friend.', 12, 0, 100, 396, 0, 27397, 0, 59147, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (54786, 4, 0, 'Ji, I\'d like you to go to the Dai-Lo Farmstead in search of Wugou, the spirit of earth.', 12, 0, 100, 1, 0, 27792, 0, 60603, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 5, 0, 'Aysa, I want you to go to the Singing Pools to find Shu, the spirit of water.', 12, 0, 100, 1, 0, 27793, 0, 60604, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 6, 0, 'And $n, you shall be the hand that guides us all. Speak with me for a moment before you join Aysa at the Singing Pools to the east.', 12, 0, 100, 1, 0, 27794, 0, 60605, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 7, 0, 'You\'ve returned with the spirits of water and earth. You make an old master proud.', 12, 0, 100, 1, 0, 27776, 0, 55295, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 8, 0, 'Wugou and Shu are welcome here. We will care for them well.', 12, 0, 100, 1, 0, 27777, 0, 55296, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 9, 0, 'The only remaining spirit is Dafeng, who hides somewhere across the Dawning Span to the west.', 12, 0, 100, 1, 0, 27778, 0, 55297, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (54786, 10, 0, 'I can carry you to the top of the temple. Ji awaits you there to lead the way.', 12, 0, 100, 1, 0, 27779, 0, 55298, 0, 'Master Shang Xi');
INSERT INTO `creature_text` VALUES (56416, 1, 0, 'Get clear when it when it raises its weapon, it\'s even stronger than it looks.', 12, 0, 100, 0, 0, 27385, 0, 64849, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56416, 2, 0, 'It\'s calling allies!  I\'ll keep him busy while you handle them.', 12, 0, 100, 0, 0, 27386, 0, 64850, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56416, 3, 0, 'We\'re wearing him down.  Keep it up!', 12, 0, 100, 0, 0, 27387, 0, 64852, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56416, 4, 0, 'I don\'t know what I would have done without you.  It\'s good that you came.', 12, 0, 100, 0, 0, 27388, 0, 64853, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60729, 4, 0, 'Ji... if this is what you must do, I will not stop you, but we could lose everything. I hope you\'re right.', 12, 0, 100, 1, 0, 27416, 0, 60194, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60729, 5, 0, 'Aysa sighs.', 16, 0, 100, 274, 0, 0, 0, 60211, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (60741, 0, 0, 'Aysa, this is our only option. We blow the ship free, then we heal his wound. We have no other solution.', 12, 0, 100, 1, 0, 27329, 0, 60189, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (60741, 1, 0, 'So we just wait until we think of something? Shen-zin Su is dying! Doing nothing risks everything!', 12, 0, 100, 6, 0, 27330, 0, 60191, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (60741, 2, 0, 'I\'d rather die knowing that at least we tried.', 12, 0, 100, 1, 0, 27331, 0, 60193, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56013, 0, 0, 'You\'ve all accomplished a great thing in saving Shen-zin Su. You do us all proud.', 12, 0, 100, 1, 0, 27784, 0, 60632, 0, 'Spirit of Master Shang Xi');
INSERT INTO `creature_text` VALUES (56013, 1, 0, 'It seems your journeys are not done. These new allies of ours come from a broken world that could use our help.', 12, 0, 100, 1, 0, 27785, 0, 60633, 0, 'Spirit of Master Shang Xi');
INSERT INTO `creature_text` VALUES (56013, 2, 0, 'What of you, Ji? Will you join the Horde in their journey back?', 12, 0, 100, 6, 0, 27786, 0, 60629, 0, 'Spirit of Master Shang Xi');
INSERT INTO `creature_text` VALUES (56013, 3, 0, 'And you, my young student? Where will you go?', 12, 0, 100, 6, 0, 27787, 0, 60631, 0, 'Spirit of Master Shang Xi');
INSERT INTO `creature_text` VALUES (57721, 0, 0, 'Master Shang Xi, I would like to accompany the Alliance back to their home.', 12, 0, 100, 1, 0, 27405, 0, 60628, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (57720, 0, 0, 'Yes... perhaps that is for the best. I like what I\'ve seen of them, and it sounds as though their world could use my help.', 12, 0, 100, 273, 0, 27305, 0, 60630, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (62202, 0, 0, '<cough> <hack> I have... returned.', 12, 0, 100, 0, 0, 30299, 0, 66031, 0, '');
INSERT INTO `creature_text` VALUES (62752, 0, 0, 'BLECCHH!!', 12, 0, 100, 0, 0, 30908, 0, 62240, 0, '');
INSERT INTO `creature_text` VALUES (62752, 1, 0, 'I am returned. I heed the call of the Klaxxi.', 12, 0, 100, 0, 0, 30909, 0, 62242, 0, '');
INSERT INTO `creature_text` VALUES (38966, 0, 0, 'I have sometin\' to show ya. It be easier to understand if ya see it yourself.', 12, 0, 100, 1, 0, 20097, 0, 38892, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 1, 0, 'Tha Darkspear are \'ere because I led dem here.  Orgrimmar be no home as long as it be under Hellscream\'s hand. ', 12, 0, 100, 1, 0, 20105, 0, 42869, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 2, 0, 'Still, I fear I was lettin\' my temper drive me ta bein\' rash. Thrall devoted himself to makin\' the Horde what it is, so I\'ve no eagerness to be leavin\' it on a whim.  Dis will be needin\' much more thought.', 12, 0, 100, 1, 0, 20106, 0, 42870, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 3, 0, 'But dese be worries for older minds.  Ya still have much to learn.  Go help tha people of tha Darkspear.  I am sure we\'ll be speakin\' again real soon.', 12, 0, 100, 1, 0, 20107, 0, 43774, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38938, 0, 0, 'Don\'t talk back to me, troll.  You know who was left in charge here.  Haven\'t you stopped to ask yourself why Thrall chose me instead of you?', 12, 0, 100, 25, 0, 20508, 0, 38897, 0, 'Vision of Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (38938, 1, 0, 'You are lucky that I don\'t gut you right here, whelp.  You are foolish to think that you can speak to your Warchief in such ways.', 12, 0, 100, 5, 0, 20512, 0, 38903, 0, 'Vision of Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (38938, 2, 0, 'And what exactly do you think that you are going to do about it?  Your threats are hollow.  Go slink away with the rest of your kind to the slums, I will endure your filth in my throne room no longer.', 12, 0, 100, 1, 0, 20509, 0, 38904, 0, 'Vision of Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (38938, 3, 0, 'You have sealed your fate, troll.', 12, 0, 100, 1, 0, 20510, 0, 38901, 0, 'Vision of Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (38938, 4, 0, '%s spits at Vol\'jin\'s feet.', 16, 0, 100, 0, 0, 0, 0, 38906, 0, 'Vision of Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (38953, 0, 0, 'Dere be no question why, Garrosh.  He gave ya tha title because ya be Grom\'s son and because tha people be wantin\' a war hero.', 12, 0, 100, 5, 0, 20098, 0, 38898, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 1, 0, 'I tink ya be even more like ya father den ya thought, even witout da demon blood.', 12, 0, 100, 1, 0, 20099, 0, 43773, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 2, 0, 'Ya be no Warchief of mine.  Ya not earned my respect and I\'ll not be seein\' tha Horde destroyed by ya foolish thirst for war.', 12, 0, 100, 1, 0, 20100, 0, 38899, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 3, 0, 'I know exactly what I\'ll be doin\' about it, son of Hellscream.  I\'ll watch and wait as ya people slowly become aware of ya ineptitude.  I\'ll laugh as dey grow ta despise ya as I do.', 12, 0, 100, 1, 0, 20101, 0, 38900, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 4, 0, 'And when tha time comes dat ya failure is complete and ya \"power\" is meaningless, I will be dere to end ya rule swiftly and silently.', 12, 0, 100, 1, 0, 20102, 0, 38905, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 5, 0, 'Ya will spend ya reign glancin\' over ya shoulda and fearin\' tha shadows, for when tha time comes and ya blood be slowly drainin\' out, ya will know exactly who fired tha arrow dat pierced ya black heart.\n', 12, 0, 100, 1, 0, 20103, 0, 38944, 0, 'Vision of Vol\'jin');
INSERT INTO `creature_text` VALUES (38953, 6, 0, 'And you yours, \"Warchief.\"', 12, 0, 100, 1, 0, 20104, 0, 38947, 0, '');
INSERT INTO `creature_text` VALUES (38989, 0, 0, 'Just let me know when ya ready, mon.', 12, 0, 100, 0, 0, 0, 0, 39243, 0, 'Jornun');
INSERT INTO `creature_text` VALUES (38931, 0, 0, 'Not so fast! Save some for me, mon!', 12, 0, 100, 5, 0, 21374, 0, 39064, 0, 'Zuni');
INSERT INTO `creature_text` VALUES (38966, 4, 0, 'Tha Sea Witch is dead, our home is reclaimed, but our future still be uncertain.', 12, 0, 100, 1, 0, 20108, 0, 38876, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 5, 0, 'I\'ve no love for Garrosh, dat much is sure, but leavin\' tha Horde is not a decision I be takin\' lightly.', 12, 0, 100, 1, 0, 20109, 0, 38878, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 6, 0, 'Dere\'s only one with tha answers I seek. Ya can stay and watch if ya like.', 12, 0, 100, 1, 0, 20110, 0, 38877, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 7, 0, 'Thrall!  I am glad ya be well.  Dere were rumors otherwise.', 12, 0, 100, 1, 0, 20111, 0, 38880, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 8, 0, 'I must beg ya council, my friend.  I can\'t be standin\' by Garrosh while he be turnin\' our people against each other for tha sake of war.  My respect for you does not extend to dis new Horde... I am tinkin\' of leadin\' my people away.', 12, 0, 100, 1, 0, 20112, 0, 38882, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 9, 0, 'I understand, brotha. I will tink on this and be troublin\' ya no furtha. You have a world to be savin\'.', 12, 0, 100, 6, 0, 20113, 0, 38885, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 10, 0, 'Ya be strong and proud, youngblood.  Tha Darkspear will be honored ta have you fight beside d\'em.', 12, 0, 100, 1, 0, 20114, 0, 38887, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 11, 0, 'Dese be dark times, but our people will be stayin\' with tha Horde for tha good of all.  ', 12, 0, 100, 1, 0, 20115, 0, 38889, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 12, 0, 'Thrall\'s words be true. Dey always be. The Horde is much more den a few old, stubborn leaders and a handful of heroes from Northrend. The people be cryin\' Garrosh\'s name... at least for now.', 12, 0, 100, 1, 0, 20116, 0, 38890, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 13, 0, 'Still, I be hopin\' Thrall will return to us one day. Tha future right now be lookin\' very grim... and very bloody.', 12, 0, 100, 1, 0, 20117, 0, 43777, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38966, 14, 0, 'Go now. Make tha Darkspear proud. Dere are many wars ahead of us, an\' I\'m sure ya be havin\' a part to play in all of dem.', 12, 0, 100, 1, 0, 20118, 0, 43778, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38939, 0, 0, 'Vol\'jin.  It\'s good to see you, brother.', 12, 0, 100, 1, 0, 20133, 0, 42871, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 1, 0, 'Indeed.  Someone did try to kill me, but that is not my greatest concern at this moment.  The world itself calls for my aid.', 12, 0, 100, 1, 0, 20134, 0, 38879, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 2, 0, 'Vol\'jin, I chose Garrosh because he has the strength to lead our people in these trying times. ', 12, 0, 100, 1, 0, 20135, 0, 38881, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 3, 0, 'For all my supposed wisdom, there have been moments that I\'ve barely been able to hold the Horde together. The Wrath Gate and Undercity displayed that clearly enough.', 12, 0, 100, 1, 0, 20136, 0, 38883, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 4, 0, 'The Horde cries for a hero of old. An orc of true blood that will bow to no human and bear no betrayal. A warrior that will make our people proud again. Garrosh can be that hero.', 12, 0, 100, 1, 0, 20137, 0, 38884, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 5, 0, 'I did not make this decision lightly, Vol\'jin. I know our alliances will suffer for it. I know the Horde will be irreversibly changed. But I made this choice with confidence that Garrosh is exactly what the Horde needs.', 12, 0, 100, 1, 0, 20138, 0, 43775, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 6, 0, 'I\'m trusting you and the other leaders to not let this divide our people. You are stronger than that.', 12, 0, 100, 1, 0, 20139, 0, 43776, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (38939, 7, 0, 'Throm\'ka, old friend.', 12, 0, 100, 66, 0, 20147, 0, 38888, 0, 'Vision of Thrall');
INSERT INTO `creature_text` VALUES (61796, 14, 0, 'Few people know this, but I was once a pit fighter myself. A gladiator for the Horde\'s amusement. So I know a thing or two about martial combat.', 12, 0, 100, 1, 0, 29568, 0, 66865, 0, '');
INSERT INTO `creature_text` VALUES (61796, 15, 0, 'Now, pandaren... let me see what you\'ve got!', 12, 0, 100, 5, 0, 29569, 0, 61454, 0, '');
INSERT INTO `creature_text` VALUES (61796, 16, 0, 'Don\'t hold back now! Let\'s have it!', 12, 0, 100, 5, 0, 29570, 0, 61455, 0, '');
INSERT INTO `creature_text` VALUES (61796, 17, 0, 'What\'s the matter? You\'ll have to do better than that.', 12, 0, 100, 6, 0, 29571, 0, 61456, 0, '');
INSERT INTO `creature_text` VALUES (61792, 3, 0, 'Careful, $p! You do not want to hurt your new king...', 12, 0, 100, 274, 0, 27393, 0, 61457, 0, '');
INSERT INTO `creature_text` VALUES (61796, 18, 0, 'Come on, pandaren! HIT ME!', 14, 0, 100, 5, 0, 29572, 0, 61458, 0, '');
INSERT INTO `creature_text` VALUES (61796, 19, 0, 'Ha ha ha haaa! I haven\'t felt like this in YEARS! Don\'t worry, $p... I\'ve been in worse scraps than this. A fighting spirit like yours is welcome in the Alliance.  Now, let\'s get back to business.', 12, 0, 100, 11, 0, 29573, 0, 61459, 0, '');
INSERT INTO `creature_text` VALUES (62087, 17, 0, 'Welcome to the Ring of Valor, pandaren. Make yourself comfortable while I finish preparing.', 14, 0, 100, 0, 0, 30735, 0, 61606, 0, '');
INSERT INTO `creature_text` VALUES (62087, 18, 0, 'Hah! It looks like we\'re ready. Now, newcomers... prepare to fight!', 14, 0, 100, 0, 0, 30736, 0, 61607, 0, '');
INSERT INTO `creature_text` VALUES (62087, 19, 0, 'This is my gift to you: a gift, and a trial.', 14, 0, 100, 0, 0, 30737, 0, 61609, 0, '');
INSERT INTO `creature_text` VALUES (62087, 20, 0, 'I\'ve heard stories about the legendary pandaren and their exotic fighting styles. Now I want to see them.', 14, 0, 100, 0, 0, 30738, 0, 61610, 0, '');
INSERT INTO `creature_text` VALUES (62087, 21, 0, 'This is a rare opportunity, pandaren. Prove your might to the Warchief of the Horde!', 14, 0, 100, 0, 0, 30739, 0, 61611, 0, '');
INSERT INTO `creature_text` VALUES (62087, 22, 0, 'Lok\'tar ogar! Victory, or death!', 14, 0, 100, 0, 0, 30740, 0, 61612, 0, '');
INSERT INTO `creature_text` VALUES (62081, 2, 0, 'Well fought. I don\'t think I could have survived that battle without you at my back.', 12, 0, 100, 0, 0, 27293, 0, 61613, 0, '');
INSERT INTO `creature_text` VALUES (44453, 0, 0, 'Gandling is strong, but do not worry.  I will join you shortly after you begin to fight him.', 12, 0, 100, 396, 0, 0, 0, 45516, 0, '');
INSERT INTO `creature_text` VALUES (44323, 0, 0, 'Prepare to die, $r!', 12, 0, 100, 0, 0, 0, 0, 45281, 0, '');
INSERT INTO `creature_text` VALUES (44323, 1, 0, 'Your life is forfeit.', 12, 0, 100, 5, 0, 0, 0, 46677, 0, '');
INSERT INTO `creature_text` VALUES (44453, 1, 0, 'For the Alliance!', 14, 0, 100, 0, 0, 0, 0, 7327, 0, '');
INSERT INTO `creature_text` VALUES (44323, 2, 0, 'Enough of this.  The Cult of the Damned doesn\'t need Andorhal.  This worthless city is yours to fight over.', 12, 0, 100, 5, 0, 0, 0, 45044, 0, '');
INSERT INTO `creature_text` VALUES (35567, 0, 0, 'You see the world through Kalytha\'s eyes...', 42, 0, 100, 0, 0, 0, 0, 35870, 0, 'Spirit of Kalytha');
INSERT INTO `creature_text` VALUES (58704, 0, 0, 'Speak to me when you are ready to try again.', 12, 0, 100, 0, 0, 0, 0, 66402, 0, 'Kelari Featherfoot');
INSERT INTO `creature_text` VALUES (65315, 0, 0, 'Ready in 3...', 42, 0, 100, 0, 0, 0, 0, 66167, 0, 'Roll Club');
INSERT INTO `creature_text` VALUES (65315, 1, 0, '2...', 42, 0, 100, 0, 0, 0, 0, 29260, 0, 'Roll Club');
INSERT INTO `creature_text` VALUES (65315, 2, 0, '1...', 42, 0, 100, 0, 0, 0, 0, 29261, 0, 'Roll Club');
INSERT INTO `creature_text` VALUES (65315, 3, 0, 'Roll!', 42, 0, 100, 0, 0, 0, 0, 66170, 0, 'Roll Club');
INSERT INTO `creature_text` VALUES (58930, 0, 0, 'I was barely inside their outer patrols when they caught me. I didn\'t find anything.', 12, 0, 100, 1, 0, 0, 0, 57799, 0, '');
INSERT INTO `creature_text` VALUES (58930, 1, 0, 'I saw their leader. He was heading further up the wall. I\'ll mark it on your map.', 12, 0, 100, 1, 0, 0, 0, 57800, 0, '');
INSERT INTO `creature_text` VALUES (58930, 2, 0, 'Thank you, friend. I didn\'t uncover anything before they caught me.', 12, 0, 100, 1, 0, 0, 0, 57801, 0, '');
INSERT INTO `creature_text` VALUES (58930, 3, 0, 'I... I failed. Thank you for coming for me.', 12, 0, 100, 1, 0, 0, 0, 57802, 0, '');
INSERT INTO `creature_text` VALUES (56841, 0, 0, 'Here\'s the report, $n. Oh! Make sure you mention my idea with the bombs. I have it lined out in the report.', 12, 0, 100, 397, 0, 0, 0, 59392, 0, 'Shademaster Kiryn');
INSERT INTO `creature_text` VALUES (56838, 0, 0, 'Report?! I have my hands full down here, $n! Oh alright, here you go.', 12, 0, 100, 396, 0, 0, 0, 59397, 0, 'Shokia');
INSERT INTO `creature_text` VALUES (56840, 0, 0, 'Nazgrim gonna be real happy with Riko! Here! Report is done!', 12, 0, 100, 396, 0, 0, 0, 59398, 0, 'Riko');
INSERT INTO `creature_text` VALUES (59305, 0, 0, 'Report\'s all ready to go, $n! I don\'t mean to brag, but General Nazgrim should be pretty pleased with my work.', 12, 0, 100, 396, 0, 0, 0, 59391, 0, 'Rivett Clutchpop');
INSERT INTO `creature_text` VALUES (66870, 0, 0, 'I will help you carry the barrels. Or did you think they were going to magically fit in your bag?', 12, 0, 100, 0, 0, 0, 0, 68021, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 1, 0, 'My village will not enter your conflict. Though, I fear your arrival has changed our peaceful existence forever.', 12, 0, 100, 0, 0, 0, 0, 68128, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 1, 1, 'Your actions will no doubt attract the attention of the Shado-Pan. I may even get to meet one!', 12, 0, 100, 0, 0, 0, 0, 68127, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 1, 2, 'One keg?! I was carrying one keg with ease when I was a young cub, barely higher than your knee!', 12, 0, 100, 0, 0, 0, 0, 68109, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 2, 0, 'Heavy, but I can hold more! Keep going!', 12, 0, 100, 0, 0, 0, 0, 68110, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 2, 1, 'A minor inconvenience.', 12, 0, 100, 0, 0, 0, 0, 20042, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 2, 2, 'Why is it you can\'t just talk out your differences? I have never encountered a problem that a good long talk couldn\'t fix!', 12, 0, 100, 0, 0, 0, 0, 68116, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 3, 0, 'Why have you brought your war to our land? What of your homes? Was there nothing worth fighting for there?', 12, 0, 100, 0, 0, 0, 0, 68126, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 3, 1, 'All this fighting is such a waste. How do your people do it all the time? When do you find time to celebrate and enjoy the simple things?', 12, 0, 100, 0, 0, 0, 0, 68114, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 3, 2, 'Horde, Alliance... what\'s the difference?! Do you not both love a good brew? Do you both not value your friends and families the same?', 12, 0, 100, 0, 0, 0, 0, 68115, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 4, 0, 'I may have overestimated my own strength...', 12, 0, 100, 0, 0, 0, 0, 68111, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 4, 1, 'That\'s it, I\'ve reached my limit! This is quite the haul!', 12, 0, 100, 0, 0, 0, 0, 68112, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 5, 0, 'This Honeybrew is the lifeblood of this village. Now THAT is something worth fighting for!', 12, 0, 100, 0, 0, 0, 0, 68129, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 5, 1, 'Honeydew Village is run by the Honeydews, but the real heart of the village is the brewery! The brewery belongs to my family, the Honeypaws. Best Honeybrew you\'ll find anywhere!', 12, 0, 100, 0, 0, 0, 0, 68119, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 6, 0, 'Thank you for your help. I\'ll make sure this Honeybrew gets back to the village. Try not to blow anything else up!', 12, 0, 100, 0, 0, 0, 0, 68133, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (66870, 6, 1, 'Thank you for your help. Try not to blow anything else up!', 12, 0, 100, 0, 0, 0, 0, 68134, 0, 'Gi-Oh');
INSERT INTO `creature_text` VALUES (47872, 0, 0, 'He killed everyone! EVERYONE!', 12, 0, 100, 5, 0, 0, 0, 48177, 0, '');
INSERT INTO `creature_text` VALUES (47872, 0, 1, 'I won\'t soon forget this kindness, $r.', 12, 0, 100, 1, 0, 0, 0, 48175, 0, '');
INSERT INTO `creature_text` VALUES (47872, 0, 2, 'It... they planted us in the dirt like crops! Stillwater is insane! Get out of here while you still can!', 12, 0, 100, 1, 0, 0, 0, 48176, 0, '');
INSERT INTO `creature_text` VALUES (47872, 0, 3, 'M... maybe you\'re not all bad after all...', 12, 0, 100, 1, 0, 0, 0, 48173, 0, '');
INSERT INTO `creature_text` VALUES (47872, 0, 4, 'Should you ever be buried neck deep in a field of feces and urine, I will be sure to lend you a hand.', 12, 0, 100, 273, 0, 0, 0, 48178, 0, '');
INSERT INTO `creature_text` VALUES (47872, 0, 5, 'Thank you for saving my life. I owe you a debt of gratitude.', 12, 0, 100, 1, 0, 0, 0, 48174, 0, '');
INSERT INTO `creature_text` VALUES (15275, 1, 0, 'The feast of souls begins now...', 14, 0, 100, 0, 0, 0, 0, 11710, 0, '');
INSERT INTO `creature_text` VALUES (15275, 0, 0, 'Oh so much pain...', 14, 0, 100, 0, 0, 0, 0, 11708, 0, '');
INSERT INTO `creature_text` VALUES (15276, 1, 0, 'Come, little ones.\n', 14, 0, 100, 0, 0, 0, 0, 11709, 0, '');
INSERT INTO `creature_text` VALUES (15276, 0, 0, 'There will be pain...', 14, 0, 100, 0, 0, 0, 0, 11707, 0, '');
INSERT INTO `creature_text` VALUES (15275, 2, 0, 'Vek\'lor, I feel your pain!', 14, 0, 100, 0, 0, 0, 0, 11454, 0, '');
INSERT INTO `creature_text` VALUES (15276, 2, 0, 'My brother, no!', 14, 0, 100, 0, 0, 0, 0, 11452, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 0, 'I am in your debt.', 12, 0, 100, 2, 0, 0, 0, 58086, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 1, 'I must rejoin the battle to save Binan Village.', 12, 0, 100, 2, 0, 0, 0, 58090, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 2, 'Shaohao must have sent you.', 12, 0, 100, 2, 0, 0, 0, 58093, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 3, 'We must drive the yaungol back to the Townlong Steppes.', 12, 0, 100, 2, 0, 0, 0, 58091, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 4, 'You have great honor, stranger. Please join us.', 12, 0, 100, 2, 0, 0, 0, 58092, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 5, 'Thank you. You have saved my life.', 12, 0, 100, 2, 0, 0, 0, 58089, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 6, 'Join us, $c. We could use you in the fight.', 12, 0, 100, 2, 0, 0, 0, 58088, 0, '');
INSERT INTO `creature_text` VALUES (59143, 0, 7, 'That is powerful healing!', 12, 0, 100, 0, 0, 0, 0, 58087, 0, '');
INSERT INTO `creature_text` VALUES (58639, 0, 0, 'I will never forget this, hero.', 12, 0, 100, 4, 0, 0, 0, 57428, 0, '');
INSERT INTO `creature_text` VALUES (58639, 0, 1, 'Thank you so much $p!', 12, 0, 100, 4, 0, 0, 0, 57426, 0, '');
INSERT INTO `creature_text` VALUES (58639, 0, 2, 'Thank you! They were saying we were going to be slaves.', 12, 0, 100, 4, 0, 0, 0, 57427, 0, '');
INSERT INTO `creature_text` VALUES (58639, 0, 3, 'They said they were looking for slaves and weapons.', 12, 0, 100, 4, 0, 0, 0, 57429, 0, '');
INSERT INTO `creature_text` VALUES (58608, 0, 0, 'I will never forget this, hero.', 12, 0, 100, 4, 0, 0, 0, 57428, 0, '');
