INSERT INTO `creature_text` VALUES (58608, 0, 2, 'Thank you! They were saying we were going to be slaves.', 12, 0, 100, 4, 0, 0, 0, 57427, 0, '');
INSERT INTO `creature_text` VALUES (58608, 0, 3, 'They said they were looking for slaves and weapons.', 12, 0, 100, 4, 0, 0, 0, 57429, 0, '');
INSERT INTO `creature_text` VALUES (54917, 0, 0, 'Join the students in the block formation below. Then follow the leader, as they do.', 12, 0, 100, 0, 0, 0, 0, 53581, 0, 'Instructor Xann');
INSERT INTO `creature_text` VALUES (41738, 0, 0, 'Hold on tight, it\'s gonna be a bumpy ride. Be on the lookout for night elves!', 12, 1, 100, 0, 0, 0, 0, 41566, 0, 'Krom\'gar Caravan Hauler');
INSERT INTO `creature_text` VALUES (41738, 1, 0, 'Got another shipment of fresh meat for you, Saurboz. Let\'s hope these ones don\'t end up back as corpses on my wagon like the last battalion.', 12, 1, 100, 0, 0, 0, 0, 41567, 0, 'Krom\'gar Caravan Hauler');
INSERT INTO `creature_text` VALUES (41739, 0, 0, 'What could an orc as depraved as you enjoy more than killing Alliance, Utvoch?', 12, 1, 100, 0, 0, 0, 0, 41569, 0, 'Sergeant Dontrag');
INSERT INTO `creature_text` VALUES (41739, 1, 0, 'You are a sick, sick orc, Ut.', 12, 1, 100, 0, 0, 0, 0, 41571, 0, 'Sergeant Dontrag');
INSERT INTO `creature_text` VALUES (41739, 2, 0, 'Yes, sir! Let\'s go Ut.', 12, 1, 100, 0, 0, 0, 0, 41577, 0, 'Sergeant Dontrag');
INSERT INTO `creature_text` VALUES (41740, 0, 0, 'Few things I like more than killing Alliance.', 12, 1, 100, 0, 0, 0, 0, 41568, 0, 'Scout Utvoch');
INSERT INTO `creature_text` VALUES (41740, 1, 0, 'You know I can\'t resist tauren women, Donty. They\'re my one weakness...', 12, 1, 100, 0, 0, 0, 0, 41570, 0, 'Scout Utvoch');
INSERT INTO `creature_text` VALUES (34341, 0, 0, 'More fodder for the Horde war machine.', 12, 1, 100, 0, 0, 0, 0, 41572, 0, 'Saurboz');
INSERT INTO `creature_text` VALUES (34341, 1, 0, 'Looks like we\'ve got some of Rockrain\'s soldiers here. Dontrag, Utvoch, we\'re gonna need your expertise over at Krom\'gar Fortress.', 12, 1, 100, 0, 0, 0, 0, 41573, 0, 'Saurboz');
INSERT INTO `creature_text` VALUES (34341, 2, 0, 'As for the rest of you grunts, suit up! We got some killin\' to do.', 12, 1, 100, 0, 0, 0, 0, 41574, 0, 'Saurboz');
INSERT INTO `creature_text` VALUES (43659, 0, 0, 'Yes, $g sir: ma\'am;!  We\'ve been biding our time in Gilneas for far too long.  We\'re ready for blood!', 12, 0, 100, 0, 0, 0, 0, 43736, 0, 'Long John Copper');
INSERT INTO `creature_text` VALUES (43660, 0, 0, 'We used to roam the seas... \'til that damned King Greymane shut down the harbors in Gilneas!  If it weren\'t for him, we wouldn\'t be...', 12, 0, 100, 0, 0, 0, 0, 43740, 0, 'Enormous Shawn Stooker');
INSERT INTO `creature_text` VALUES (43661, 0, 0, 'Yes, $g sir: ma\'am;.  We attack from the north at your signal.  The Brashtide will wash over Booty Bay!  Aroooo!!!!', 12, 0, 100, 0, 0, 0, 0, 43743, 0, 'Wailing Mary Smitts');
INSERT INTO `creature_text` VALUES (43636, 0, 0, 'Draw yer own steel, ye harpy-lovin\' scoundrel!', 14, 0, 100, 0, 0, 0, 0, 43708, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 1, 'I\'d never leave the Buccaneers, ye whale-tail skivvy-sniffer!', 14, 0, 100, 0, 0, 0, 0, 43707, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 2, 'I\'d never turn sides, ye green-gilled murloc!', 14, 0, 100, 0, 0, 0, 0, 43709, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 3, 'I\'ll poke out yer deadlights, ye lily-livered eel!', 14, 0, 100, 0, 0, 0, 0, 43714, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 4, 'I\'ll run ye through, ye cannon-kissin\' cannonface!', 14, 0, 100, 0, 0, 0, 0, 43711, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 5, 'I\'ll send ye down to Neptulon\'s Locker, ye three-gallon pufferfish!', 14, 0, 100, 0, 0, 0, 0, 43712, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 6, 'I\'ll send ye to feed the fishes, ye flat-shanty trogg!', 14, 0, 100, 0, 0, 0, 0, 43713, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (43636, 0, 7, 'Mutiny?  I\'ll show ye mutiny, ye cockle-knuckled squiffy!', 14, 0, 100, 0, 0, 0, 0, 43710, 0, 'Bloodsail Corsair');
INSERT INTO `creature_text` VALUES (48331, 0, 0, 'Deep breaths... deep breaths!', 12, 0, 100, 0, 0, 0, 0, 48683, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 1, 'I\'m okay!', 12, 0, 100, 0, 0, 0, 0, 48684, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 2, 'Pull it together!', 12, 0, 100, 0, 0, 0, 0, 48685, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 3, 'That felt kinda good!', 12, 0, 100, 0, 0, 0, 0, 48686, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 4, 'That\'s enough! Time to act like a goblin!', 12, 0, 100, 0, 0, 0, 0, 48680, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 5, 'Wow! Where am I?!', 12, 0, 100, 0, 0, 0, 0, 48679, 0, '');
INSERT INTO `creature_text` VALUES (48331, 0, 6, 'You don\'t have to tell me that twice!', 12, 0, 100, 0, 0, 0, 0, 48681, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 2, 'Thank you, hero.', 12, 0, 100, 0, 0, 0, 0, 31130, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 1, 'I must get to Stoneplow! The mantid will attack soon.', 12, 0, 100, 0, 0, 0, 0, 57490, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 0, 'The mogu ambushed us while we were trying to retreat from the mantid.', 12, 0, 100, 0, 0, 0, 0, 57491, 0, '');
INSERT INTO `creature_text` VALUES (58955, 0, 3, 'Thank you, stranger.', 12, 0, 100, 0, 0, 0, 0, 22945, 0, '');
INSERT INTO `creature_text` VALUES (55196, 0, 0, 'Your friend put up quite the fight. He nearly escaped, but now he is a prisoner, just like the rest of us.', 12, 0, 100, 0, 0, 30637, 0, 57950, 0, '');
INSERT INTO `creature_text` VALUES (58718, 0, 0, 'First packet of seeds is on the house! Come back to me anytime you want to buy more seeds, ya hear?', 12, 0, 100, 0, 0, 0, 0, 67826, 0, '');
INSERT INTO `creature_text` VALUES (55768, 0, 0, 'Ah, a visitor! This is an incredible time for Pandaria - the mists have parted, and we rejoin the world. You come in peace, I hope?', 12, 0, 100, 0, 0, 30064, 0, 62621, 0, '');
INSERT INTO `creature_text` VALUES (41255, 0, 0, 'You are little more than insects! When I am done with you, I will burn your shrine to the ground!', 12, 0, 100, 0, 0, 21682, 0, 41209, 0, 'Sethria');
INSERT INTO `creature_text` VALUES (41255, 1, 0, 'You buy time... but not your lives... my hatchery remains...', 12, 0, 100, 0, 0, 21683, 0, 41210, 0, 'Sethria');
