INSERT INTO `creature_text` VALUES (21851, 0, 0, 'From the darkest night shall rise again the raven, shall take flight in the shadows, shall reveal the nature of its kind. Prepare yourself for its coming, for the faithful shall be elevated to take flight with the raven, the rest be forgotten to walk upon the ground, clipped wings and shame.', 15, 0, 100, 0, 0, 0, 0, 19475, 0, 'The Voice of the Raven God');
