INSERT INTO `creature_text` VALUES (49634, 0, 3, 'Even death may die.', 15, 0, 100, 0, 0, 0, 0, 49619, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 4, 'There is a little lamb lost in dark woods.', 15, 0, 100, 0, 0, 0, 0, 49624, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 5, 'All places, all things have souls. All souls can be devoured.', 15, 0, 100, 0, 0, 0, 0, 49605, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 6, 'What can change the nature of a man?', 15, 0, 100, 0, 0, 0, 0, 49634, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 7, 'The stars sweep chill currents that make men shiver in the dark.', 15, 0, 100, 0, 0, 0, 0, 49616, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 8, 'You will all be alone in the end.', 15, 0, 100, 0, 0, 0, 0, 49622, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 9, 'Do you dream while you sleep or is it an escape from the horrors of reality?', 15, 0, 100, 0, 0, 0, 0, 49609, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 10, 'Look around. They will all betray you. Flee screaming into the black forest.', 15, 0, 100, 0, 0, 0, 0, 49627, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 11, 'In the land of Ny\'alotha there is only sleep.', 15, 0, 100, 0, 0, 0, 0, 49608, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 12, 'In the sleeping city of Ny\'alotha walk only mad things.', 15, 0, 100, 0, 0, 0, 0, 49611, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 13, 'Ny\'alotha is a city of old, terrible, unnumbered crimes.', 15, 0, 100, 0, 0, 0, 0, 49618, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 14, 'Y\'knath k\'th\'rygg k\'yi mrr\'ungha gr\'mula.', 15, 0, 100, 0, 0, 0, 0, 49604, 0, '');
