INSERT INTO `creature_text` VALUES (49634, 0, 19, 'In the sunken city, he lays dreaming.', 15, 0, 100, 0, 0, 0, 0, 49603, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 20, 'Open me! Open me! Open me! Then only will you know peace.', 15, 0, 100, 0, 0, 0, 0, 49629, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 21, 'You resist. You cling to your life as if it actually matters. You will learn.', 15, 0, 100, 0, 0, 0, 0, 49636, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 22, 'The tortured spirits of your ancestors cling to you, screaming in silence. Apparently they are quite numerous.', 15, 0, 100, 0, 0, 0, 0, 49633, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 23, 'The fish know all the secrets. They know the cold. They know the dark.', 15, 0, 100, 0, 0, 0, 0, 49631, 0, '');
INSERT INTO `creature_text` VALUES (49634, 0, 24, 'The giant rook watches from the dead trees. Nothing breathes beneath his shadow.', 15, 0, 100, 0, 0, 0, 0, 49625, 0, '');
