INSERT INTO `creature_text` VALUES (30656, 0, 0, 'Hail. I could not help but overhear your conversation. Please allow me to lend some assistance.', 12, 0, 100, 0, 0, 0, 0, 31380, 0, '');
INSERT INTO `creature_text` VALUES (68514, 0, 0, 'There\'s my survey team. Bring us in.', 14, 0, 100, 0, 0, 34681, 0, 69704, 0, '');
INSERT INTO `creature_text` VALUES (68514, 1, 0, 'What\'s the situation?', 12, 0, 100, 547, 0, 34682, 0, 69705, 0, '');
INSERT INTO `creature_text` VALUES (68519, 0, 0, 'Yer Highness! This site has everything we need. Lumber, stone and-', 12, 0, 100, 396, 0, 34244, 0, 69706, 0, '');
INSERT INTO `creature_text` VALUES (68514, 2, 0, 'The Horde?', 12, 0, 100, 6, 0, 34683, 0, 69707, 0, '');
INSERT INTO `creature_text` VALUES (68519, 1, 0, 'Marshal Troteman and a handful o\' scouts pursued a Horde warband up the beach. They haven\'t come back.', 12, 0, 100, 396, 0, 34245, 0, 69708, 0, '');
INSERT INTO `creature_text` VALUES (68514, 3, 0, 'Hmm... Troteman may have gotten himself in over his head. We\'ll split up.', 12, 0, 100, 396, 0, 34684, 0, 69709, 0, '');
INSERT INTO `creature_text` VALUES (68514, 4, 0, 'Lynna, stay here and establish a camp. Your first priority should be medical tents and shelters. When the peasants arrive, see that they begin construction of the docks.', 12, 0, 100, 397, 0, 34685, 0, 69710, 0, '');
INSERT INTO `creature_text` VALUES (68514, 5, 0, 'Hornswaggle, notify the rest of the survey team. Quarry and mining operations will begin as soon as the fleet arrives.', 12, 0, 100, 396, 0, 34686, 0, 69711, 0, '');
INSERT INTO `creature_text` VALUES (68519, 2, 0, 'Yes, Yer Majesty!', 12, 0, 100, 113, 0, 34246, 0, 69712, 0, '');
INSERT INTO `creature_text` VALUES (68514, 6, 0, 'Champion, you know this land better than anyone. You\'re with me. Let\'s go find Troteman.', 12, 0, 100, 396, 0, 34687, 0, 69713, 0, '');
INSERT INTO `creature_text` VALUES (68088, 0, 0, 'Here! Stop the boats here.', 14, 0, 100, 0, 0, 34241, 0, 69427, 0, '');
INSERT INTO `creature_text` VALUES (68088, 1, 0, 'This island will make a perfect coastal stronghold. Rokla! When the peons arrive, clear these trees and begin construction of the docks.', 12, 0, 100, 1, 0, 34242, 0, 69428, 0, '');
INSERT INTO `creature_text` VALUES (68088, 2, 0, 'Champion, you have been here for many weeks, and know this land well. Come with me.', 12, 0, 100, 25, 0, 34243, 0, 69429, 0, '');
INSERT INTO `creature_text` VALUES (58705, 0, 0, 'Awww shucks, I was gonna eat these! Well, if Ella needs \'em, I ain\'t gonna no more. Here, take \'em!', 12, 0, 100, 0, 0, 0, 0, 65906, 0, '');
INSERT INTO `creature_text` VALUES (57402, 0, 0, 'Little Ella\'s trying her hand at brewing? Well, I\'ll be! I wish her luck. Brewing isn\'t something a novice can just take up, you know. Anyhow, I can spare a Lakemelon for her.', 12, 0, 100, 0, 0, 0, 0, 65908, 0, '');
INSERT INTO `creature_text` VALUES (57298, 0, 0, 'A radish for sweet little Ella? Well, I sure can spare one. Here you go.', 12, 0, 100, 0, 0, 0, 0, 65907, 0, '');
INSERT INTO `creature_text` VALUES (58706, 0, 1, 'I was going to charge you a pretty penny for that peach, but Ella is such a good customer at my market, this one\'s on the house. Just this once, ya hear!', 12, 0, 100, 0, 0, 0, 0, 65909, 0, '');
INSERT INTO `creature_text` VALUES (64946, 0, 0, 'Mmmmm. Mmmmmm...... This is... simply put... amazing. Your friend Ella has real talent! Tell her to speak to my Uncle Gao at the Brewery. I think she has found a new home with us Stormstouts.', 12, 0, 100, 0, 0, 0, 0, 65916, 0, '');
INSERT INTO `creature_text` VALUES (58717, 1, 0, 'Hmmm, this is excellent! EXCELLENT! This is the best stuff I\'ve tasted in a long time. Who brewed it?', 12, 0, 100, 0, 0, 0, 0, 65912, 0, '');
INSERT INTO `creature_text` VALUES (58710, 0, 0, 'Wow! Thish ish shome amazing shtuff! Where can I get shome more?', 12, 0, 100, 0, 0, 0, 0, 65911, 0, '');
INSERT INTO `creature_text` VALUES (58646, 0, 0, 'Mmm, that\'s mighty tasty, $n. I wouldn\'t mind another round of that... or two... hic!', 12, 0, 100, 0, 0, 0, 0, 65913, 0, '');
INSERT INTO `creature_text` VALUES (64597, 0, 0, 'Mmmmm, I haven\'t tasted a brew of this complexity and clarity since... well... since the golden age of the Stormstout Brewery! Whoever made this has a real talent.', 12, 0, 100, 0, 0, 0, 0, 65914, 0, '');
INSERT INTO `creature_text` VALUES (46654, 0, 0, 'Deathwing\'s madness overcomes him, and we, the red dragons, must take this opportunity to steal his black dragons from beneath him.  We will remake the black dragons as they were intended to be: the warders of the earth.', 12, 0, 100, 5, 0, 0, 0, 46758, 0, '');
INSERT INTO `creature_text` VALUES (68065, 0, 0, '|TInterface\\Icons\\trade_archaeology_whitehydrafigurine:20|tMegaera begins to |cFFF00000|Hspell:139458|h[Rampage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 72655, 0, '');
INSERT INTO `creature_text` VALUES (68065, 1, 0, 'Megaera\'s rage subsides.', 41, 0, 100, 0, 0, 0, 0, 72748, 0, '');
INSERT INTO `creature_text` VALUES (16802, 0, 0, 'It was inevitable!', 14, 0, 100, 0, 0, 0, 0, 47063, 0, '');
INSERT INTO `creature_text` VALUES (16802, 0, 1, 'You die in vain!', 14, 0, 100, 0, 0, 0, 0, 47064, 0, '');
INSERT INTO `creature_text` VALUES (36648, 0, 0, 'The attackers shall not succeed!  Strength and honor to the Horde!', 14, 0, 100, 0, 0, 0, 0, 16232, 0, '');
INSERT INTO `creature_text` VALUES (36648, 1, 0, 'The attackers have fled!  Strength and honor to the Horde!', 14, 0, 100, 0, 0, 0, 0, 10632, 0, '');
INSERT INTO `creature_text` VALUES (69473, 0, 0, 'For millennia I have waited in darkness, in silence pierced only by the shrieks of the tormented, my dreams a waking nightmare.', 14, 0, 100, 0, 0, 35756, 0, 72237, 0, '');
INSERT INTO `creature_text` VALUES (69473, 1, 0, 'I see the naive hope in your eyes. You... think you are my saviors? Wring that mocking pity from your hearts and focus it inwards, for you shall soon know my agony.', 14, 0, 100, 0, 0, 35757, 0, 72238, 0, '');
INSERT INTO `creature_text` VALUES (69473, 2, 0, 'This twisted world is beyond redemption, beyond the reach of deluded heroes. The only answer to corruption is destruction. And that begins... now.', 14, 0, 100, 0, 0, 35758, 0, 72239, 0, '');
INSERT INTO `creature_text` VALUES (69473, 3, 0, 'When I am finished with you, I shall not rest until this world is cleansed. Now perish!', 14, 0, 100, 0, 0, 35754, 0, 72240, 0, '');
INSERT INTO `creature_text` VALUES (69473, 4, 0, 'The touch of creation... and destruction.', 14, 0, 100, 0, 0, 35763, 0, 72236, 0, '');
INSERT INTO `creature_text` VALUES (69473, 5, 0, 'You still think victory possible? Fools!', 14, 0, 100, 0, 0, 35761, 0, 72244, 0, '');
INSERT INTO `creature_text` VALUES (69473, 6, 0, 'This is but a taste of my power.', 14, 0, 100, 0, 0, 35765, 0, 72233, 0, '');
INSERT INTO `creature_text` VALUES (69473, 7, 0, 'Wait! I am... I am not your enemy. You are powerful. More powerful than he was, even.... Perhaps you are right. Perhaps there is still hope.', 14, 0, 100, 0, 0, 35766, 0, 72246, 0, '');
INSERT INTO `creature_text` VALUES (69473, 8, 0, 'But there is a yawning chasm of darkness beneath you, mortals. Vast, endless, and all-consuming. I do not believe that you can correct this doomed course.', 14, 0, 100, 0, 0, 0, 0, 72250, 0, '');
INSERT INTO `creature_text` VALUES (69473, 9, 0, 'Ah, the familiar embrace of the storms....', 14, 0, 100, 0, 0, 35764, 0, 72235, 0, '');
INSERT INTO `creature_text` VALUES (69473, 10, 0, 'Feel your own life essence betray you.', 14, 0, 100, 0, 0, 35762, 0, 72241, 0, '');
INSERT INTO `creature_text` VALUES (34956, 0, 2, 'Let me at those crazy cultists! I\'ll cleave \'em in half!', 12, 0, 100, 5, 0, 0, 0, 35791, 0, '');
INSERT INTO `creature_text` VALUES (34956, 0, 3, 'Let\'s rescue the rest of those aspirants and get back to camp. I\'m going to need a whole keg of ale after this!', 12, 0, 100, 5, 0, 0, 0, 35792, 0, '');
INSERT INTO `creature_text` VALUES (34956, 0, 4, 'Teach those cultists a lesson, $n. No one messes with Crusader Kul and gets away with it!', 12, 0, 100, 5, 0, 0, 0, 35793, 0, '');
INSERT INTO `creature_text` VALUES (68904, 0, 0, 'More kindling for the flame! Come, Lu\'lin... let us burn them to ash!', 14, 0, 100, 0, 0, 35798, 0, 72188, 0, '');
INSERT INTO `creature_text` VALUES (68905, 0, 0, 'If I must...', 14, 0, 100, 0, 0, 35702, 0, 72179, 0, '');
INSERT INTO `creature_text` VALUES (68905, 1, 0, 'Please, just go! You are making her angry.', 14, 0, 100, 0, 0, 35709, 0, 72184, 0, '');
INSERT INTO `creature_text` VALUES (68904, 1, 0, 'Witness the true meaning of pain!', 14, 0, 100, 0, 0, 35807, 0, 72195, 0, '');
INSERT INTO `creature_text` VALUES (68904, 2, 0, 'Lu\'lin! Lend me your strength!', 14, 0, 100, 0, 0, 35806, 0, 72194, 0, '');
INSERT INTO `creature_text` VALUES (68905, 2, 0, 'Just this once...', 14, 0, 100, 0, 0, 35710, 0, 72185, 0, '');
INSERT INTO `creature_text` VALUES (68904, 3, 0, 'I can see the light.', 14, 0, 100, 0, 0, 35799, 0, 72191, 0, '');
INSERT INTO `creature_text` VALUES (68905, 3, 0, 'Are they gone yet?', 14, 0, 100, 0, 0, 35705, 0, 72187, 0, '');
INSERT INTO `creature_text` VALUES (63359, 0, 0, 'Skeer the Bloodseeker has returned! Unnngghhh...', 12, 0, 100, 0, 0, 0, 0, 62832, 0, '');
INSERT INTO `creature_text` VALUES (63359, 1, 0, 'Yes, Klaxxi\'va. What does the Council command? Skeer hears. Skeer obeys.', 12, 0, 100, 0, 0, 0, 0, 62833, 0, '');
INSERT INTO `creature_text` VALUES (63359, 2, 0, 'It appears that Empress Shek\'zeer has let our empire fall into chaos. This we must address. But first, I must regain my strength.', 12, 0, 100, 0, 0, 0, 0, 66451, 0, '');
INSERT INTO `creature_text` VALUES (68397, 0, 0, 'You! You have earned my ire. I will make an example of you, such that all who look upon my might will tremble and submit.', 14, 0, 100, 0, 0, 35589, 0, 72509, 0, '');
INSERT INTO `creature_text` VALUES (68397, 1, 0, 'I am Lei Shen, slayer of kings and gods. You have made a grave mistake.', 14, 0, 100, 0, 0, 35581, 0, 72510, 0, '');
INSERT INTO `creature_text` VALUES (68397, 2, 0, 'Submit!', 14, 0, 100, 0, 0, 35601, 0, 31780, 0, '');
INSERT INTO `creature_text` VALUES (68397, 3, 0, 'You would challenge me in my own sanctum? Witness your folly!', 14, 0, 100, 0, 0, 35595, 0, 72518, 0, '');
INSERT INTO `creature_text` VALUES (68397, 4, 0, 'Bah! I will crush you myself. Let us see how you fare against Lightning.', 14, 0, 100, 0, 0, 35596, 0, 72519, 0, '');
INSERT INTO `creature_text` VALUES (68397, 5, 0, 'Lightning, seek my foes!', 14, 0, 100, 0, 0, 35602, 0, 72514, 0, '');
INSERT INTO `creature_text` VALUES (68397, 6, 0, 'You are NOTHING! I wield the power of the heavens!', 14, 0, 100, 0, 0, 35597, 0, 72520, 0, '');
INSERT INTO `creature_text` VALUES (68397, 7, 0, 'NO! You are UNWORTHY! I... AM... THE THUNDER KING!', 14, 0, 100, 0, 0, 35598, 0, 72521, 0, '');
INSERT INTO `creature_text` VALUES (68397, 8, 0, 'I sought only to finish... the work of the Gods...', 14, 0, 100, 0, 0, 35585, 0, 72522, 0, '');
