INSERT INTO `creature_text` VALUES (67801, 0, 0, 'CANNONBALL!!', 12, 0, 100, 0, 0, 0, 0, 70822, 0, '');
INSERT INTO `creature_text` VALUES (67801, 1, 0, 'For Theramore!', 12, 0, 100, 0, 0, 0, 0, 66575, 0, '');
INSERT INTO `creature_text` VALUES (67801, 2, 0, 'Monsters... all of yeh... *sputter*', 12, 0, 100, 0, 0, 0, 0, 70823, 0, '');
INSERT INTO `creature_text` VALUES (68078, 0, 0, 'Strike quickly, Manchu. Show them no mercy!', 14, 0, 100, 0, 0, 35474, 0, 72169, 0, '');
INSERT INTO `creature_text` VALUES (68078, 1, 0, 'All brawn. No brains. A recoverable loss.', 14, 0, 100, 0, 0, 35475, 0, 72170, 0, '');
INSERT INTO `creature_text` VALUES (68078, 2, 0, 'Focus, Weisheng. Show your enemy the meaning of pain!', 14, 0, 100, 0, 0, 35479, 0, 72171, 0, '');
INSERT INTO `creature_text` VALUES (68078, 3, 0, 'He fought with honor. His first and last mistake.', 14, 0, 100, 0, 0, 35476, 0, 72172, 0, '');
INSERT INTO `creature_text` VALUES (68078, 4, 0, 'Stare into the face of true power!', 14, 0, 100, 0, 0, 35465, 0, 72162, 0, '');
INSERT INTO `creature_text` VALUES (68078, 5, 0, 'How can this be?!', 14, 0, 100, 0, 0, 35462, 0, 72176, 0, '');
INSERT INTO `creature_text` VALUES (68078, 6, 0, 'Quet\'zal! Unleash the storm upon my enemies!!!', 14, 0, 100, 0, 0, 35467, 0, 72167, 0, '');
INSERT INTO `creature_text` VALUES (68078, 7, 0, 'Dam\'ren!  Freeze them to oblivion!', 14, 0, 100, 0, 0, 35466, 0, 72168, 0, '');
INSERT INTO `creature_text` VALUES (68078, 8, 0, 'ARGH! Worthless mongrels! I will kill you myself!', 14, 0, 100, 0, 0, 35477, 0, 72173, 0, '');
INSERT INTO `creature_text` VALUES (68078, 9, 0, 'More... training...', 14, 0, 100, 0, 0, 35469, 0, 72166, 0, '');
INSERT INTO `creature_text` VALUES (69017, 0, 0, 'So cold, so dark...', 12, 0, 100, 0, 0, 35744, 0, 71586, 0, '');
INSERT INTO `creature_text` VALUES (69017, 1, 0, 'We will not be forgotten, no, no, no...', 12, 0, 100, 0, 0, 35745, 0, 71587, 0, '');
INSERT INTO `creature_text` VALUES (69017, 2, 0, 'We are the perfect one, yes, yes, not a failure, no no, not a failure...', 12, 0, 100, 0, 0, 35746, 0, 71588, 0, '');
INSERT INTO `creature_text` VALUES (69017, 3, 0, 'Your flesh, yes yes, so perfect, GIVE IT TO US!', 14, 0, 100, 0, 0, 35742, 0, 71590, 0, '');
INSERT INTO `creature_text` VALUES (69017, 4, 0, 'No no no, the pain returns!', 14, 0, 100, 0, 0, 35749, 0, 71591, 0, '');
INSERT INTO `creature_text` VALUES (69017, 5, 0, 'Again...we are torn apart...again...to the cold darkness...', 14, 0, 100, 0, 0, 35743, 0, 71594, 0, '');
