INSERT INTO `creature_text` VALUES (69017, 10, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136245|h[Metabolic Boost]|h|r!', 41, 0, 100, 0, 0, 0, 0, 71607, 0, '');
INSERT INTO `creature_text` VALUES (69017, 11, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136246|h[Erupting Pustules]|h|r!', 41, 0, 100, 0, 0, 0, 0, 71608, 0, '');
INSERT INTO `creature_text` VALUES (69702, 0, 0, 'Stop them! The Animus must not be disturbed!', 14, 0, 100, 0, 0, 35725, 0, 72611, 0, '');
INSERT INTO `creature_text` VALUES (69702, 1, 0, 'The power... It cannot be wielded this way! You tempt forces you do not understand!', 14, 0, 100, 0, 0, 35722, 0, 72612, 0, '');
INSERT INTO `creature_text` VALUES (69702, 2, 0, 'You have doomed yourselves. It... is... unleashed.', 14, 0, 100, 0, 0, 35724, 0, 72613, 0, '');
INSERT INTO `creature_text` VALUES (69756, 0, 0, 'The orb explodes!', 41, 0, 100, 0, 0, 0, 0, 72051, 0, '');
INSERT INTO `creature_text` VALUES (69427, 0, 0, 'Stop casting! %s is generating an |cFFFF0000|Hspell:138763|h[Interrupting Jolt]|h|r.', 41, 0, 100, 0, 0, 0, 0, 72389, 0, '');
INSERT INTO `creature_text` VALUES (69427, 1, 0, '%s drains power from the other golems with |cFFFF0000|Hspell:138644|h[Siphon Anima]|h|r!', 41, 0, 100, 0, 0, 0, 0, 72266, 0, '');
INSERT INTO `creature_text` VALUES (1355, 0, 0, 'By th\' Light, it worked! Grimnur\'s brilliant... oh, I hope all the pinchin\' didn\'t hurt ye too much... But when ye taste the meat, ye\'ll know it\'s worth it!', 12, 0, 100, 5, 0, 0, 0, 52135, 0, '');
INSERT INTO `creature_text` VALUES (44768, 0, 1, 'The forgemaster\'s a terrible murderer.  Are you sure you want to go looking for him?', 12, 0, 100, 0, 0, 0, 0, 44849, 0, '');
INSERT INTO `creature_text` VALUES (44768, 0, 2, 'Ah, yes... I know about the forgemaster!  I just overheard that he\'s not in the camp right now.', 12, 0, 100, 0, 0, 0, 0, 44850, 0, '');
INSERT INTO `creature_text` VALUES (44768, 0, 3, 'The forgemaster is meeting with the Twilight leadership.  Trust me, I traded some gossip for a loaf of stale bread!', 12, 0, 100, 0, 0, 0, 0, 44851, 0, '');
INSERT INTO `creature_text` VALUES (44768, 0, 4, 'I don\'t know nothin\' bout no forgemaster.  Now tell me where this safe place you came from is!', 12, 0, 100, 0, 0, 0, 0, 44852, 0, '');
INSERT INTO `creature_text` VALUES (44768, 0, 5, 'I heard the forgemaster keeps notes about his meetings  in a book at the tower overlooking the camp.', 12, 0, 100, 0, 0, 0, 0, 44853, 0, '');
