INSERT INTO `creature_text` VALUES (55402, 0, 0, 'Attention, fairegoers! The Darkmoon Deathmatch is about to begin! In mere moments the prize chest will appear at the center of the fighting pit!', 14, 0, 100, 22, 0, 0, 0, 53813, 0, 'Korgol Crushskull');
INSERT INTO `creature_text` VALUES (55402, 1, 0, 'Let the Darkmoon Deathmatch BEGIN!', 14, 0, 100, 22, 0, 0, 0, 53814, 0, 'Korgol Crushskull');
