INSERT INTO `creature_text` VALUES (14822, 0, 0, 'Come to me and see what the future holds for you!', 12, 0, 100, 0, 0, 0, 0, 10125, 0, 'Sayge');
INSERT INTO `creature_text` VALUES (14822, 0, 1, 'Come speak with me, and what once was cloudy shall become crystal clear.', 12, 0, 100, 0, 0, 0, 0, 10126, 0, 'Sayge');
INSERT INTO `creature_text` VALUES (14822, 0, 2, 'Fear not!  All fortunes can be discerned for those who know the way.', 12, 0, 100, 0, 0, 0, 0, 10127, 0, 'Sayge');
INSERT INTO `creature_text` VALUES (14822, 0, 3, 'The longer you wait, the less future you have for me to foretell.', 12, 0, 100, 0, 0, 0, 0, 10128, 0, 'Sayge');
