INSERT INTO `creature_text` VALUES (14846, 0, 0, 'Come and take a look at my wares while you still can.  We\'re here all week, and then we\'re off to some far off land.', 12, 0, 100, 0, 0, 0, 0, 10143, 0, 'Lhara');
