INSERT INTO `creature_text` VALUES (68589, 0, 0, 'Jaina\'s gone over the edge. She\'s imprisoning the Sunreavers and attacking those who resist. What\'s worse, the Silver Covenant has seized the opportunity to join the bloodshed.', 12, 0, 100, 0, 0, 34269, 0, 69656, 0, '');
INSERT INTO `creature_text` VALUES (68695, 0, 0, 'I guess I can head back to Silvermoon...', 12, 0, 100, 547, 0, 0, 0, 70469, 0, '');
INSERT INTO `creature_text` VALUES (68695, 0, 1, 'This is an outrage! Aethas will hear about this!', 12, 0, 100, 412, 0, 0, 0, 70481, 0, '');
INSERT INTO `creature_text` VALUES (68695, 0, 2, 'Thank you for the warning. Be safe.', 12, 0, 100, 2, 0, 0, 0, 70502, 0, '');
INSERT INTO `creature_text` VALUES (68695, 0, 3, 'Can the Kirin Tor really kick us out like this?', 12, 0, 100, 6, 0, 0, 0, 70552, 0, '');
INSERT INTO `creature_text` VALUES (68695, 0, 4, 'I think it may be time to bring my swords out of retirement...', 12, 0, 100, 5, 0, 0, 0, 70553, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 0, 'You should not have interfered!', 12, 0, 100, 0, 0, 0, 0, 70520, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 1, 'What do you think you\'re doing?', 12, 0, 100, 0, 0, 0, 0, 70546, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 2, 'Interference will not be tolerated!', 12, 0, 100, 0, 0, 0, 0, 70551, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 3, 'Silence!', 12, 0, 100, 0, 0, 0, 0, 70548, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 4, 'Halt!', 12, 0, 100, 0, 0, 0, 0, 70547, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 5, 'Just who do you think YOU are?', 12, 0, 100, 0, 0, 0, 0, 70549, 0, '');
INSERT INTO `creature_text` VALUES (68692, 0, 6, 'The Sunreavers are scum!', 12, 0, 100, 0, 0, 0, 0, 70550, 0, '');
INSERT INTO `creature_text` VALUES (68590, 0, 0, 'It\'s chaos up there. We need to save whoever we can, and we\'ll need to move quickly.', 12, 0, 100, 0, 0, 34270, 0, 69657, 0, '');
INSERT INTO `creature_text` VALUES (68593, 0, 0, 'Wait for my command...', 12, 0, 100, 0, 0, 34271, 0, 69658, 0, '');
INSERT INTO `creature_text` VALUES (68593, 1, 0, 'Go go go go! To the center of the city! This way!', 14, 0, 100, 0, 0, 34272, 0, 69659, 0, '');
INSERT INTO `creature_text` VALUES (68711, 0, 0, 'Come on, girls. You\'ve had your share of meat for the day.', 12, 0, 100, 0, 0, 0, 0, 70559, 0, '');
INSERT INTO `creature_text` VALUES (68714, 0, 0, 'So long, Dalaran...', 12, 0, 100, 0, 0, 0, 0, 70560, 0, '');
INSERT INTO `creature_text` VALUES (68715, 0, 0, 'Thank you, $n. See you in Silvermoon...', 12, 0, 100, 0, 0, 0, 0, 70561, 0, '');
INSERT INTO `creature_text` VALUES (68716, 0, 0, 'I hope the Horde has a place for another mage...', 12, 0, 100, 0, 0, 0, 0, 70562, 0, '');
INSERT INTO `creature_text` VALUES (68717, 0, 0, 'Jaina is taking away my home by force? I\'ll remember this...', 12, 0, 100, 0, 0, 0, 0, 70563, 0, '');
INSERT INTO `creature_text` VALUES (68631, 0, 0, 'Aethas isn\'t here... they must have moved him. We can continue without him.', 12, 0, 100, 0, 0, 34273, 0, 69660, 0, '');
INSERT INTO `creature_text` VALUES (68586, 0, 0, 'Aethas, this way! Into the portal!', 12, 0, 100, 0, 0, 34275, 0, 69662, 0, '');
INSERT INTO `creature_text` VALUES (68586, 1, 0, 'Enter the portal!', 41, 0, 100, 0, 0, 0, 0, 70408, 0, '');
INSERT INTO `creature_text` VALUES (68634, 0, 0, 'Stay by me! We\'re going out the tunnel!', 12, 0, 100, 0, 0, 34276, 0, 69663, 0, '');
INSERT INTO `creature_text` VALUES (68634, 1, 0, 'Stay by my side!', 12, 0, 100, 0, 0, 34277, 0, 69664, 0, '');
INSERT INTO `creature_text` VALUES (40737, 0, 0, 'There are too many! Shaman, do something!', 14, 0, 100, 396, 0, 0, 0, 40661, 0, '');
INSERT INTO `creature_text` VALUES (40737, 1, 0, 'Rest while you can, men. That wasn\'t the last of them....', 14, 0, 100, 396, 0, 0, 0, 40662, 0, '');
INSERT INTO `creature_text` VALUES (40737, 2, 0, 'Brace yourselves, men!', 14, 0, 100, 396, 0, 0, 0, 40676, 0, '');
INSERT INTO `creature_text` VALUES (40737, 3, 0, 'Make them pay!', 14, 0, 100, 396, 0, 0, 0, 40704, 0, '');
INSERT INTO `creature_text` VALUES (40737, 4, 0, 'Stay alert! Don\'t let them drag you away!', 14, 0, 100, 396, 0, 0, 0, 40677, 0, '');
INSERT INTO `creature_text` VALUES (40737, 5, 0, 'Don\'t give up! Fight! Fight for the Alliance!', 14, 0, 100, 396, 0, 0, 0, 40705, 0, '');
INSERT INTO `creature_text` VALUES (40737, 6, 0, 'Their numbers are... endless...', 14, 0, 100, 396, 0, 0, 0, 40706, 0, '');
INSERT INTO `creature_text` VALUES (40737, 7, 0, 'My men - they\'re all taken... Go! Save youselves!', 14, 0, 100, 396, 0, 0, 0, 40695, 0, '');
INSERT INTO `creature_text` VALUES (50347, 0, 0, 'The darkness comes.', 12, 0, 100, 0, 0, 0, 0, 66107, 0, '');
INSERT INTO `creature_text` VALUES (69565, 0, 0, 'It is chaos out there, $p! The siege has begun but our heavy explosives never made it to the wall.', 12, 0, 100, 5, 0, 36375, 0, 71638, 0, '');
INSERT INTO `creature_text` VALUES (69565, 1, 0, 'I will cover the flanks. Gather up our explosives and get them to the wall. Hurry!', 12, 0, 100, 1, 0, 36376, 0, 71639, 0, '');
INSERT INTO `creature_text` VALUES (70055, 0, 0, 'Cover one another! Stand your ground!', 14, 0, 100, 0, 0, 35625, 0, 72301, 0, '');
INSERT INTO `creature_text` VALUES (69755, 0, 0, 'Crush them! Bleed them!', 14, 0, 100, 0, 0, 35995, 0, 72309, 0, '');
INSERT INTO `creature_text` VALUES (69565, 2, 0, 'The charges are placed. Hero - you may have the honor.', 12, 0, 100, 1, 0, 36377, 0, 71737, 0, '');
INSERT INTO `creature_text` VALUES (69755, 1, 0, 'The spirits give us strength!', 14, 0, 100, 0, 0, 35993, 0, 72307, 0, '');
INSERT INTO `creature_text` VALUES (70055, 1, 0, 'Spectacular! Move up - press onward. Show them no quarter!', 14, 0, 100, 0, 0, 35632, 0, 72677, 0, '');
INSERT INTO `creature_text` VALUES (69565, 3, 0, 'This way - the Zandalari are falling back!', 14, 0, 100, 5, 0, 36379, 0, 72678, 0, '');
INSERT INTO `creature_text` VALUES (69755, 2, 0, 'Step forward! Witness the gift of the Thunder King!', 14, 0, 100, 1, 0, 35997, 0, 72696, 0, '');
INSERT INTO `creature_text` VALUES (69565, 4, 0, 'What... what did they DO to that troll!?', 12, 0, 100, 5, 0, 36381, 0, 72680, 0, '');
INSERT INTO `creature_text` VALUES (69755, 3, 0, 'I was chosen. My blood burns with his power!', 14, 0, 100, 1, 0, 35998, 0, 72697, 0, '');
INSERT INTO `creature_text` VALUES (69755, 4, 0, 'You will be punished! Your life a lesson in futility!', 14, 0, 100, 1, 0, 36000, 0, 72699, 0, '');
INSERT INTO `creature_text` VALUES (69755, 5, 0, 'For Zandalar!!', 14, 0, 100, 1, 0, 36001, 0, 72700, 0, '');
INSERT INTO `creature_text` VALUES (70055, 2, 0, 'Rangers - to me! We have broken their lines!', 12, 0, 100, 0, 0, 35633, 0, 72682, 0, '');
INSERT INTO `creature_text` VALUES (69565, 5, 0, 'My Lord Regent - you should not be so close to the front lines.', 12, 0, 100, 5, 0, 36383, 0, 72681, 0, '');
INSERT INTO `creature_text` VALUES (70055, 3, 0, 'Nonsense. This is where I belong.', 12, 0, 100, 0, 0, 35634, 0, 72683, 0, '');
INSERT INTO `creature_text` VALUES (70055, 4, 0, 'Champion! Again you are at the forefront of a Horde triumph. And once more I am in your debt.', 12, 0, 100, 0, 0, 35635, 0, 72684, 0, '');
INSERT INTO `creature_text` VALUES (70055, 5, 0, 'Regroup behind the wards, everyone. They will counter-attack... but we will be ready.', 12, 0, 100, 0, 0, 35636, 0, 72685, 0, '');
INSERT INTO `creature_text` VALUES (69949, 0, 0, 'The siege is underway but our heavy explosives never made it to the wall!', 12, 0, 100, 5, 0, 35920, 0, 72252, 0, '');
INSERT INTO `creature_text` VALUES (69949, 1, 0, 'Gather up the explosives and get them to the wall while I rally the reinforcements.', 12, 0, 100, 1, 0, 35921, 0, 72253, 0, '');
INSERT INTO `creature_text` VALUES (70092, 0, 0, 'Don\'t fall back. Don\'t give them an inch!', 14, 0, 100, 0, 0, 35484, 0, 72396, 0, '');
INSERT INTO `creature_text` VALUES (69949, 2, 0, 'The wall is wired up and ready to go - the honor is all yours!', 12, 0, 100, 1, 0, 35922, 0, 72716, 0, '');
INSERT INTO `creature_text` VALUES (70092, 1, 0, 'Well done! We\'re through - pull together, everyone. Press the advantage!', 14, 0, 100, 0, 0, 35492, 0, 72692, 0, '');
INSERT INTO `creature_text` VALUES (69949, 3, 0, 'This way - the Zandalari are falling back!', 14, 0, 100, 5, 0, 35923, 0, 72678, 0, '');
INSERT INTO `creature_text` VALUES (69949, 4, 0, 'What... what did they DO to that troll!?', 12, 0, 100, 5, 0, 36381, 0, 72680, 0, '');
INSERT INTO `creature_text` VALUES (70092, 2, 0, 'It\'s dead! You\'re an inspiration to the Kirin Tor!', 12, 0, 100, 0, 0, 35493, 0, 72729, 0, '');
INSERT INTO `creature_text` VALUES (70092, 3, 0, 'Vereesa, are you hurt?', 12, 0, 100, 0, 0, 35494, 0, 72693, 0, '');
INSERT INTO `creature_text` VALUES (69949, 5, 0, 'It is nothing. I am fine.', 12, 0, 100, 5, 0, 35928, 0, 72691, 0, '');
INSERT INTO `creature_text` VALUES (70092, 4, 0, 'You need to get back to camp. I can\'t lose you.', 12, 0, 100, 0, 0, 35495, 0, 72694, 0, '');
INSERT INTO `creature_text` VALUES (70092, 5, 0, 'Gather up the wounded! We\'ve struck a mighty blow today - the next part of our campaign begins.', 12, 0, 100, 0, 0, 35496, 0, 72695, 0, '');
INSERT INTO `creature_text` VALUES (42022, 0, 0, 'Why must we ride to battle on a mercenary ship? Used to be that we\'d kill pirates as soon as look at them.', 12, 0, 100, 6, 0, 21325, 0, 41841, 0, '');
INSERT INTO `creature_text` VALUES (42021, 0, 0, 'See those ships in dock?', 12, 0, 100, 25, 0, 21333, 0, 41849, 0, '');
INSERT INTO `creature_text` VALUES (42021, 1, 0, 'They\'re the only ones that have returned from battle, and they\'re being repaired.', 12, 0, 100, 396, 0, 21334, 0, 41850, 0, '');
INSERT INTO `creature_text` VALUES (42021, 2, 0, 'We pay the mercs to get us out there, or our troops get no reinforcements.', 12, 0, 100, 396, 0, 21336, 0, 41852, 0, '');
INSERT INTO `creature_text` VALUES (42022, 1, 0, 'That makes sense, I suppose. Just doesn\'t feel right...', 12, 0, 100, 396, 0, 21326, 0, 41853, 0, '');
INSERT INTO `creature_text` VALUES (42021, 3, 0, 'Welcome to the military, recruit.', 12, 0, 100, 396, 0, 21337, 0, 41856, 0, '');
INSERT INTO `creature_text` VALUES (42059, 0, 0, 'Earthquakes every other day, a crazy dragon tearing up the world, what\'s next?', 12, 0, 100, 6, 0, 21327, 0, 41924, 0, '');
INSERT INTO `creature_text` VALUES (42021, 4, 0, 'Put all of that out of your head, recruit.\n', 12, 0, 100, 396, 0, 21343, 0, 41925, 0, '');
INSERT INTO `creature_text` VALUES (42021, 5, 0, 'I\'ve been fightin\' Horde for ten years now, and I\'ve never seen it worse than this.', 12, 0, 100, 396, 0, 21342, 0, 41926, 0, '');
INSERT INTO `creature_text` VALUES (42021, 6, 0, 'Just stay focused on the battle in front of us, or you won\'t live to worry about the rest.', 12, 0, 100, 396, 0, 21344, 0, 41927, 0, '');
INSERT INTO `creature_text` VALUES (42059, 1, 0, 'Yessir.', 12, 0, 100, 24, 0, 21328, 0, 18906, 0, '');
INSERT INTO `creature_text` VALUES (42021, 7, 0, 'I stopped by the park district on my way here. There\'s nothing left of it...\n', 12, 0, 100, 396, 0, 21351, 0, 41900, 0, '');
INSERT INTO `creature_text` VALUES (42021, 8, 0, 'Aye. I was here when the dragon attacked.', 12, 0, 100, 396, 0, 21353, 0, 41901, 0, '');
INSERT INTO `creature_text` VALUES (42021, 9, 0, 'He could\'ve destroyed the entire city if he willed it. We were powerless to stop him.', 12, 0, 100, 1, 0, 22801, 0, 41902, 0, '');
INSERT INTO `creature_text` VALUES (42021, 10, 0, 'What drove him off, then?', 12, 0, 100, 6, 0, 21352, 0, 41903, 0, '');
INSERT INTO `creature_text` VALUES (42021, 11, 0, 'No idea. It made no sense...', 12, 0, 100, 1, 0, 21355, 0, 41904, 0, '');
INSERT INTO `creature_text` VALUES (42021, 12, 0, 'He flew in, torched the park, then just flew off.', 12, 0, 100, 1, 0, 21356, 0, 41906, 0, '');
INSERT INTO `creature_text` VALUES (42021, 13, 0, 'If you ask me, the beast is insane.', 12, 0, 100, 396, 0, 21357, 0, 41905, 0, '');
INSERT INTO `creature_text` VALUES (42021, 14, 0, 'I think I can just make it out from here. Right on the edge of the horizon.', 12, 0, 100, 25, 0, 21345, 0, 41950, 0, '');
INSERT INTO `creature_text` VALUES (42059, 2, 0, 'Make what out, exactly?', 12, 0, 100, 6, 0, 21329, 0, 41965, 0, '');
INSERT INTO `creature_text` VALUES (42021, 15, 0, 'The new island - just surfaced after the last earthquakes.', 12, 0, 100, 1, 0, 21346, 0, 41951, 0, '');
INSERT INTO `creature_text` VALUES (42021, 16, 0, 'It\'s what we\'re fighting for. On this front, at least.', 12, 0, 100, 1, 0, 21347, 0, 41952, 0, '');
INSERT INTO `creature_text` VALUES (42059, 3, 0, 'Have you been there?', 12, 0, 100, 6, 0, 21330, 0, 41966, 0, '');
INSERT INTO `creature_text` VALUES (42021, 17, 0, 'This\'ll be my third time now.', 12, 0, 100, 273, 0, 21348, 0, 41953, 0, '');
INSERT INTO `creature_text` VALUES (42021, 18, 0, 'Toughest fighting I\'ve seen...', 12, 0, 100, 1, 0, 21349, 0, 41954, 0, '');
INSERT INTO `creature_text` VALUES (42021, 19, 0, 'We lose that island to the Horde and Stormwind\'s as good as gone... and they know it.', 12, 0, 100, 1, 0, 21350, 0, 41955, 0, '');
INSERT INTO `creature_text` VALUES (42103, 0, 0, 'That\'s enough, soldier.', 12, 0, 100, 1, 0, 21207, 0, 41961, 0, '');
INSERT INTO `creature_text` VALUES (42103, 1, 0, 'Now get your minds right! We\'ll be killing orcs within the hour.', 12, 0, 100, 1, 0, 21208, 0, 41964, 0, '');
INSERT INTO `creature_text` VALUES (42059, 4, 0, 'Yeah, I... I think I can see it too.', 12, 0, 100, 6, 0, 21331, 0, 41967, 0, '');
INSERT INTO `creature_text` VALUES (42103, 2, 0, 'Listen up! Our transport should be here momentarily.', 14, 0, 100, 22, 0, 21204, 0, 41983, 0, '');
INSERT INTO `creature_text` VALUES (42103, 3, 0, 'Fall in!', 14, 0, 100, 22, 0, 21205, 0, 41984, 0, '');
INSERT INTO `creature_text` VALUES (42103, 4, 0, 'Alright, men! Move out!', 14, 0, 100, 4, 0, 21206, 0, 41987, 0, '');
INSERT INTO `creature_text` VALUES (68526, 1, 0, 'They say the eyes are the window to the soul. If that\'s the case, I\'m guessing this guest and their soul have something to hide.', 12, 0, 100, 0, 0, 0, 0, 70223, 0, '');
INSERT INTO `creature_text` VALUES (68526, 2, 0, 'This guest wears a memento of a love lost. I guess it\'s true what they say; every love story has its thorns, just like every night has its dawn.', 12, 0, 100, 0, 0, 0, 0, 70224, 0, '');
INSERT INTO `creature_text` VALUES (68526, 3, 0, 'It\'s best not to get on the bad side of those that prepare your food. However, I\'m not sure this patron has anything but a bad side...', 12, 0, 100, 0, 0, 0, 0, 70222, 0, '');
INSERT INTO `creature_text` VALUES (68537, 0, 0, 'Now I can really get this party started!', 12, 0, 100, 0, 0, 0, 0, 70242, 0, '');
INSERT INTO `creature_text` VALUES (68540, 0, 0, 'Took you long enough!', 12, 0, 100, 0, 0, 0, 0, 70251, 0, '');
INSERT INTO `creature_text` VALUES (68539, 0, 0, 'Who else would this belong to? What\'s that funny look for? You look like I was about to hit you!', 12, 0, 100, 0, 0, 0, 0, 70239, 0, '');
INSERT INTO `creature_text` VALUES (68531, 0, 0, 'So ye want me to go join the Alliance front in this... Pandaria... eh?', 12, 0, 100, 0, 0, 0, 0, 70256, 0, '');
INSERT INTO `creature_text` VALUES (68531, 1, 0, 'Alright, I\'ll go. But this is the last time I\'m going to infiltrate the Horde! And I mean it.', 12, 0, 100, 0, 0, 0, 0, 70865, 0, '');
INSERT INTO `creature_text` VALUES (68531, 2, 0, 'I\'m off to gather supplies and reconnect with old contacts. If I need backup in Pandaria, I\'ll send for ya!', 12, 0, 100, 0, 0, 0, 0, 70866, 0, '');
INSERT INTO `creature_text` VALUES (68417, 0, 0, 'Pssssst! Hey, over here, it\'s me, Agent Connelly! Don\'t blow our disguise!', 12, 0, 100, 0, 0, 0, 0, 70832, 0, '');
INSERT INTO `creature_text` VALUES (68417, 1, 0, 'So Garrosh is looking for something called the Divine Bell? Whatever that is, it can\'t be good. I\'ll get this information back to headquarters.', 12, 0, 100, 0, 0, 0, 0, 70830, 0, '');
INSERT INTO `creature_text` VALUES (68417, 2, 0, 'Excellent work! I\'ll be in touch soon.', 12, 0, 100, 0, 0, 0, 0, 70831, 0, '');
INSERT INTO `creature_text` VALUES (14444, 1, 0, 'Wow, so you\'re the Banshee Queen? I kind of expected you to be taller!', 12, 0, 100, 1, 0, 0, 0, 51372, 0, 'Children\'s Week - Quest: The Banshee Queen');
INSERT INTO `creature_text` VALUES (14444, 2, 0, 'Everyone says you had a fight with the warchief and you\'re going to abandon the Horde. Is that true?', 12, 0, 100, 1, 0, 0, 0, 51373, 0, 'Children\'s Week - Quest: The Banshee Queen');
INSERT INTO `creature_text` VALUES (14444, 3, 0, 'So it\'s not true? Thank the ancestors! I wouldn\'t want you to be my enemy, Lady Sylvanas.', 12, 0, 100, 1, 0, 0, 0, 51377, 0, 'Children\'s Week - Quest: The Banshee Queen');
INSERT INTO `creature_text` VALUES (14444, 4, 0, 'So this is how a chieftain is honored!', 12, 0, 100, 1, 0, 0, 0, 51419, 0, 'Children\'s Week - Quest: The Fallen Chieftain');
INSERT INTO `creature_text` VALUES (16287, 1, 0, 'You forget yourself, child! Do you know who stands before you?', 12, 0, 100, 5, 0, 0, 0, 51378, 0, '');
INSERT INTO `creature_text` VALUES (10181, 3, 0, 'The Horde embraced my people when we stood alone and we Forsaken never forget a debt...', 12, 0, 100, 1, 0, 0, 0, 51375, 0, '');
INSERT INTO `creature_text` VALUES (10181, 4, 0, 'You needn\'t worry, child. The warchief\'s \"observers\" are here to make certain of that.', 12, 0, 100, 0, 0, 0, 0, 51453, 0, '');
INSERT INTO `creature_text` VALUES (14305, 1, 0, 'This has to be the biggest diamond ever!', 12, 0, 100, 1, 0, 0, 0, 50999, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (14305, 2, 0, 'It looks just like the paintings of King Magni too! Did he really do this to himself?', 12, 0, 100, 1, 0, 0, 0, 51000, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (14305, 3, 0, 'Wow, I don\'t think that was such a good idea...', 12, 0, 100, 1, 0, 0, 0, 51002, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (14305, 4, 0, 'I\'m really sorry you lost your king. But in a way, it\'s like he\'s with you forever!', 12, 0, 100, 1, 0, 0, 0, 51003, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (14305, 5, 0, 'Wow, you sure don\'t look like any night elf I\'ve seen before!', 12, 0, 100, 1, 0, 0, 0, 51011, 0, 'Children\'s Week - Quest: Malfurion has Returned');
INSERT INTO `creature_text` VALUES (14305, 6, 0, 'Our orphan matron says druids can turn into birds, cats, and bears, but you look like a combination of all of them!', 12, 0, 100, 1, 0, 0, 0, 51012, 0, 'Children\'s Week - Quest: Malfurion has Returned');
INSERT INTO `creature_text` VALUES (14305, 7, 0, 'I see what you\'re doing. You\'re gonna take all the druid forms and make them into one super-form!', 12, 0, 100, 1, 0, 0, 0, 51013, 0, 'Children\'s Week - Quest: Malfurion has Returned');
INSERT INTO `creature_text` VALUES (14305, 8, 0, 'The Emerald Dream doesn\'t sound like a place for me. What if it turns me into a turtle? I\'ve always wanted wings, though...', 12, 0, 100, 1, 0, 0, 0, 51014, 0, 'Children\'s Week - Quest: Malfurion has Returned');
INSERT INTO `creature_text` VALUES (14305, 9, 0, 'Wheeee! I\'m a bird-boy!', 12, 0, 100, 1, 0, 0, 0, 51015, 0, 'Children\'s Week - Quest: Malfurion has Returned');
INSERT INTO `creature_text` VALUES (52321, 1, 0, 'Aye, lad. He tried to use an old titan ritual to keep his people safe, but this is what it did to him.', 12, 0, 100, 1, 0, 0, 0, 51001, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (52321, 2, 0, 'Thank you, lad.', 12, 0, 100, 1, 0, 0, 0, 51006, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (2918, 1, 0, 'This was my fault, my lord. I should\'ve done more research on those tablets...', 12, 0, 100, 6, 0, 0, 0, 51005, 0, 'Children\'s Week - Quest: The Biggest Diamond Ever');
INSERT INTO `creature_text` VALUES (43845, 1, 0, 'No, little one, it\'s nothing like that. My spirit spent many years in the Emerald Dream while my body slept, and it started to take on attributes of our animal forms.', 12, 0, 100, 1, 0, 0, 0, 51016, 0, '');
INSERT INTO `creature_text` VALUES (69481, 0, 0, 'Elsia is waiting for you, champion.', 12, 0, 100, 0, 0, 35664, 0, 72410, 0, '');
INSERT INTO `creature_text` VALUES (69745, 0, 0, 'This creature you\'ve recovered is immune to the lightning strikes that protect the island.', 12, 0, 100, 0, 0, 36388, 0, 72425, 0, '');
INSERT INTO `creature_text` VALUES (69745, 1, 0, 'Do you think you can ride it?', 12, 0, 100, 0, 0, 36389, 0, 72426, 0, '');
INSERT INTO `creature_text` VALUES (69745, 2, 0, 'Very good!  Once you take to the skies, the Zandalari will try to stop you.', 12, 0, 100, 0, 0, 36390, 0, 72427, 0, '');
INSERT INTO `creature_text` VALUES (69745, 3, 0, 'Be ready to dogfight in the air. Jump from mount to mount if you have to!', 12, 0, 100, 0, 0, 36391, 0, 72428, 0, '');
INSERT INTO `creature_text` VALUES (69745, 4, 0, 'A Shado-Pan contact will be waiting to meet you behind the mines. Good luck!!', 14, 0, 100, 0, 0, 36392, 0, 72429, 0, '');
INSERT INTO `creature_text` VALUES (69559, 0, 0, 'FOOLS! I will show you how it is done!', 14, 0, 100, 25, 0, 0, 0, 72593, 0, '');
INSERT INTO `creature_text` VALUES (69559, 1, 0, 'Did ya think ya could just fly ova\' hea\' unnoticed?!', 14, 0, 100, 25, 0, 0, 0, 71738, 0, '');
INSERT INTO `creature_text` VALUES (69559, 2, 0, 'Well, ya got where you were goin\'. Too bad you\'re not gonna live long enough to see it!', 14, 0, 100, 5, 0, 0, 0, 71739, 0, '');
INSERT INTO `creature_text` VALUES (69559, 3, 0, 'Prepare to die, intrudah!', 14, 0, 100, 53, 0, 0, 0, 71740, 0, '');
INSERT INTO `creature_text` VALUES (69559, 4, 0, 'Horaki readies a Frost Shot!', 41, 0, 100, 0, 0, 0, 0, 71741, 0, '');
INSERT INTO `creature_text` VALUES (69559, 5, 0, 'Horaki launches a Hail of Arrows into the sky!', 41, 0, 100, 0, 0, 0, 0, 71742, 0, '');
INSERT INTO `creature_text` VALUES (69810, 0, 0, 'Let\'s get down to business.', 12, 0, 100, 396, 0, 31349, 0, 62100, 0, '');
INSERT INTO `creature_text` VALUES (69810, 1, 0, 'They will learn that no place is safe from the Shado-Pan.', 12, 0, 100, 0, 0, 35837, 0, 72063, 0, '');
INSERT INTO `creature_text` VALUES (69810, 2, 0, 'Well now. You sure know how to arrive in style.', 12, 0, 100, 0, 0, 35828, 0, 72054, 0, '');
INSERT INTO `creature_text` VALUES (69917, 0, 0, 'The mogu forge weapons of both blood and steel. Their blades feast on death.', 12, 0, 100, 0, 0, 35831, 0, 72057, 0, '');
INSERT INTO `creature_text` VALUES (69917, 1, 0, 'Slaves - everywhere slaves. You can see what we fight against.', 12, 0, 100, 0, 0, 35832, 0, 72058, 0, '');
INSERT INTO `creature_text` VALUES (69917, 2, 0, 'Step lightly! We are almost to the gate.', 12, 0, 100, 0, 0, 35841, 0, 72067, 0, '');
INSERT INTO `creature_text` VALUES (69810, 3, 0, 'There - the slave master! Hold his attention while I undo the lock.', 12, 0, 100, 0, 0, 35842, 0, 72068, 0, '');
INSERT INTO `creature_text` VALUES (69923, 0, 0, 'What is this? Another uprising?', 14, 0, 100, 0, 0, 35787, 0, 72430, 0, '');
INSERT INTO `creature_text` VALUES (69923, 1, 0, 'Bend before me!', 14, 0, 100, 0, 0, 35791, 0, 72434, 0, '');
INSERT INTO `creature_text` VALUES (69810, 4, 0, 'Keep him busy! I am almost finished!', 14, 0, 100, 0, 0, 35844, 0, 72070, 0, '');
INSERT INTO `creature_text` VALUES (69923, 2, 0, 'You will be... punished...', 14, 0, 100, 0, 0, 35788, 0, 72436, 0, '');
INSERT INTO `creature_text` VALUES (69810, 5, 0, 'I have it! The gates are undone!', 14, 0, 100, 0, 0, 35845, 0, 72071, 0, '');
INSERT INTO `creature_text` VALUES (69481, 1, 0, 'Rangers! Secure the forge. Smash the gate, so it cannot be closed!', 14, 0, 100, 0, 0, 35938, 0, 72411, 0, '');
INSERT INTO `creature_text` VALUES (69481, 2, 0, 'Incredible work. I knew the Shado-Pan would make excellent allies.', 12, 0, 100, 0, 0, 35666, 0, 72412, 0, '');
INSERT INTO `creature_text` VALUES (69810, 6, 0, 'We share a common enemy, Regent Lord, nothing more.', 12, 0, 100, 2, 0, 35883, 0, 72415, 0, '');
INSERT INTO `creature_text` VALUES (69481, 3, 0, 'Regardless, we have delivered a terrible blow to the Thunder King today. Horde metalsmiths can begin studying mogu fabrication techniques immediately.', 12, 0, 100, 0, 0, 35667, 0, 72413, 0, '');
INSERT INTO `creature_text` VALUES (69810, 7, 0, 'With all respect - these abominable tools should be destroyed!', 12, 0, 100, 5, 0, 35884, 0, 72416, 0, '');
INSERT INTO `creature_text` VALUES (69481, 4, 0, 'In good time, my pandaren friend. In good time.', 12, 0, 100, 0, 0, 35668, 0, 72414, 0, '');
INSERT INTO `creature_text` VALUES (69741, 0, 0, 'Vereesa is waiting for you, champion.', 12, 0, 100, 0, 0, 35538, 0, 72623, 0, '');
INSERT INTO `creature_text` VALUES (69744, 0, 0, 'This beautiful creature you rescued is immune to the lightning strikes that protect the island.', 12, 0, 100, 0, 0, 35933, 0, 72625, 0, '');
INSERT INTO `creature_text` VALUES (69744, 1, 0, 'Do you think you can ride it?', 12, 0, 100, 0, 0, 35934, 0, 72426, 0, '');
INSERT INTO `creature_text` VALUES (69744, 2, 0, 'Wonderful!  Once you take to the skies, the Zandalari may come after you.', 12, 0, 100, 0, 0, 35935, 0, 72627, 0, '');
INSERT INTO `creature_text` VALUES (69744, 3, 0, 'Be ready to dogfight in the air. Jump from mount to mount if you have to!', 12, 0, 100, 0, 0, 35936, 0, 72428, 0, '');
INSERT INTO `creature_text` VALUES (69744, 4, 0, 'A Shado-Pan agent will meet you behind the mines. Good luck!!', 14, 0, 100, 0, 0, 35937, 0, 72629, 0, '');
INSERT INTO `creature_text` VALUES (69741, 1, 0, 'Rangers! Secure the forge. Smash the gate, so it cannot be closed!', 14, 0, 100, 0, 0, 35938, 0, 72411, 0, '');
INSERT INTO `creature_text` VALUES (69741, 2, 0, 'You two did it! I knew the Shado-Pan would make excellent allies.', 12, 0, 100, 0, 0, 35539, 0, 72648, 0, '');
INSERT INTO `creature_text` VALUES (69810, 8, 0, 'We share a common enemy, Lady Proudmoore. Nothing more.', 12, 0, 100, 2, 0, 35846, 0, 72649, 0, '');
INSERT INTO `creature_text` VALUES (69741, 3, 0, 'But together we have delivered a terrible blow to the Thunder King!', 12, 0, 100, 0, 0, 35540, 0, 72651, 0, '');
INSERT INTO `creature_text` VALUES (69741, 4, 0, 'Vereesa, gather up everything we can find before the mogu return. Leave nothing for the Horde to claim.', 12, 0, 100, 0, 0, 35541, 0, 72652, 0, '');
INSERT INTO `creature_text` VALUES (69810, 9, 0, 'My lady. Weapons of the Thunder King are an abomination. They should be destroyed.', 12, 0, 100, 2, 0, 35847, 0, 72650, 0, '');
INSERT INTO `creature_text` VALUES (69741, 5, 0, 'Oh, they will be. But knowledge is power, and I will never turn down an opportunity to study something new. Thank you, Taoshi.', 12, 0, 100, 0, 0, 35542, 0, 72653, 0, '');
INSERT INTO `creature_text` VALUES (69741, 6, 0, 'And thank YOU, hero. It will be difficult to hold the forge, but think of what we can learn here!', 12, 0, 100, 0, 0, 35543, 0, 72654, 0, '');
INSERT INTO `creature_text` VALUES (69457, 0, 0, 'I count six... no, seven guards on patrol in the sky.', 12, 0, 100, 0, 0, 35852, 0, 71821, 0, '');
INSERT INTO `creature_text` VALUES (69457, 1, 0, 'One of them flies higher than the others... He loops far out over the water - alone. He is over-extending. That is our in.', 12, 0, 100, 0, 0, 35853, 0, 71822, 0, '');
INSERT INTO `creature_text` VALUES (69457, 2, 0, 'We will neutralize him and take his route. Let\'s go!', 12, 0, 100, 0, 0, 35854, 0, 71823, 0, '');
INSERT INTO `creature_text` VALUES (69457, 3, 0, 'Just a little closer...', 12, 0, 100, 0, 0, 35855, 0, 71824, 0, '');
INSERT INTO `creature_text` VALUES (69457, 4, 0, 'Hyah!', 12, 0, 100, 0, 0, 35856, 0, 71825, 0, '');
INSERT INTO `creature_text` VALUES (69457, 5, 0, 'The Zandalari are more cautious than I thought. Look down there - swarming with patrols.', 12, 0, 100, 0, 0, 35857, 0, 71826, 0, '');
INSERT INTO `creature_text` VALUES (69457, 6, 0, 'And their shamans have laid out a network of sentry totems.', 12, 0, 100, 0, 0, 35859, 0, 71828, 0, '');
INSERT INTO `creature_text` VALUES (69457, 7, 0, 'One of these smoke bombs will blind a sentry totem.', 12, 0, 100, 0, 0, 35861, 0, 71830, 0, '');
INSERT INTO `creature_text` VALUES (69457, 8, 0, 'Clear a path from the ship to the gate at the upper level - we will need to cross on foot.', 12, 0, 100, 0, 0, 35862, 0, 72406, 0, '');
INSERT INTO `creature_text` VALUES (69457, 9, 0, 'We cannot circle up here for long. Do what you can while I plan our next move.', 12, 0, 100, 0, 0, 35863, 0, 71831, 0, '');
INSERT INTO `creature_text` VALUES (69743, 0, 0, 'Awaken, Hu\'seng! The docks are your responsibility. See that none pass his majesty\'s gates.', 14, 0, 100, 0, 0, 35781, 0, 71833, 0, '');
INSERT INTO `creature_text` VALUES (69483, 0, 0, 'The Thunder King wills it; it shall be done.', 12, 0, 100, 0, 0, 35446, 0, 71834, 0, '');
INSERT INTO `creature_text` VALUES (69457, 10, 0, 'Our time is up. We need to get aboard that ship.', 12, 0, 100, 0, 0, 35864, 0, 71835, 0, '');
INSERT INTO `creature_text` VALUES (69544, 0, 0, 'We should sabotage the siege weapons toward the bow. Then we will go below decks and find the captain.', 12, 0, 100, 0, 0, 35868, 0, 71836, 0, '');
INSERT INTO `creature_text` VALUES (69544, 1, 0, 'Easy now... Get in close and incapacitate them.', 12, 0, 100, 0, 0, 35866, 0, 71837, 0, '');
INSERT INTO `creature_text` VALUES (69544, 2, 0, 'Taoshi nods toward the crewman on the right. Sneak up behind him to knock him out!', 41, 0, 100, 0, 0, 0, 0, 72664, 0, '');
INSERT INTO `creature_text` VALUES (69544, 3, 0, 'There will be a very unpleasant surprise waiting for the next troll who disturbs these. Let\'s keep moving.', 12, 0, 100, 0, 0, 35867, 0, 71840, 0, '');
INSERT INTO `creature_text` VALUES (69544, 4, 0, 'Taoshi motions at Captain Halu\'kal, then draws a finger across her neck.', 41, 0, 100, 25, 0, 0, 0, 72265, 0, '');
INSERT INTO `creature_text` VALUES (69482, 0, 0, 'Hm-wha? Stowaways!', 12, 0, 100, 0, 0, 35321, 0, 71842, 0, '');
INSERT INTO `creature_text` VALUES (69482, 1, 0, 'Oh hoh, you tickle me. But I be done playin\' with you.', 12, 0, 100, 0, 0, 35323, 0, 71844, 0, '');
INSERT INTO `creature_text` VALUES (69482, 2, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|t %s wakes up with a terrible |cFFFF0000|Hspell:140432|h[Hangover]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73028, 0, '');
INSERT INTO `creature_text` VALUES (69482, 3, 0, 'You... never get outta here... alive.', 12, 0, 100, 0, 0, 35325, 0, 71846, 0, '');
INSERT INTO `creature_text` VALUES (69544, 5, 0, 'Not as quiet as I would prefer, but it works.', 12, 0, 100, 0, 0, 35870, 0, 71839, 0, '');
INSERT INTO `creature_text` VALUES (69544, 6, 0, 'The courtyard gate is directly up that hill.', 12, 0, 100, 0, 0, 35871, 0, 71847, 0, '');
INSERT INTO `creature_text` VALUES (69544, 7, 0, 'Remember to stay clear of those sentry totems.', 12, 0, 100, 0, 0, 35872, 0, 71848, 0, '');
INSERT INTO `creature_text` VALUES (69544, 8, 0, 'Sounds like they found our little present.', 12, 0, 100, 0, 0, 35877, 0, 71852, 0, '');
INSERT INTO `creature_text` VALUES (69483, 1, 0, 'Enough hiding, little ones!', 14, 0, 100, 0, 0, 35447, 0, 71854, 0, '');
INSERT INTO `creature_text` VALUES (69483, 2, 0, 'There is no escape now. The only way out is through me!', 14, 0, 100, 0, 0, 35448, 0, 71855, 0, '');
INSERT INTO `creature_text` VALUES (69544, 9, 0, 'Through you, is it? I think we can manage that.', 12, 0, 100, 397, 0, 35878, 0, 71856, 0, '');
INSERT INTO `creature_text` VALUES (69544, 10, 0, 'Would you prefer we go through the belly, or the ribcage?', 12, 0, 100, 45, 0, 35879, 0, 71857, 0, '');
INSERT INTO `creature_text` VALUES (69483, 3, 0, '|TInterface\\Icons\\spell_shadow_destructivesoul.blp:20|t%s casts |cFFFF0000|Hspell:140543|h[Empower Axe]|h|r! Run Away!', 41, 0, 100, 0, 0, 0, 0, 73035, 0, '');
INSERT INTO `creature_text` VALUES (69483, 4, 0, 'You are finished!', 14, 0, 100, 0, 0, 35454, 0, 71863, 0, '');
INSERT INTO `creature_text` VALUES (69483, 5, 0, '|TInterface\\Icons\\warrior_talent_icon_bloodandthunder.blp:20|t%s begins to cast |cFFFF0000|Hspell:140531|h[Desist]|h|r! Kill the  |cFFFF0000Chains|r to free yourself!', 41, 0, 100, 0, 0, 0, 0, 73036, 0, '');
INSERT INTO `creature_text` VALUES (69483, 6, 0, 'Trespassers will be executed!', 14, 0, 100, 0, 0, 35452, 0, 71861, 0, '');
INSERT INTO `creature_text` VALUES (69483, 7, 0, 'To strike me is to strike his Majesty! He will not suffer this insult!', 14, 0, 100, 0, 0, 35455, 0, 71864, 0, '');
INSERT INTO `creature_text` VALUES (69483, 8, 0, '|TInterface\\Icons\\ability_thunderking_thunderstruck.blp:20|tLei Shen has become angered and calls down a |cFFFF0000|Hspell:140549|h[Lightning Storm]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73034, 0, '');
INSERT INTO `creature_text` VALUES (69483, 9, 0, 'None... shall...', 12, 0, 100, 0, 0, 35456, 0, 71865, 0, '');
INSERT INTO `creature_text` VALUES (69544, 11, 0, 'Well fought! Now - the gates. Before the thunder king\'s entire army comes down on us!', 12, 0, 100, 0, 0, 35880, 0, 71866, 0, '');
INSERT INTO `creature_text` VALUES (69615, 2, 0, 'The Horde is just across the courtyard.', 12, 0, 100, 0, 0, 35930, 0, 71907, 0, '');
INSERT INTO `creature_text` VALUES (69616, 1, 0, 'Of course they are. One problem at a time, Vereesa.', 12, 0, 100, 0, 0, 35500, 0, 71908, 0, '');
INSERT INTO `creature_text` VALUES (69616, 0, 0, 'But what?', 12, 0, 100, 0, 0, 35499, 0, 71906, 0, '');
INSERT INTO `creature_text` VALUES (69544, 12, 0, 'The fighting skills of my companion are quickly becoming legendary among the Shado-Pan.', 12, 0, 100, 0, 0, 35881, 0, 71869, 0, '');
INSERT INTO `creature_text` VALUES (69615, 0, 0, 'Good, you are here.', 12, 0, 100, 0, 0, 36594, 0, 71754, 0, '');
INSERT INTO `creature_text` VALUES (69615, 1, 0, 'Lady Proudmoore! Everyone is in position, and the Shado-Pan are inside, but...', 12, 0, 100, 0, 0, 36157, 0, 71905, 0, '');
INSERT INTO `creature_text` VALUES (69201, 0, 0, 'Unda my command, dis ship gonna be feared by all! Isn\'t dat right, Ripi?', 14, 0, 100, 0, 0, 0, 0, 73005, 0, '');
INSERT INTO `creature_text` VALUES (69272, 0, 0, 'Dis ship is unda new management! Anybody not pullin\' dey weight is gonna have dey spirit re-assigned to a golem.', 14, 0, 100, 0, 0, 0, 0, 73004, 0, '');
INSERT INTO `creature_text` VALUES (69616, 2, 0, 'Everyone! Advance!', 14, 0, 100, 0, 0, 35501, 0, 71909, 0, '');
INSERT INTO `creature_text` VALUES (69616, 3, 0, '... and if anyone sees that traitor, Aethas Sunreaver, save that one for me...', 12, 0, 100, 0, 0, 35502, 0, 71910, 0, '');
INSERT INTO `creature_text` VALUES (69530, 0, 0, 'Taran Zhu! It does not matter how many you array against the Thunder King. Soon all will kneel before him.', 14, 0, 100, 0, 0, 35769, 0, 71767, 0, '');
INSERT INTO `creature_text` VALUES (69501, 0, 0, 'YOU will kneel when you beg for my mercy, monster.', 14, 0, 100, 0, 0, 35888, 0, 71766, 0, '');
INSERT INTO `creature_text` VALUES (69617, 4, 0, 'One problem at a time, people. Focus on the Zandalari!', 14, 0, 100, 0, 0, 35643, 0, 71765, 0, '');
INSERT INTO `creature_text` VALUES (69616, 4, 0, 'Ignore the Horde - focus on the Zandalari guarding the door!', 14, 0, 100, 0, 0, 35503, 0, 71911, 0, '');
INSERT INTO `creature_text` VALUES (69501, 1, 0, 'Those statues wield the power of the first emperor. Destroy them!', 14, 0, 100, 0, 0, 35889, 0, 71768, 0, '');
INSERT INTO `creature_text` VALUES (69615, 3, 0, 'Lady Proudmoore - more Zandalari are approaching from the docks.', 14, 0, 100, 0, 0, 35931, 0, 71912, 0, '');
INSERT INTO `creature_text` VALUES (69616, 5, 0, 'They\'re trying to flank us. Hold this position!', 14, 0, 100, 0, 0, 35504, 0, 71913, 0, '');
INSERT INTO `creature_text` VALUES (69501, 2, 0, 'Taoshi, get those doors open.', 12, 0, 100, 0, 0, 35890, 0, 71769, 0, '');
INSERT INTO `creature_text` VALUES (69501, 3, 0, 'Champion, with me. We must press forward while your comrades cover the rear.', 12, 0, 100, 0, 0, 35891, 0, 71770, 0, '');
INSERT INTO `creature_text` VALUES (69501, 4, 0, 'Shan Bu! Again you try to create a God. When will you learn?', 14, 0, 100, 0, 0, 35892, 0, 71776, 0, '');
INSERT INTO `creature_text` VALUES (69530, 1, 0, 'Who needs a God? We have the Thunder King!', 14, 0, 100, 0, 0, 35770, 0, 71774, 0, '');
INSERT INTO `creature_text` VALUES (69530, 2, 0, 'Trolls - make yourselves useful. I am almost finished here.', 14, 0, 100, 0, 0, 35771, 0, 71775, 0, '');
INSERT INTO `creature_text` VALUES (69501, 5, 0, 'Zandalari reinforcements are coming.', 14, 0, 100, 0, 0, 35893, 0, 71777, 0, '');
INSERT INTO `creature_text` VALUES (69501, 6, 0, 'Yalia - we need more healing! Taoshi - cover the rear!', 14, 0, 100, 0, 0, 35894, 0, 71778, 0, '');
INSERT INTO `creature_text` VALUES (69530, 3, 0, 'Arise, Nalak! Protect our beloved emperor.', 14, 0, 100, 0, 0, 35772, 0, 71780, 0, '');
INSERT INTO `creature_text` VALUES (69501, 7, 0, 'Face me, Shan Bu! Or is the court of the Thunder King a den of cowards?', 14, 0, 100, 0, 0, 35895, 0, 71779, 0, '');
INSERT INTO `creature_text` VALUES (69530, 4, 0, 'The emperor will be most pleased when I present him with your head, Shado-Pan.', 14, 0, 100, 0, 0, 35773, 0, 71781, 0, '');
INSERT INTO `creature_text` VALUES (69562, 0, 0, 'I am born of thunder!', 14, 0, 100, 0, 0, 35732, 0, 71355, 0, '');
INSERT INTO `creature_text` VALUES (69530, 5, 0, 'Ours is to rule. Yours is to serve!', 14, 0, 100, 0, 0, 35778, 0, 71882, 0, '');
INSERT INTO `creature_text` VALUES (69501, 8, 0, 'Dance around Shan Bu\'s attacks. We are faster! We are smarter!', 14, 0, 100, 0, 0, 35898, 0, 71879, 0, '');
INSERT INTO `creature_text` VALUES (69530, 6, 0, 'I tire of your insolence! I will choke the life from you, impudent slave!', 14, 0, 100, 0, 0, 35779, 0, 71885, 0, '');
INSERT INTO `creature_text` VALUES (69501, 9, 0, 'Auugh - kill him. Quickly!', 14, 0, 100, 0, 0, 35903, 0, 71886, 0, '');
INSERT INTO `creature_text` VALUES (69530, 7, 0, 'I ... no... My emperor! My emperor, I have failed you!', 14, 0, 100, 0, 0, 0, 0, 71889, 0, '');
INSERT INTO `creature_text` VALUES (69501, 10, 0, 'You see? No power of this world can trample the pure of spirit.', 12, 0, 100, 0, 0, 35905, 0, 71919, 0, '');
INSERT INTO `creature_text` VALUES (69621, 0, 0, 'Lord Zhu! The Alliance - the Horde - all chaos is about to erupt out here!', 14, 0, 100, 0, 0, 35820, 0, 71920, 0, '');
INSERT INTO `creature_text` VALUES (69501, 11, 0, 'Like children.', 12, 0, 100, 0, 0, 36158, 0, 71921, 0, '');
INSERT INTO `creature_text` VALUES (69501, 12, 0, 'Taoshi! Hand me my weapon.', 12, 0, 100, 0, 0, 36159, 0, 71922, 0, '');
INSERT INTO `creature_text` VALUES (69621, 1, 0, 'My Lord... you are gravely wounded!', 12, 0, 100, 0, 0, 35821, 0, 71923, 0, '');
INSERT INTO `creature_text` VALUES (69501, 13, 0, 'My weapon! Champion, with me. I want you to see this.', 12, 0, 100, 0, 0, 35907, 0, 71924, 0, '');
INSERT INTO `creature_text` VALUES (69616, 6, 0, 'Hand over the Archmage and I may yet allow you to walk out of here, Lor\'themar.', 14, 0, 100, 0, 0, 35505, 0, 71925, 0, '');
INSERT INTO `creature_text` VALUES (69617, 5, 0, 'Proudmoore! You will release my people from the Violet Hold, or I will cut you down myself!', 14, 0, 100, 0, 0, 35608, 0, 71926, 0, '');
INSERT INTO `creature_text` VALUES (69616, 7, 0, 'Your people are legitimate prisoners of war. They orchestrated an attack on Darnassus from MY city-', 14, 0, 100, 0, 0, 35506, 0, 71927, 0, '');
INSERT INTO `creature_text` VALUES (69617, 6, 0, 'The Sunreavers knew NOTHING of Garrosh\'s raid on Darnassus!', 14, 0, 100, 0, 0, 35609, 0, 71928, 0, '');
INSERT INTO `creature_text` VALUES (69501, 14, 0, 'ENOUGH!! There will be no more bloodshed today.', 14, 0, 100, 0, 0, 35908, 0, 71930, 0, '');
INSERT INTO `creature_text` VALUES (69501, 15, 0, 'I see now why your Alliance and your Horde cannot stop fighting.', 14, 0, 100, 0, 0, 35909, 0, 71931, 0, '');
INSERT INTO `creature_text` VALUES (69501, 16, 0, 'Every reprisal is itself an act of aggression, and every act of aggression triggers immediate reprisal.', 14, 0, 100, 0, 0, 35910, 0, 71932, 0, '');
INSERT INTO `creature_text` VALUES (69616, 8, 0, 'They have undermined EVERY attempt at peace!', 14, 0, 100, 0, 0, 35507, 0, 71933, 0, '');
INSERT INTO `creature_text` VALUES (69617, 7, 0, 'I must protect my sovereign people.', 14, 0, 100, 0, 0, 35610, 0, 71934, 0, '');
INSERT INTO `creature_text` VALUES (69501, 17, 0, 'SILENCE! YOU must break the cycle.', 14, 0, 100, 0, 0, 35911, 0, 71935, 0, '');
INSERT INTO `creature_text` VALUES (69501, 18, 0, 'It ends TODAY. Here. The cycle ends when you, Regent Lord, and you, Lady Proudmoore, turn from one another. And walk. Away.', 14, 0, 100, 0, 0, 35912, 0, 71936, 0, '');
INSERT INTO `creature_text` VALUES (69617, 8, 0, 'Rangers. Lower your weapons.', 12, 0, 100, 0, 0, 35611, 0, 71939, 0, '');
INSERT INTO `creature_text` VALUES (69616, 9, 0, 'Very well. We will stand down.', 12, 0, 100, 0, 0, 35509, 0, 71942, 0, '');
INSERT INTO `creature_text` VALUES (69615, 4, 0, 'They killed my husband!', 14, 0, 100, 0, 0, 35932, 0, 71943, 0, '');
INSERT INTO `creature_text` VALUES (69616, 10, 0, 'This won\'t bring him back.', 12, 0, 100, 0, 0, 35508, 0, 71944, 0, '');
INSERT INTO `creature_text` VALUES (69616, 11, 0, 'But know this, \"Blood\" Elf: There can be no peace while Hellscream is Warchief of the Horde.', 12, 0, 100, 0, 0, 35510, 0, 71945, 0, '');
INSERT INTO `creature_text` VALUES (69617, 9, 0, 'That is precisely why I wish to conserve our strength today.', 12, 0, 100, 0, 0, 35613, 0, 71946, 0, '');
INSERT INTO `creature_text` VALUES (69617, 10, 0, 'Lady.', 12, 0, 100, 0, 0, 35614, 0, 71949, 0, '');
INSERT INTO `creature_text` VALUES (69616, 12, 0, 'Lord.', 12, 0, 100, 0, 0, 35511, 0, 71948, 0, '');
INSERT INTO `creature_text` VALUES (69616, 13, 0, 'Gather the wounded. Withdraw to the harbor.', 14, 0, 100, 0, 0, 35615, 0, 71950, 0, '');
INSERT INTO `creature_text` VALUES (69617, 11, 0, 'Everyone, regroup back at the camp. Our work here is done.', 14, 0, 100, 0, 0, 35512, 0, 71951, 0, '');
INSERT INTO `creature_text` VALUES (69621, 2, 0, 'Lord Zhu!', 14, 0, 100, 0, 0, 35822, 0, 71952, 0, '');
INSERT INTO `creature_text` VALUES (69501, 19, 0, 'Champion, this cease-fire may not last, but remember what you have seen today.', 12, 0, 100, 0, 0, 35913, 0, 71953, 0, '');
INSERT INTO `creature_text` VALUES (69621, 3, 0, 'We must get you back to the monastery.', 12, 0, 100, 0, 0, 35823, 0, 71954, 0, '');
INSERT INTO `creature_text` VALUES (69501, 20, 0, 'Shan Bu\'s monstrosity remains at the foot of the palace. It must be stopped before it wreaks havoc across Pandaria.', 12, 0, 100, 0, 0, 35914, 0, 71955, 0, '');
INSERT INTO `creature_text` VALUES (69621, 4, 0, 'Yalia, take Lord Zhu to safety.', 12, 0, 100, 0, 0, 35824, 0, 71956, 0, '');
INSERT INTO `creature_text` VALUES (69621, 5, 0, 'Hero, it will be up to you to defeat the Storm Lord Nalak. You will have to marshal a small army!', 12, 0, 100, 0, 0, 35825, 0, 71957, 0, '');
INSERT INTO `creature_text` VALUES (69621, 6, 0, 'But after what I have seen today, I have faith in you.', 12, 0, 100, 0, 0, 35826, 0, 71958, 0, '');
INSERT INTO `creature_text` VALUES (69621, 7, 0, 'It has been a great honor to fight by your side. Thank you.', 12, 0, 100, 0, 0, 35827, 0, 71959, 0, '');
INSERT INTO `creature_text` VALUES (69620, 0, 0, 'Good, you are here.', 12, 0, 100, 0, 0, 36156, 0, 71754, 0, '');
INSERT INTO `creature_text` VALUES (69620, 1, 0, 'Regent Lord! Everyone is in position, and the Shado-Pan are inside, but...', 12, 0, 100, 0, 0, 36595, 0, 71755, 0, '');
INSERT INTO `creature_text` VALUES (69617, 0, 0, 'Yes?', 12, 0, 100, 0, 0, 35639, 0, 2873, 0, '');
INSERT INTO `creature_text` VALUES (69620, 2, 0, 'The Alliance is waiting for us just across the courtyard.', 12, 0, 100, 0, 0, 36385, 0, 71756, 0, '');
INSERT INTO `creature_text` VALUES (69617, 1, 0, 'Ahhh, so the grand lady of the Kirin Tor wants a fight, does she?', 12, 0, 100, 0, 0, 35640, 0, 71758, 0, '');
INSERT INTO `creature_text` VALUES (69617, 2, 0, 'Rangers! Advance!', 14, 0, 100, 0, 0, 35641, 0, 71759, 0, '');
INSERT INTO `creature_text` VALUES (69617, 3, 0, 'I will deal with Proudmoore.', 14, 0, 100, 0, 0, 35642, 0, 71760, 0, '');
INSERT INTO `creature_text` VALUES (68106, 0, 0, '$p, we should discuss matters back in Krasarang. For now, thank you for your help. There is a portal that can return you to Lion\'s Landing whenever you choose.', 12, 0, 100, 0, 0, 0, 0, 69831, 0, '');
INSERT INTO `creature_text` VALUES (68815, 0, 0, 'Who dares disturb the Korune? You shall pay for your insolence!', 12, 0, 100, 0, 0, 0, 0, 69161, 0, '');
INSERT INTO `creature_text` VALUES (67734, 0, 0, 'Good work, $c. These blood elves are actively researching the mogu. It\'s clear that Garrosh is searching for artifacts of power, but we won\'t let him succeed.', 12, 0, 100, 0, 0, 0, 0, 69218, 0, '');
INSERT INTO `creature_text` VALUES (67716, 0, 0, 'Oh! Brann, jus\' look at this claw! I have a creepin\' suspicion that these mogu are meddlin\' with some dark magic.', 12, 0, 100, 0, 0, 0, 0, 69177, 0, '');
INSERT INTO `creature_text` VALUES (67682, 0, 0, 'I hear you, Hilda. Mogu mixin\' up with sha? I don\'t like that one bit! We\'d better bring this evidence back to the King. $n, thanks fer yer help today, but we\'ll take it from here!', 12, 0, 100, 0, 0, 0, 0, 69179, 0, '');
INSERT INTO `creature_text` VALUES (68006, 0, 0, 'Thank you for saving me from that nightmare. I wanted to rip everything apart!', 12, 0, 100, 0, 0, 0, 0, 69338, 0, '');
INSERT INTO `creature_text` VALUES (68006, 0, 1, 'I don\'t know what came over me. Thank you for clearing my head.', 12, 0, 100, 0, 0, 0, 0, 69339, 0, '');
INSERT INTO `creature_text` VALUES (68006, 0, 2, 'Thank you for saving me, soldier. You too, young prince.', 12, 0, 100, 0, 0, 0, 0, 69340, 0, '');
INSERT INTO `creature_text` VALUES (68006, 0, 3, 'I fought against the anger, but it overwhelmed me. You have my thanks.', 12, 0, 100, 0, 0, 0, 0, 69341, 0, '');
INSERT INTO `creature_text` VALUES (70100, 0, 0, 'The trillium you brought me must be refined for our purposes.', 12, 0, 100, 1, 0, 36021, 0, 72551, 0, '');
INSERT INTO `creature_text` VALUES (70100, 1, 0, 'This foundry should be sufficient.  Help me manipulate the molds.', 12, 0, 100, 25, 0, 36022, 0, 72552, 0, '');
INSERT INTO `creature_text` VALUES (70100, 2, 0, 'The Shado-Pan are here! They may be of use to us. We should help.', 12, 0, 100, 0, 0, 36402, 0, 72656, 0, '');
INSERT INTO `creature_text` VALUES (70099, 0, 0, 'The Shado-pan Defender has placed a |cFFFF2222|Hspell:132744|h[Healing Orb]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73015, 0, '');
INSERT INTO `creature_text` VALUES (70099, 1, 0, 'Thank you for your help, adventurer.  We will assist you as best we can in fending off the mogu.', 14, 0, 100, 0, 0, 0, 0, 72419, 0, '');
INSERT INTO `creature_text` VALUES (70100, 3, 0, 'This foundry should be sufficient.  We will empower the metal with an electrical charge.', 12, 0, 100, 1, 0, 36405, 0, 72657, 0, '');
INSERT INTO `creature_text` VALUES (70100, 4, 0, 'Be ready to fight if we draw too much attention.', 14, 0, 100, 0, 0, 36023, 0, 72553, 0, '');
INSERT INTO `creature_text` VALUES (70061, 0, 0, 'The Thunder Forge has begun to charge an area nearby.  Run into it to |cFFFF2222|Hspell:139397|h[Overcharge]|h|r yourself!', 41, 0, 100, 0, 0, 0, 0, 72741, 0, '');
INSERT INTO `creature_text` VALUES (70074, 0, 0, 'Forgemaster Vul\'kon equips his Staff and begins to channel the powers of lightning!', 41, 0, 100, 0, 0, 0, 0, 72995, 0, '');
INSERT INTO `creature_text` VALUES (70074, 1, 0, 'Forgemaster Vul\'kon equips his Hammer and begins to swing with great force!', 41, 0, 100, 0, 0, 0, 0, 72996, 0, '');
INSERT INTO `creature_text` VALUES (70099, 2, 0, 'The forge is safe, and we must take our leave.  Good luck on your quest, adventurer.', 12, 0, 100, 0, 0, 0, 0, 72800, 0, '');
INSERT INTO `creature_text` VALUES (70100, 5, 0, 'Excellent! The materials are prepared.  Follow me.', 12, 0, 100, 4, 0, 36026, 0, 72556, 0, '');
INSERT INTO `creature_text` VALUES (70100, 6, 0, 'How interesting! This forge is built atop of ancient Titan machinery. Lei Shen, you clever, clever tyrant, you!', 12, 0, 100, 0, 0, 36027, 0, 72557, 0, '');
INSERT INTO `creature_text` VALUES (70100, 7, 0, 'Now if I were a dwarf I would just start randomly pushing buttons. But you and I are here for a purpose.', 12, 0, 100, 0, 0, 36028, 0, 72558, 0, '');
INSERT INTO `creature_text` VALUES (70100, 8, 0, 'Look at that. How easy it must have been for Lei Shen to build an empire, for he wielded the power to create worlds.', 12, 0, 100, 0, 0, 36030, 0, 72560, 0, '');
INSERT INTO `creature_text` VALUES (70100, 9, 0, 'The mogu were using only a fraction of the resources available to them. Few among them possessed the cunning of the Thunder King.', 12, 0, 100, 0, 0, 36039, 0, 72569, 0, '');
INSERT INTO `creature_text` VALUES (70100, 10, 0, 'What\'s this - \"Anomaly detected.\" Anomaly?', 12, 0, 100, 6, 0, 36040, 0, 72570, 0, '');
INSERT INTO `creature_text` VALUES (70100, 11, 0, 'Look at that. This old Titan technology is acting like a magnet for the sha.', 12, 0, 100, 1, 0, 36041, 0, 72571, 0, '');
INSERT INTO `creature_text` VALUES (70100, 12, 0, 'You\'re an expert at destroying sha by now. Clean up this mess!', 12, 0, 100, 396, 0, 36042, 0, 72572, 0, '');
INSERT INTO `creature_text` VALUES (69837, 0, 0, 'The Celestial Defender manifests an area of |cFFFF2222|Hspell:140068|h[healing energy]|h|r!', 41, 0, 100, 0, 0, 0, 0, 72536, 0, '');
INSERT INTO `creature_text` VALUES (70283, 0, 0, 'A |cFFFF2222[Thundering Anvil]|r has become overcharged! Activate it to |cFFFF2222|Hspell:138834|h[Discharge]|h|r its energy.', 41, 0, 100, 0, 0, 0, 0, 73026, 0, '');
INSERT INTO `creature_text` VALUES (70100, 13, 0, 'Perhaps if I overcharge the forge, it will destabilize this sha nuisance? Let\'s try it.', 12, 0, 100, 0, 0, 36406, 0, 72658, 0, '');
INSERT INTO `creature_text` VALUES (70100, 14, 0, 'Oh - I may have spoken too soon. The sha grows in strength! Destroy it!', 12, 0, 100, 274, 0, 36408, 0, 72660, 0, '');
INSERT INTO `creature_text` VALUES (70100, 15, 0, 'You aren\'t harboring any bad thoughts, are you?', 12, 0, 100, 0, 0, 36409, 0, 72661, 0, '');
INSERT INTO `creature_text` VALUES (70283, 1, 0, 'I\'ve overcharged the anvils. They will be VERY effective against the sha, but each one can only be used once more!', 12, 0, 100, 22, 0, 36410, 0, 72662, 0, '');
INSERT INTO `creature_text` VALUES (70228, 0, 0, 'The Sha Amalgamation begins casting |cFFFF2222|Hspell:139382|h[Insanity]|h|r!  Discharge one of the anvils to stop it!', 41, 0, 100, 0, 0, 0, 0, 72676, 0, '');
INSERT INTO `creature_text` VALUES (70100, 16, 0, 'There! Our work is complete - we have forged a masterpiece!', 12, 0, 100, 1, 0, 36049, 0, 72579, 0, '');
INSERT INTO `creature_text` VALUES (70100, 17, 0, 'We are done here, but the weapon is not yet tempered.', 12, 0, 100, 1, 0, 36050, 0, 72580, 0, '');
INSERT INTO `creature_text` VALUES (68336, 0, 0, 'Use the Kor\'kron Disguise, then search for intel in the middle of Garrosh\'ar Advance.', 42, 0, 100, 0, 0, 0, 0, 70082, 0, '');
INSERT INTO `creature_text` VALUES (68336, 1, 0, 'I am losing patience, Shan Kien. Tell me where I can find the Divine Bell!', 12, 0, 100, 5, 0, 0, 0, 69923, 0, '');
INSERT INTO `creature_text` VALUES (68339, 0, 0, 'Kill me if you must, but I owe no allegiance to you or your Horde. The only master I serve is the Thunder King.', 12, 0, 100, 0, 0, 0, 0, 69924, 0, '');
INSERT INTO `creature_text` VALUES (68336, 2, 0, 'Ishi, draw your blade. A few carefully delivered cuts will change his mind.', 12, 0, 100, 1, 0, 0, 0, 69925, 0, '');
INSERT INTO `creature_text` VALUES (68287, 0, 0, 'Garrosh, please. There may be another way. One that requires no bloodshed.', 12, 0, 100, 1, 0, 0, 0, 69926, 0, '');
INSERT INTO `creature_text` VALUES (68336, 3, 0, 'Speak, then!', 12, 0, 100, 1, 0, 0, 0, 69927, 0, '');
INSERT INTO `creature_text` VALUES (68287, 1, 0, 'Let us use persuasion, not force. I have here a cask of pandaren Memory Wine. It will allow us to see into Shan Kien\'s past.', 12, 0, 100, 1, 0, 0, 0, 69991, 0, '');
INSERT INTO `creature_text` VALUES (68375, 0, 0, 'WHAT?! You saw where the mogu hid the Divine Bell? I\'ll alert Lion\'s Landing immediately. We gotta get this thing before Garrosh. We\'ll contact you as soon as we find it!', 12, 0, 100, 5, 0, 0, 0, 70083, 0, '');
INSERT INTO `creature_text` VALUES (68551, 0, 0, 'Light... I see... light. Finally, after centuries of silence, someone comes at last.', 12, 0, 100, 0, 0, 34197, 0, 69693, 0, '');
INSERT INTO `creature_text` VALUES (68551, 1, 0, 'I have kept the Bell safe as promised, Master.', 12, 0, 100, 0, 0, 34198, 0, 69694, 0, '');
INSERT INTO `creature_text` VALUES (68551, 2, 0, 'Wait... you are not mogu! Intruders!', 12, 0, 100, 0, 0, 34199, 0, 69695, 0, '');
INSERT INTO `creature_text` VALUES (68551, 3, 0, 'Guardians, awaken! Destroy those who would steal the Thunder King\'s treasure!', 12, 0, 100, 0, 0, 34200, 0, 69696, 0, '');
INSERT INTO `creature_text` VALUES (69099, 6, 0, 'An Essence of Storms erupts from the embedded spear and fixates on you!', 41, 0, 100, 0, 0, 0, 0, 71888, 0, '');
INSERT INTO `creature_text` VALUES (70516, 11, 0, 'Ah! The final piece of the puzzle. The Thunder King took on the powers of Ra-Den, the very essence of a Titan. He united the mogu into an empire and tried to continue the Titans\' work. Of course he saw himself as a God!', 12, 0, 100, 0, 0, 36423, 0, 72372, 0, '');
INSERT INTO `creature_text` VALUES (70516, 10, 0, 'Hmmm. This runestone speaks of mogu savagery. They fell apart into warrior clans. But one King was more ambitious than the others - Lei Shen. The Thunder King. When the Titan Keeper fell silent, Lei Shen decided to speak for him.', 12, 0, 100, 0, 0, 36422, 0, 72371, 0, '');
INSERT INTO `creature_text` VALUES (70516, 9, 0, 'You\'ve found another piece of the puzzle! This was carved by the mogu. They were frustrated - a thousand years with no orders from their master, and their bodies had turned to flesh. The mogu began to argue among themselves.', 12, 0, 100, 0, 0, 36421, 0, 72370, 0, '');
INSERT INTO `creature_text` VALUES (70516, 8, 0, 'This runestone is more recent than the others. Something about \"The sleeping keeper.\" I think it\'s referring to Ra-Den. But why was he asleep?', 12, 0, 100, 0, 0, 36420, 0, 72369, 0, '');
INSERT INTO `creature_text` VALUES (70516, 7, 0, 'Here, this fragment speaks of \"The Winged Legions of Y\'Shaarj.\" Servants of an Old God? Could this mean the mantid?', 12, 0, 100, 0, 0, 36419, 0, 72368, 0, '');
INSERT INTO `creature_text` VALUES (70516, 6, 0, 'We\'re beginning to put the pieces together, champion. The mogu were created and sent here to guard the Vale for their Titan masters. Guard it from what? Were the Old Gods still a threat?', 12, 0, 100, 0, 0, 36418, 0, 72367, 0, '');
INSERT INTO `creature_text` VALUES (70516, 5, 0, 'This runestone speaks of a \"Cradle of Life.\" A place where the Titans could create new creatures. Oh - of course - the Vale of Eternal Blossoms! It was a living laboratory, much like Un\'goro Crater or Sholazar Basin.', 12, 0, 100, 0, 0, 36417, 0, 72366, 0, '');
INSERT INTO `creature_text` VALUES (70516, 4, 0, 'Here, there is mention of an Old God. \"Y\'Shaarj.\" A beast with seven heads, who breathed darkness. I suspect that thing was the source of the sha that haunts Pandaria to this day.', 12, 0, 100, 0, 0, 36416, 0, 72365, 0, '');
INSERT INTO `creature_text` VALUES (70516, 3, 0, 'According to this, the mogu and their master were sent to Pandaria. They were pursuing a great darkness - ah, the fragment is incomplete. We must find others.', 12, 0, 100, 0, 0, 36415, 0, 72364, 0, '');
INSERT INTO `creature_text` VALUES (70516, 2, 0, 'The mogu served a Titan keeper. His name was \"Ra.\" In the mogu tongue, \"Ra-den\" means \"Master Ra.\" Fascinating! I must know more!', 12, 0, 100, 0, 0, 36414, 0, 72363, 0, '');
INSERT INTO `creature_text` VALUES (70516, 1, 0, 'You\'ve found another stone! What have we learned? The \"curse of flesh\" made the mogu mortal. Ah - but later, they found a way to reverse the process. A clever race.', 12, 0, 100, 0, 0, 36413, 0, 72362, 0, '');
INSERT INTO `creature_text` VALUES (70516, 0, 0, 'According to this runestone, the mogu were Titan constructs, soulless creatures built of stone. We suspected as much.', 12, 0, 100, 0, 0, 36412, 0, 72361, 0, '');
INSERT INTO `creature_text` VALUES (70956, 0, 0, 'Thank the laws of physics you\'re here!', 12, 0, 100, 5, 0, 37164, 0, 73242, 0, '');
INSERT INTO `creature_text` VALUES (70956, 1, 0, 'We found somethin\' down there, somethin\' HUGE, and all of a sudden the rocks started fightin\' back and everyone went crazy. Bad business!', 12, 0, 100, 0, 0, 37165, 0, 73118, 0, '');
INSERT INTO `creature_text` VALUES (70956, 2, 0, 'Please, just make it all stop!', 12, 0, 100, 20, 0, 37166, 0, 73119, 0, '');
INSERT INTO `creature_text` VALUES (70824, 0, 0, 'Fiery Anger is about to |cFFFF2222|Hspell:141456|h[Detonate]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73320, 0, '');
INSERT INTO `creature_text` VALUES (70959, 0, 0, 'Minions, destroy these insects!', 14, 0, 100, 0, 0, 0, 0, 73933, 0, '');
INSERT INTO `creature_text` VALUES (70959, 1, 0, '|cFFFF0000|Hspell:141387|h[Urtharges\' Shield]|h|r has been broken!', 41, 0, 100, 0, 0, 0, 0, 73685, 0, '');
INSERT INTO `creature_text` VALUES (70788, 0, 0, '+5 Artifact Bonus!', 41, 0, 100, 0, 0, 0, 0, 74266, 0, '');
INSERT INTO `creature_text` VALUES (70792, 0, 0, '+10 Artifact Bonus!', 41, 0, 100, 0, 0, 0, 0, 74267, 0, '');
INSERT INTO `creature_text` VALUES (71358, 0, 0, 'It\'s all clear then?', 12, 0, 100, 0, 0, 0, 0, 73958, 0, '');
INSERT INTO `creature_text` VALUES (71358, 1, 0, 'No time to waste... let\'s blow this wall to pieces!', 12, 0, 100, 0, 0, 0, 0, 73965, 0, '');
INSERT INTO `creature_text` VALUES (71358, 2, 0, 'That was some explosion!  Let\'s check it out.', 12, 0, 100, 0, 0, 0, 0, 73959, 0, '');
INSERT INTO `creature_text` VALUES (71358, 3, 0, 'It looks like some sort of gigantic chest... probably full of gold.  And it\'s all ours!', 12, 0, 100, 0, 0, 0, 0, 73960, 0, '');
INSERT INTO `creature_text` VALUES (71246, 0, 0, 'Containment failure.  Active defense matrix enabled.', 14, 0, 100, 0, 0, 37205, 0, 73589, 0, '');
INSERT INTO `creature_text` VALUES (71246, 1, 0, 'Power units activated.', 14, 0, 100, 0, 0, 37206, 0, 73591, 0, '');
INSERT INTO `creature_text` VALUES (71123, 0, 0, 'My blood hungers.', 14, 0, 100, 0, 0, 37142, 0, 73594, 0, '');
INSERT INTO `creature_text` VALUES (71123, 1, 0, 'Your class cooldowns have been reset!', 41, 0, 100, 0, 0, 0, 0, 72391, 0, '');
INSERT INTO `creature_text` VALUES (71123, 2, 0, 'I only sleep.', 14, 0, 100, 0, 0, 37143, 0, 73600, 0, '');
INSERT INTO `creature_text` VALUES (70956, 3, 0, 'Just what in the name of all that is unholy was that thing?', 12, 0, 100, 6, 0, 37158, 0, 73583, 0, '');
INSERT INTO `creature_text` VALUES (71177, 0, 0, 'Step aside goblin.  This artifact is exactly what the warchief hoped to find here.  I am taking it under Kor\'kron protection.', 12, 0, 100, 5, 0, 37182, 0, 73601, 0, '');
INSERT INTO `creature_text` VALUES (70956, 4, 0, 'Oh don\'t you worry none, I don\'t think anyone wants to touch that thing again.  I\'ll collect our money and we are outta here.', 12, 0, 100, 1, 0, 37159, 0, 73584, 0, '');
INSERT INTO `creature_text` VALUES (70956, 5, 0, 'Hey hey, what is this, huh?  This is barely a fifth of the promised payment!', 12, 0, 100, 5, 0, 37160, 0, 73585, 0, '');
INSERT INTO `creature_text` VALUES (71177, 1, 0, 'You are paid for all workers present, foreman.  The warchief does not compensate the dead.', 12, 0, 100, 1, 0, 37183, 0, 73602, 0, '');
INSERT INTO `creature_text` VALUES (70956, 6, 0, 'Naw naw naw, see there\'s a contract.  Payment in full is req-', 12, 0, 100, 274, 0, 37161, 0, 73586, 0, '');
INSERT INTO `creature_text` VALUES (71177, 2, 0, 'THIS.  This is what has diluted the vitality of our precious Horde!', 12, 0, 100, 0, 0, 37184, 0, 73603, 0, '');
INSERT INTO `creature_text` VALUES (71177, 3, 0, 'The warchief graciously permits the families of the dead to live in his city.  To die in the service of the Horde should be a great honor, but you see it as another opportunity to line your filthy pockets!', 12, 0, 100, 1, 0, 37185, 0, 73604, 0, '');
INSERT INTO `creature_text` VALUES (71177, 4, 0, 'Your kind DISGUSTS me.  Small.  Weak.  Honorless as thieves.  Be thankful the warchief still tolerates your presence at all.', 12, 0, 100, 5, 0, 37186, 0, 73605, 0, '');
INSERT INTO `creature_text` VALUES (70956, 7, 0, 'Yeah, we\'ll see who\'s got Garrosh\'s back when it counts.', 12, 0, 100, 1, 0, 37162, 0, 73587, 0, '');
INSERT INTO `creature_text` VALUES (71177, 5, 0, 'What was that, goblin?', 12, 0, 100, 547, 0, 37187, 0, 73606, 0, '');
INSERT INTO `creature_text` VALUES (70956, 8, 0, 'Oh yeah, hey no nothing your, uh, your hugeness.  Uh, for the Horde, and all that...  Bye!', 12, 0, 100, 1, 0, 37163, 0, 73588, 0, '');
INSERT INTO `creature_text` VALUES (25740, 2, 0, 'Your frozen reign will not come to pass!', 12, 0, 100, 0, 0, 0, 0, 24894, 0, 'Ahune - SAY_PLAYER_TEXT_2');
INSERT INTO `creature_text` VALUES (25740, 3, 0, 'The Earthen Ring\'s Assault Begins.', 41, 0, 100, 0, 0, 0, 0, 24930, 0, 'Ahune - EMOTE_EARTHEN_ASSAULT');
INSERT INTO `creature_text` VALUES (25740, 4, 0, 'Ahune Retreats.  His defenses diminish.', 41, 0, 100, 0, 0, 0, 0, 24931, 0, 'Ahune - EMOTE_SUBMERGED');
INSERT INTO `creature_text` VALUES (25740, 5, 0, 'Ahune will soon resurface.', 41, 0, 100, 0, 0, 0, 0, 24932, 0, 'Ahune - EMOTE_EMERGE_SOON');
INSERT INTO `creature_text` VALUES (68096, 0, 0, 'This temple is the domain of the Ashtongue Brotherhood. I must ask you to leave - now.', 12, 0, 100, 0, 0, 0, 0, 69433, 0, '');
INSERT INTO `creature_text` VALUES (68096, 1, 0, 'What did you see, brother?', 12, 0, 100, 0, 0, 0, 0, 69431, 0, '');
INSERT INTO `creature_text` VALUES (68137, 0, 0, 'Foolish warlocks. I knew you would try again to ransack our temple...!', 12, 0, 100, 0, 0, 0, 0, 69487, 0, '');
INSERT INTO `creature_text` VALUES (68137, 1, 0, 'Wait... you\'re not one of the warlocks that snuck in here before. Why then are you here?', 12, 0, 100, 0, 0, 0, 0, 69488, 0, '');
INSERT INTO `creature_text` VALUES (68137, 2, 0, 'It has been many months since those two warlocks broke into the temple... but I\'m still not sure what they were after.', 12, 0, 100, 0, 0, 0, 0, 69498, 0, '');
INSERT INTO `creature_text` VALUES (68137, 3, 0, 'Follow me. They visited both the Shrine of Lost Souls and the Temple Summit.', 12, 0, 100, 0, 0, 0, 0, 69499, 0, '');
INSERT INTO `creature_text` VALUES (68137, 4, 0, 'I have decided to trust this warlock for now. Let $ghim:her; move about the temple freely.', 12, 0, 100, 0, 0, 0, 0, 69500, 0, '');
INSERT INTO `creature_text` VALUES (68137, 5, 0, 'It will take many years for us to undo the damage that Magtheridon, Illidan and the fel orcs did to this holy place.', 12, 0, 100, 0, 0, 0, 0, 69501, 0, '');
INSERT INTO `creature_text` VALUES (68137, 6, 0, 'Within lies the Shrine of Lost Souls. The souls of thousands of my fallen brethren remain within this area.', 12, 0, 100, 0, 0, 0, 0, 69502, 0, '');
INSERT INTO `creature_text` VALUES (68137, 7, 0, 'Use caution, there are many traps still active within. Perhaps one of your demon spells will grant you the ability to scout ahead before advancing forward.', 12, 0, 100, 0, 0, 0, 0, 69503, 0, '');
INSERT INTO `creature_text` VALUES (68151, 0, 0, 'The powers of this place are NOT for your kind, warlock!', 14, 0, 100, 0, 0, 0, 0, 69529, 0, '');
INSERT INTO `creature_text` VALUES (68151, 1, 0, 'DIE!', 14, 0, 100, 0, 0, 0, 0, 1694, 0, '');
INSERT INTO `creature_text` VALUES (68151, 2, 0, 'If you love demons so much... then you shall have them!', 14, 0, 100, 0, 0, 0, 0, 69537, 0, '');
INSERT INTO `creature_text` VALUES (68137, 8, 0, '$n! Some of my men are trapped in the basement - there is little time, help me save them!!', 14, 0, 100, 0, 0, 0, 0, 69538, 0, '');
INSERT INTO `creature_text` VALUES (69964, 0, 0, 'BEHOLD! I have truly mastered the fel energies of this world! The demonic power I now command... It is indescribable, unlimited, OMNIPOTENT!', 12, 0, 100, 1, 0, 0, 0, 72452, 0, '');
INSERT INTO `creature_text` VALUES (69964, 1, 0, 'You think I\'ve gone too far? There is no such thing. The Black Harvest will enslave the most powerful demons, draw on their power, and destroy all who oppose us, in this world, and on Azeroth! Do YOU mean to stop me, little one?', 12, 0, 100, 1, 0, 0, 0, 72453, 0, '');
INSERT INTO `creature_text` VALUES (69964, 2, 0, 'I\'d like to see you try.', 12, 0, 100, 1, 0, 0, 0, 72457, 0, '');
INSERT INTO `creature_text` VALUES (69964, 3, 0, 'Hah. Your feeble attacks are entertaining. Would you like to see one of my new pets?', 12, 0, 100, 1, 0, 0, 0, 72456, 0, '');
INSERT INTO `creature_text` VALUES (69964, 4, 0, 'The Annihilan are incredibly destructive creatures. Their sheer strength and power is absolutely unrivaled.', 12, 0, 100, 1, 0, 0, 0, 72458, 0, '');
INSERT INTO `creature_text` VALUES (69964, 5, 0, 'Hah ha ha... You thought you could defeat me? NOTHING can kill me now, puny mortal!', 12, 0, 100, 1, 0, 0, 0, 72500, 0, '');
INSERT INTO `creature_text` VALUES (69964, 6, 0, 'And now, your sad tale comes to an end.', 12, 0, 100, 1, 0, 0, 0, 72501, 0, '');
INSERT INTO `creature_text` VALUES (69964, 7, 0, 'Jubeka?! What are you...?!', 12, 0, 100, 1, 0, 0, 0, 72502, 0, '');
INSERT INTO `creature_text` VALUES (69964, 8, 0, '...AUGHHHHHH!!!', 12, 0, 100, 1, 0, 0, 0, 72503, 0, '');
INSERT INTO `creature_text` VALUES (70166, 0, 0, 'This is only what you once asked me to do if it came to this, Kanrethad. You brought this on yourself.', 12, 0, 100, 1, 0, 0, 0, 72496, 0, '');
INSERT INTO `creature_text` VALUES (70166, 1, 0, 'Kanrethad enveloped himself with so much fel energy that it corrupted him totally. But a small taste of his power should not be harmful. Go ahead.', 12, 0, 100, 1, 0, 0, 0, 72498, 0, '');
INSERT INTO `creature_text` VALUES (70166, 2, 0, 'Now go, and use what you\'ve learned. If you ever wish to have the fel energy purged from you, or reabsorb some of Kanrethad\'s, talk to me. I\'ll be at the Altar of Damnation in Shadowmoon Valley, keeping him banished.', 12, 0, 100, 1, 0, 0, 0, 72499, 0, '');
INSERT INTO `creature_text` VALUES (56779, 0, 0, 'Ah, the dragon child and the hero. Together you will achieve great things.', 12, 0, 100, 2, 0, 37364, 0, 73748, 0, '');
INSERT INTO `creature_text` VALUES (56779, 1, 0, 'Tell me, dragon child: What is the nature of wisdom?', 12, 0, 100, 2, 0, 37365, 0, 73749, 0, '');
INSERT INTO `creature_text` VALUES (71352, 0, 0, 'To do what is right, no matter the cost. That is wisdom.', 12, 0, 100, 1, 0, 37450, 0, 73746, 0, '');
INSERT INTO `creature_text` VALUES (56779, 2, 0, 'Spoken like a true child of the Aspects. Always seeking to change the world.', 12, 0, 100, 2, 0, 37366, 0, 73750, 0, '');
INSERT INTO `creature_text` VALUES (56779, 3, 0, 'But true wisdom comes from KNOWING what is right. And sometimes, DOING nothing at all.', 12, 0, 100, 2, 0, 37367, 0, 73751, 0, '');
INSERT INTO `creature_text` VALUES (71352, 1, 0, 'When is it ever wise not to act for the greater good?', 12, 0, 100, 1, 0, 37451, 0, 73747, 0, '');
INSERT INTO `creature_text` VALUES (56779, 4, 0, 'To learn THAT is to learn true wisdom.', 12, 0, 100, 2, 0, 37368, 0, 73752, 0, '');
INSERT INTO `creature_text` VALUES (56779, 5, 0, 'Come forward! I will give you my blessing.', 12, 0, 100, 2, 0, 37369, 0, 73753, 0, '');
INSERT INTO `creature_text` VALUES (56779, 6, 0, 'My challenge, if you accept it, will test your cunning and footwork even in the midst of life-threatening danger.', 12, 0, 100, 2, 0, 37524, 0, 73754, 0, '');
INSERT INTO `creature_text` VALUES (71166, 0, 0, 'The Red Crane of Hope has inspired the citizens of Pandaria from the very dawn of history. I very much want to speak with him.', 12, 0, 100, 1, 0, 37386, 0, 73550, 0, '');
INSERT INTO `creature_text` VALUES (71166, 1, 0, 'Great Red Crane! Hear our plea.', 12, 0, 100, 1, 0, 37392, 0, 73551, 0, '');
INSERT INTO `creature_text` VALUES (71166, 2, 0, 'My champion and I seek your blessing of hope.', 12, 0, 100, 1, 0, 37393, 0, 73552, 0, '');
INSERT INTO `creature_text` VALUES (71169, 0, 0, 'My my, the child of the Worldbreaker, proof that none are beyond redemption. I am honored by your visit.', 12, 0, 100, 1, 0, 37249, 0, 73559, 0, '');
INSERT INTO `creature_text` VALUES (71169, 1, 0, 'Tell me, son of the Earth-Warder: What is the nature of hope?', 12, 0, 100, 1, 0, 37250, 0, 73560, 0, '');
INSERT INTO `creature_text` VALUES (71166, 3, 0, 'Hope is... a belief in a better tomorrow.', 12, 0, 100, 1, 0, 37394, 0, 73553, 0, '');
INSERT INTO `creature_text` VALUES (71169, 2, 0, 'You speak, but doubt your own words.', 12, 0, 100, 1, 0, 37251, 0, 73561, 0, '');
INSERT INTO `creature_text` VALUES (71166, 4, 0, '...Great Crane. You have not seen what I have seen.', 12, 0, 100, 0, 0, 37395, 0, 73554, 0, '');
INSERT INTO `creature_text` VALUES (71169, 3, 0, 'You underestimate me.', 12, 0, 100, 1, 0, 37252, 0, 73562, 0, '');
INSERT INTO `creature_text` VALUES (71166, 5, 0, '... the fires that once burned the sky will return. It is inevitable. The Burning Legion WILL find Azeroth.', 12, 0, 100, 0, 0, 37396, 0, 73555, 0, '');
INSERT INTO `creature_text` VALUES (71166, 6, 0, 'Seas of blood, cities in ruin! Who are we - one divided world - to stand against a legion?', 12, 0, 100, 0, 0, 37397, 0, 73556, 0, '');
INSERT INTO `creature_text` VALUES (71166, 7, 0, 'You speak of hope. Believe me, the thinnest sliver of belief that we might somehow survive the coming devastation is all that sustains me.', 12, 0, 100, 0, 0, 37398, 0, 73557, 0, '');
INSERT INTO `creature_text` VALUES (71169, 4, 0, 'Rise, son of Deathwing. I will give you my blessing, for you need it more than any I have ever met.', 12, 0, 100, 1, 0, 37253, 0, 73563, 0, '');
INSERT INTO `creature_text` VALUES (71169, 5, 0, 'I challenge you not to think of hope as a vague and unimaginable future.', 12, 0, 100, 1, 0, 37254, 0, 73564, 0, '');
INSERT INTO `creature_text` VALUES (71169, 6, 0, 'Live EVERY day with hope in your heart. In doing so, you CREATE the future you dream of.', 12, 0, 100, 1, 0, 37255, 0, 73565, 0, '');
INSERT INTO `creature_text` VALUES (71166, 8, 0, '... thank you, Great Crane.', 12, 0, 100, 1, 0, 37399, 0, 73558, 0, '');
INSERT INTO `creature_text` VALUES (71169, 7, 0, 'My challenge, if you accept it, tasks you with healing the most grievous of wounds.', 12, 0, 100, 1, 0, 37256, 0, 73566, 0, '');
INSERT INTO `creature_text` VALUES (71142, 0, 0, 'Here we are, the home of Niuzao, the Black Ox. He is the patron spirit of Pandaren fortitude, the only celestial who chooses to live beyond the wall.', 12, 0, 100, 1, 0, 37387, 0, 73524, 0, '');
INSERT INTO `creature_text` VALUES (71142, 1, 0, 'Mighty Black Ox! Hear our plea.', 12, 0, 100, 1, 0, 37409, 0, 73525, 0, '');
INSERT INTO `creature_text` VALUES (71142, 2, 0, 'My champion and I seek your blessing of fortitude.', 12, 0, 100, 1, 0, 37410, 0, 73526, 0, '');
INSERT INTO `creature_text` VALUES (71144, 0, 0, 'So enters the dragon, and his mortal champion. You are welcome here.', 12, 0, 100, 402, 0, 37274, 0, 73530, 0, '');
INSERT INTO `creature_text` VALUES (71144, 1, 0, 'Tell me, Black Prince: What is the nature of fortitude?', 12, 0, 100, 402, 0, 37275, 0, 73531, 0, '');
INSERT INTO `creature_text` VALUES (71142, 3, 0, 'The strength to overcome any hardship! This is fortitude.', 12, 0, 100, 1, 0, 37411, 0, 73527, 0, '');
INSERT INTO `creature_text` VALUES (71144, 2, 0, 'You confuse strength with fortitude, young dragon. But power is worthless without spirit!', 12, 0, 100, 402, 0, 37276, 0, 73532, 0, '');
INSERT INTO `creature_text` VALUES (71144, 3, 0, 'I have seen humble slaves endure unimaginable torture, only to rise up and overthrow their masters.', 12, 0, 100, 402, 0, 37277, 0, 73533, 0, '');
INSERT INTO `creature_text` VALUES (71144, 4, 0, 'And I have seen the mightiest of emperors laid low by the perseverance of the smallest of enemies.', 12, 0, 100, 402, 0, 37278, 0, 73534, 0, '');
INSERT INTO `creature_text` VALUES (71142, 4, 0, 'You mean to say - physical strength is developed without, but fortitude comes from within?', 12, 0, 100, 1, 0, 37412, 0, 73528, 0, '');
INSERT INTO `creature_text` VALUES (71144, 5, 0, 'Precisely!', 12, 0, 100, 1, 0, 37279, 0, 73535, 0, '');
INSERT INTO `creature_text` VALUES (71142, 5, 0, '...Sure. I knew that.', 12, 0, 100, 1, 0, 37413, 0, 73529, 0, '');
INSERT INTO `creature_text` VALUES (71144, 6, 0, 'Of course you did. We will put your understanding to the test.', 12, 0, 100, 1, 0, 37280, 0, 73536, 0, '');
INSERT INTO `creature_text` VALUES (71144, 7, 0, 'My challenge, if you accept it, will test your champion\'s ability to defend and protect while enduring terrible hardship.', 12, 0, 100, 402, 0, 37281, 0, 73537, 0, '');
INSERT INTO `creature_text` VALUES (71108, 0, 0, 'I am looking forward to this: The White Tiger is the embodiment of strength in Pandaria!', 12, 0, 100, 1, 0, 37385, 0, 73470, 0, '');
INSERT INTO `creature_text` VALUES (71108, 1, 0, 'Great White Tiger! Hear our plea.', 12, 0, 100, 5, 0, 37434, 0, 73477, 0, '');
INSERT INTO `creature_text` VALUES (71108, 2, 0, 'My champion and I seek the blessing of your strength.', 12, 0, 100, 1, 0, 37467, 0, 73479, 0, '');
INSERT INTO `creature_text` VALUES (71107, 0, 0, 'Ah, the young black dragon graces my temple. The deeds of you and your champion are quickly becoming Pandaren legend.', 12, 0, 100, 1, 0, 37347, 0, 73478, 0, '');
INSERT INTO `creature_text` VALUES (71108, 3, 0, 'I take great pride in the company I keep.', 12, 0, 100, 1, 0, 37435, 0, 73480, 0, '');
INSERT INTO `creature_text` VALUES (71107, 1, 0, 'Tell me, young prince: What is the nature of strength?', 12, 0, 100, 1, 0, 37348, 0, 73481, 0, '');
INSERT INTO `creature_text` VALUES (71108, 4, 0, 'The power to crush one\'s foes. That is strength.', 12, 0, 100, 1, 0, 37436, 0, 73482, 0, '');
INSERT INTO `creature_text` VALUES (71107, 2, 0, 'You speak only in terms of one\'s enemies.', 12, 0, 100, 1, 0, 37349, 0, 73483, 0, '');
INSERT INTO `creature_text` VALUES (71108, 5, 0, 'Certainly, strength can benefit one\'s allies.', 12, 0, 100, 273, 0, 37437, 0, 73484, 0, '');
INSERT INTO `creature_text` VALUES (71107, 3, 0, 'How so?', 12, 0, 100, 1, 0, 37350, 0, 73485, 0, '');
INSERT INTO `creature_text` VALUES (71108, 6, 0, '...by using it to crush their enemies!', 12, 0, 100, 5, 0, 37438, 0, 73486, 0, '');
INSERT INTO `creature_text` VALUES (71107, 4, 0, 'A black dragon, through and through. Very well, I will give you my blessing, but also some advice:', 12, 0, 100, 1, 0, 37351, 0, 73487, 0, '');
INSERT INTO `creature_text` VALUES (71107, 5, 0, 'Strength used in the service of others is twice as powerful as strength spent on our foes.', 12, 0, 100, 1, 0, 37352, 0, 73488, 0, '');
INSERT INTO `creature_text` VALUES (71107, 6, 0, 'Now. My challenge, should you accept it, will test your ability to stand right in the face of danger and persevere.', 12, 0, 100, 1, 0, 37353, 0, 73489, 0, '');
INSERT INTO `creature_text` VALUES (37123, 5, 0, 'By the light, the transporter worked!', 14, 0, 100, 0, 0, 16814, 0, 37435, 0, '');
INSERT INTO `creature_text` VALUES (33701, 7, 0, 'Yogg-Saron is here? It sounds like we really will have our hands full then.', 12, 0, 100, 0, 0, 0, 0, 33713, 0, '');
INSERT INTO `creature_text` VALUES (33701, 8, 0, 'What... What did you just do, $n?! Brann! Braaaaannn!', 14, 0, 100, 0, 0, 0, 0, 34446, 0, '');
INSERT INTO `creature_text` VALUES (33701, 9, 0, 'Brann! $n just activated the orbital defense system! If we don\'t get out of here soon, we\'re going to be toast!', 14, 0, 100, 0, 0, 0, 0, 34447, 0, '');
INSERT INTO `creature_text` VALUES (33701, 10, 0, 'Looks like the battle\'s about to get under way, that\'s my cue to leave. We\'ll have to pick this up later.', 12, 0, 100, 0, 0, 0, 0, 33714, 0, '');
INSERT INTO `creature_text` VALUES (33686, 7, 0, 'Entity designate: Yogg-Saron. Security has been compromised. Prison operational status unknown. Unable to contact Watchers for notification purposes.', 12, 0, 100, 0, 0, 0, 0, 33711, 0, '');
INSERT INTO `creature_text` VALUES (33579, 0, 0, 'Pentarus, you heard the $gman:woman;. Have your mages release the shield and let these brave souls through!', 14, 0, 100, 0, 0, 0, 0, 33671, 0, '');
INSERT INTO `creature_text` VALUES (33579, 1, 0, 'Okay! Let\'s move out. Get into your machines; I\'ll speak to you from here via the radio!', 12, 0, 100, 0, 0, 15807, 0, 33684, 0, '');
INSERT INTO `creature_text` VALUES (33579, 2, 0, 'Our allies are ready. Bring down the shield and make way!', 14, 0, 100, 0, 0, 0, 0, 33687, 0, '');
INSERT INTO `creature_text` VALUES (33579, 3, 0, 'The iron dwarves have been seen emerging from the bunkers at the base of the pillars straight ahead of you! Destroy the bunkers, and they\'ll be forced to fall back!', 12, 0, 100, 0, 0, 15794, 0, 34145, 0, '');
INSERT INTO `creature_text` VALUES (33579, 4, 0, 'There are four generators powering the defense structures. If you sabotage the generators, the missile attacks will stop!', 12, 0, 100, 0, 0, 15796, 0, 34147, 0, '');
INSERT INTO `creature_text` VALUES (33624, 0, 0, 'Of course, Brann. We will have the shield down momentarily.', 12, 0, 100, 0, 0, 0, 0, 33673, 0, '');
INSERT INTO `creature_text` VALUES (33624, 1, 0, 'Mages of the Kirin Tor, on Brann\'s Command, release the shield! Defend this platform and our allies with your lives! For Dalaran!', 14, 0, 100, 0, 0, 0, 0, 33677, 0, '');
INSERT INTO `creature_text` VALUES (33696, 6, 0, '%s nods.', 16, 0, 100, 0, 0, 0, 0, 3065, 0, '');
INSERT INTO `creature_text` VALUES (33696, 11, 0, '%s whispers something to Brann.', 16, 0, 100, 0, 0, 0, 0, 33716, 0, '');
INSERT INTO `creature_text` VALUES (71107, 7, 0, 'Let the challenge begin! Hero, you must defeat the Black Prince in close-quarters combat.', 12, 0, 100, 1, 0, 37343, 0, 73508, 0, '');
INSERT INTO `creature_text` VALUES (71108, 7, 0, 'Wait - that\'s absurd. I would annihilate my own champion!', 12, 0, 100, 274, 0, 37388, 0, 73512, 0, '');
INSERT INTO `creature_text` VALUES (71107, 8, 0, 'You, my dark prince, will be blindfolded.', 12, 0, 100, 2, 0, 37371, 0, 73766, 0, '');
INSERT INTO `creature_text` VALUES (71108, 8, 0, '... interesting. But the outcome is unlikely to change.', 12, 0, 100, 6, 0, 37389, 0, 73513, 0, '');
INSERT INTO `creature_text` VALUES (71107, 9, 0, 'Black Prince, you are very strong. But what good is strength without the vision to guide it?', 12, 0, 100, 1, 0, 37345, 0, 73510, 0, '');
INSERT INTO `creature_text` VALUES (71108, 9, 0, 'Very well. I am sorry, champion - But I will not be the one learning a lesson today.', 12, 0, 100, 1, 0, 37390, 0, 73514, 0, '');
INSERT INTO `creature_text` VALUES (71108, 10, 0, 'A blind dragon. Ridiculous!', 12, 0, 100, 1, 0, 37391, 0, 73517, 0, '');
INSERT INTO `creature_text` VALUES (71426, 0, 0, 'Welcome back, champion!', 12, 0, 100, 1, 0, 37415, 0, 74101, 0, '');
INSERT INTO `creature_text` VALUES (71426, 1, 0, 'Excellent work with the celestials. Can you believe how chatty they were?', 12, 0, 100, 1, 0, 37416, 0, 74102, 0, '');
INSERT INTO `creature_text` VALUES (71426, 2, 0, 'I just wanted them to hand us our reward, but they were just, \"talk talk talk talk talk.\"', 12, 0, 100, 1, 0, 37417, 0, 74103, 0, '');
INSERT INTO `creature_text` VALUES (71426, 3, 0, 'I\'m sorry - where were we? Oh yes! I have something for you:', 12, 0, 100, 1, 0, 37418, 0, 74104, 0, '');
INSERT INTO `creature_text` VALUES (71426, 4, 0, 'The very spirits of Pandaria are supporting us in our grand crusade! Behold:', 12, 0, 100, 1, 0, 37419, 0, 74105, 0, '');
INSERT INTO `creature_text` VALUES (71426, 5, 0, 'The strength of the White Tiger.', 12, 0, 100, 25, 0, 37420, 0, 74106, 0, '');
INSERT INTO `creature_text` VALUES (71426, 6, 0, 'The wisdom of the Jade Serpent.', 12, 0, 100, 1, 0, 37421, 0, 74107, 0, '');
INSERT INTO `creature_text` VALUES (71426, 7, 0, 'The fortitude of the Black Ox.', 12, 0, 100, 1, 0, 37422, 0, 74108, 0, '');
INSERT INTO `creature_text` VALUES (71426, 8, 0, 'And from the Red Crane, a shining beacon of hope for a better world!', 12, 0, 100, 1, 0, 37423, 0, 74109, 0, '');
INSERT INTO `creature_text` VALUES (71426, 9, 0, 'Our world.', 12, 0, 100, 1, 0, 37424, 0, 74110, 0, '');
INSERT INTO `creature_text` VALUES (71426, 10, 0, 'There! It is ready.', 12, 0, 100, 1, 0, 37425, 0, 74111, 0, '');
INSERT INTO `creature_text` VALUES (73330, 0, 0, 'Ah, we adventure together again, except this time I am afraid that the circumstances are much more dire.', 14, 0, 100, 378, 0, 38120, 0, 74968, 0, '');
INSERT INTO `creature_text` VALUES (73330, 1, 0, 'The land is scarred, the ever-blooming trees and plants wither and die as the water from the pools drains away.', 14, 0, 100, 378, 0, 38122, 0, 74970, 0, '');
INSERT INTO `creature_text` VALUES (73349, 0, 0, 'Our work is not yet complete! Stop them!', 14, 0, 100, 0, 0, 0, 0, 76003, 0, '');
INSERT INTO `creature_text` VALUES (73330, 2, 0, 'Once, the Titans used these life-giving waters to create and shape all life in Pandaria.', 14, 0, 100, 0, 0, 38124, 0, 74972, 0, '');
INSERT INTO `creature_text` VALUES (73330, 3, 0, 'It is these waters that kept the Vale in bloom.  Their runoff into the Valley of the Four Winds created the most fertile farmland in the world!', 14, 0, 100, 378, 0, 38125, 0, 74973, 0, '');
INSERT INTO `creature_text` VALUES (73330, 4, 0, 'And now, the malignance of the Old God has manifested itself within the waters.', 14, 0, 100, 1, 0, 38126, 0, 74974, 0, '');
INSERT INTO `creature_text` VALUES (73330, 5, 0, 'Such a foul, foul thing - destroy it, before it seeps deep into the soil and corrupts all of Pandaria!', 14, 0, 100, 5, 0, 38127, 0, 74975, 0, '');
INSERT INTO `creature_text` VALUES (73342, 0, 0, 'Defend me!', 14, 0, 100, 0, 0, 0, 0, 76030, 0, '');
INSERT INTO `creature_text` VALUES (71543, 0, 0, '|TInterface\\Icons\\ability_shawaterelemental_split.blp:20|t%s |cFFFF0000|Hspell:143020|h[Splits]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74312, 2, '');
INSERT INTO `creature_text` VALUES (71543, 1, 0, '|TInterface\\Icons\\ability_shawaterelemental_reform.blp:20|t%s |cFFFF0000|Hspell:143469|h[Reforms]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74315, 2, '');
INSERT INTO `creature_text` VALUES (71543, 2, 0, '|TInterface\\Icons\\spell_frost_summonwaterelemental.blp:20|t%s begins to cast |cFFFF0000|Hspell:143309|h[Swirl]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74311, 2, '');
INSERT INTO `creature_text` VALUES (73330, 6, 0, 'Ah, you have done it!  The waters are pure once more.', 14, 0, 100, 5, 0, 38128, 0, 74976, 0, '');
INSERT INTO `creature_text` VALUES (73330, 7, 0, 'Can you feel their life-giving energies flow through you?', 14, 0, 100, 6, 0, 38129, 0, 74977, 0, '');
INSERT INTO `creature_text` VALUES (73330, 8, 0, 'It will take much time for the Vale to heal, but you have given us hope!', 14, 0, 100, 1, 0, 38130, 0, 74978, 0, '');
INSERT INTO `creature_text` VALUES (34054, 1, 0, 'There are four generators powering the defense structures. If you sabotage the generators, the missile attacks will stop!', 12, 0, 100, 0, 0, 15796, 0, 34147, 3, '');
INSERT INTO `creature_text` VALUES (34054, 2, 0, 'This tower powers the Hammer of Hodir. It is said to have the power to turn entire armies to ice! ', 12, 0, 100, 0, 0, 15797, 0, 34148, 3, '');
INSERT INTO `creature_text` VALUES (34054, 3, 0, 'You\'re approaching the tower of Freya. It contains the power to turn barren wastelands into jungles teeming with life overnight. ', 12, 0, 100, 0, 0, 15798, 0, 34149, 3, '');
INSERT INTO `creature_text` VALUES (34054, 4, 0, 'This generator powers Mimiron\'s Gaze. In moments, it can turn earth to ash, stone to magma--we cannot let it reach full power!', 12, 0, 100, 0, 0, 15799, 0, 34150, 3, '');
INSERT INTO `creature_text` VALUES (34054, 5, 0, 'Ah, the tower of Krolmir. It is said that the power of Thorim has been used only once... and that it turned an entire continent to dust. ', 12, 0, 100, 0, 0, 15801, 0, 34151, 3, '');
INSERT INTO `creature_text` VALUES (34054, 6, 0, 'The control unit for the missile generator is on the top floor of the tower. If you can find a way to get up there, you can shut it down yourselves.', 12, 0, 100, 0, 0, 15802, 0, 34152, 3, '');
INSERT INTO `creature_text` VALUES (34054, 7, 0, 'It appears you are near a repair station! Drive your vehicle onto the platform, and it should be automatically repaired.', 12, 0, 100, 0, 0, 15803, 0, 34153, 3, '');
INSERT INTO `creature_text` VALUES (34054, 8, 0, 'You\'ve done it! You\'ve broken the defenses of Ulduar. In a few moments, we will be dropping in to...', 12, 0, 100, 0, 0, 15804, 0, 34154, 3, '');
INSERT INTO `creature_text` VALUES (34054, 9, 0, 'What is that? Be careful! Something\'s headed your way!', 12, 0, 100, 0, 0, 15805, 0, 34155, 3, '');
INSERT INTO `creature_text` VALUES (34054, 10, 0, 'Quickly! Evasive action! Evasive act--', 12, 0, 100, 0, 0, 15806, 0, 34156, 3, '');
INSERT INTO `creature_text` VALUES (34054, 11, 0, 'Okay! Let\'s move out. Get into your machines; I\'ll speak to you from here via the radio!', 12, 0, 100, 0, 0, 15807, 0, 33684, 3, '');
INSERT INTO `creature_text` VALUES (33626, 2, 0, 'The darned thing just started working! This should take you right up to the end of the concourse, where you last fought the Flame Leviathan.', 12, 0, 100, 0, 0, 0, 0, 34189, 0, '');
INSERT INTO `creature_text` VALUES (73330, 9, 0, 'Can it be...? Oh no, no no no no no.', 14, 0, 100, 274, 0, 38131, 0, 74979, 0, '');
INSERT INTO `creature_text` VALUES (73330, 10, 0, 'The Golden Lotus... they pledged their lives to defend this place.', 14, 0, 100, 1, 0, 38132, 0, 74980, 0, '');
INSERT INTO `creature_text` VALUES (71475, 0, 0, 'You... Rook knows you....', 14, 0, 100, 396, 0, 38718, 0, 76023, 0, '');
INSERT INTO `creature_text` VALUES (73330, 11, 0, 'Yes!  Rook Stonetoe!  You remember me!  What has happened to you?', 14, 0, 100, 5, 0, 38133, 0, 74981, 0, '');
INSERT INTO `creature_text` VALUES (71475, 1, 0, 'Rook... does not know... head... is cloudy....', 14, 0, 100, 396, 0, 38720, 0, 76024, 0, '');
INSERT INTO `creature_text` VALUES (71480, 0, 0, 'We know only despair now. A fitting punishment for our failure....', 14, 0, 100, 396, 0, 38415, 0, 76025, 0, '');
INSERT INTO `creature_text` VALUES (73330, 12, 0, 'I see. This sha energy has trapped their spirits here, to endlessly relive their failure. It feeds on their despair.', 14, 0, 100, 1, 0, 38134, 0, 74982, 0, '');
INSERT INTO `creature_text` VALUES (71475, 2, 0, 'Please... help....', 14, 0, 100, 396, 0, 38722, 0, 76026, 0, '');
INSERT INTO `creature_text` VALUES (73330, 13, 0, 'This is a fate far worse than death - please, heroes!  Set their souls free!', 14, 0, 100, 1, 0, 38135, 0, 74984, 0, '');
INSERT INTO `creature_text` VALUES (71475, 3, 0, 'Sun... He... aid me.', 14, 0, 100, 0, 0, 38716, 0, 74647, 0, '');
INSERT INTO `creature_text` VALUES (71480, 1, 0, '|TInterface\\Icons\\ability_druid_typhoon:20|t%s enters a |cFFFF0000|Hspell:143559|h[Dark Meditation]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74653, 0, '');
INSERT INTO `creature_text` VALUES (71480, 2, 0, 'Protectors, come to my aid!', 14, 0, 100, 0, 0, 38420, 0, 74634, 0, '');
INSERT INTO `creature_text` VALUES (71479, 0, 0, '|TInterface\\Icons\\ability_rogue_deadliness:20|t%s creates a |cFFFF0000|Hspell:143840|h[Mark of Anguish]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74655, 0, '');
INSERT INTO `creature_text` VALUES (71479, 1, 0, '%s signals for help.', 16, 0, 100, 0, 0, 0, 0, 74648, 0, '');
INSERT INTO `creature_text` VALUES (71475, 4, 0, '|TInterface\\Icons\\spell_monk_ringofpeace:20|t%s calls upon |cFFFF0000|Hspell:143955|h[Misery, Sorrow, and Gloom]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74658, 0, '');
INSERT INTO `creature_text` VALUES (71475, 5, 0, 'Rook... needs help!', 14, 0, 100, 0, 0, 38727, 0, 74644, 0, '');
INSERT INTO `creature_text` VALUES (71480, 3, 0, 'We have not fulfilled our oath!', 14, 0, 100, 0, 0, 38421, 0, 74636, 0, '');
INSERT INTO `creature_text` VALUES (71480, 4, 0, '|TInterface\\Icons\\ability_priest_pathofthedevout:20|t%s begins to cast |cFFFF0000|Hspell:143497|h[Bond of the Golden Lotus]|h|r.', 41, 0, 100, 0, 0, 0, 0, 74354, 0, '');
INSERT INTO `creature_text` VALUES (71479, 2, 0, '|TInterface\\Icons\\ability_priest_pathofthedevout:20|t%s begins to cast |cFFFF0000|Hspell:143497|h[Bond of the Golden Lotus]|h|r.', 41, 0, 100, 0, 0, 0, 0, 74354, 0, '');
INSERT INTO `creature_text` VALUES (71475, 6, 0, '|TInterface\\Icons\\ability_priest_pathofthedevout:20|t%s begins to cast |cFFFF0000|Hspell:143497|h[Bond of the Golden Lotus]|h|r.', 41, 0, 100, 0, 0, 0, 0, 74354, 0, '');
INSERT INTO `creature_text` VALUES (71475, 7, 0, 'Rook... not safe...', 14, 0, 100, 0, 0, 38728, 0, 74643, 0, '');
INSERT INTO `creature_text` VALUES (71479, 3, 0, '%s squints menacingly.', 16, 0, 100, 0, 0, 0, 0, 75973, 0, '');
INSERT INTO `creature_text` VALUES (73330, 14, 0, 'Be at peace, dear friends.', 14, 0, 100, 1, 0, 38136, 0, 74985, 0, '');
INSERT INTO `creature_text` VALUES (73330, 15, 0, 'May your souls become one with the land you gave your life to protect.', 14, 0, 100, 1, 0, 38137, 0, 74986, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 0, 'This footprint is too muddy to be useful.', 41, 0, 100, 0, 0, 0, 0, 58658, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 1, 'Next to this track, you see some bits of chewed up carrot.', 41, 0, 100, 0, 0, 0, 0, 58659, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 2, 'The muddy track clearly shows a dexterous paw.', 41, 0, 100, 0, 0, 0, 0, 58660, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 3, 'Stamped into the footprint is a clod of mud mixed with hay and yak hair.', 41, 0, 100, 0, 0, 0, 0, 58661, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 4, 'Behind the print is the long impression of a prehensile tail.', 41, 0, 100, 0, 0, 0, 0, 58662, 0, '');
INSERT INTO `creature_text` VALUES (59505, 0, 5, 'Next to signs of a scuffle is a sharp, pointy spearhead and some colorful feathers.', 41, 0, 100, 0, 0, 0, 0, 58663, 0, '');
INSERT INTO `creature_text` VALUES (58918, 0, 0, 'You found...', 41, 0, 100, 0, 0, 0, 0, 58910, 0, '');
INSERT INTO `creature_text` VALUES (58918, 1, 0, 'A Lost Hen! Bring her back to a chicken coop.', 41, 0, 100, 0, 0, 0, 0, 58912, 0, '');
INSERT INTO `creature_text` VALUES (58918, 2, 0, 'A Wild Prairie Chicken! Kick him out of here!', 41, 0, 100, 0, 0, 0, 0, 58914, 0, '');
INSERT INTO `creature_text` VALUES (58918, 3, 0, 'A Marmot Disguised in Chicken Feathers! Expose him for the sham he really is!', 41, 0, 100, 0, 0, 0, 0, 58915, 0, '');
INSERT INTO `creature_text` VALUES (58918, 4, 0, 'An Amorous Rooster! Dip him in the water to cool his passions.', 41, 0, 100, 0, 0, 0, 0, 58913, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 0, 'Old Hillpaw\'s prize-winning chicken, Mother Clucks!', 41, 0, 100, 0, 0, 0, 0, 58916, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 1, 'Old Hillpaw\'s prize-winning chicken, Madam Specklybottom!', 41, 0, 100, 0, 0, 0, 0, 58918, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 2, 'Old Hillpaw\'s prize-winning chicken, Thunderpeck!', 41, 0, 100, 0, 0, 0, 0, 58919, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 3, 'Old Hillpaw\'s prize-winning chicken, Mantidsbane!', 41, 0, 100, 0, 0, 0, 0, 58920, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 4, 'Old Hillpaw\'s prize-winning chicken, Rumblefeather!', 41, 0, 100, 0, 0, 0, 0, 58922, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 5, 'Old Hillpaw\'s prize-winning chicken, Egga!', 41, 0, 100, 0, 0, 0, 0, 58923, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 6, 'Old Hillpaw\'s prize-winning chicken, Stumbleflutter!', 41, 0, 100, 0, 0, 0, 0, 58925, 0, '');
INSERT INTO `creature_text` VALUES (58918, 5, 7, 'Old Hillpaw\'s prize-winning chicken, Buck-Bucka Betty!', 41, 0, 100, 0, 0, 0, 0, 58926, 0, '');
INSERT INTO `creature_text` VALUES (58666, 0, 0, 'KOR! NOOOOOOOO!', 14, 0, 100, 0, 0, 0, 0, 57440, 0, '');
INSERT INTO `creature_text` VALUES (58224, 0, 0, 'You fail to bend the knee to our power? Then die like the vermin you are.', 12, 0, 100, 0, 0, 0, 0, 57437, 0, '');
INSERT INTO `creature_text` VALUES (58975, 0, 0, 'What now, Sunwalker?', 12, 0, 100, 0, 0, 0, 0, 58095, 0, '');
INSERT INTO `creature_text` VALUES (58666, 1, 0, 'I think we have both lost enough today, old elf. See to your dead.', 12, 0, 100, 0, 0, 0, 0, 58097, 0, '');
INSERT INTO `creature_text` VALUES (58975, 1, 0, 'Agreed.', 12, 0, 100, 0, 0, 0, 0, 58096, 0, '');
INSERT INTO `creature_text` VALUES (58975, 2, 0, 'Lyalia! I am so sorry, my child.', 12, 0, 100, 0, 0, 0, 0, 57631, 0, '');
INSERT INTO `creature_text` VALUES (55092, 0, 0, 'Did my son send you? There\'s plenty of stew to go around. Please, help yourself.', 12, 0, 100, 0, 0, 0, 0, 53420, 0, '');
INSERT INTO `creature_text` VALUES (54915, 0, 0, '$n, you have no idea how glad I am to see you. Supervising is hungry work.', 12, 0, 100, 0, 0, 0, 0, 53413, 0, '');
INSERT INTO `creature_text` VALUES (72872, 0, 0, 'Astounding! Another chamber that looks to originate from these \"Titans.\"', 12, 0, 100, 0, 0, 38138, 0, 74987, 0, '');
INSERT INTO `creature_text` VALUES (72872, 1, 0, 'It must have been hidden away long before recorded history.  And with good reason!', 12, 0, 100, 0, 0, 38139, 0, 74988, 0, '');
INSERT INTO `creature_text` VALUES (72872, 2, 0, 'In the chamber ahead, under our peaceful land, slept the heart of an Old God.', 12, 0, 100, 0, 0, 38140, 0, 74989, 0, '');
INSERT INTO `creature_text` VALUES (71965, 0, 0, 'Halt!', 14, 0, 100, 0, 0, 38883, 0, 19511, 0, '');
INSERT INTO `creature_text` VALUES (72872, 3, 0, 'Oh my!  What is this?  Hello!  I am Lorewalker Cho!', 12, 0, 100, 3, 0, 38141, 0, 74990, 0, '');
INSERT INTO `creature_text` VALUES (71965, 1, 0, 'No further corruption will enter the heart chamber!', 14, 0, 100, 0, 0, 38884, 0, 75027, 0, '');
INSERT INTO `creature_text` VALUES (72872, 4, 0, 'Further corruption?  No, we are here to stop the corruption, and save Pandaria!', 12, 0, 100, 274, 0, 38142, 0, 74991, 0, '');
INSERT INTO `creature_text` VALUES (71965, 2, 0, 'You wish to purge the corruption?', 14, 0, 100, 0, 0, 38885, 0, 75028, 0, '');
INSERT INTO `creature_text` VALUES (72872, 5, 0, 'Yes! Please let us pass!', 12, 0, 100, 273, 0, 38143, 0, 74992, 0, '');
INSERT INTO `creature_text` VALUES (71965, 3, 0, 'Should you pass this door, at this time, you would fail!  You, all of you, are corrupted with the insiduous plague known as Pride.', 14, 0, 100, 0, 0, 38886, 0, 75029, 0, '');
INSERT INTO `creature_text` VALUES (71965, 4, 0, 'You stand tall and proud atop your accomplishments, and this will be your downfall.', 14, 0, 100, 0, 0, 0, 0, 75159, 0, '');
INSERT INTO `creature_text` VALUES (71965, 5, 0, 'Should you wish to defeat the corruption, you will first need to purify the corruption within yourselves.', 14, 0, 100, 0, 0, 0, 0, 75160, 0, '');
INSERT INTO `creature_text` VALUES (71965, 6, 0, 'Speak to me again when you are prepared to face your inner demons.', 14, 0, 100, 0, 0, 38887, 0, 75030, 0, '');
INSERT INTO `creature_text` VALUES (71965, 7, 0, 'Very well, I will create a field to keep your corruption quarantined.', 14, 0, 100, 0, 0, 38888, 0, 75031, 0, '');
INSERT INTO `creature_text` VALUES (71965, 8, 0, 'Prove yourselves worthy and I will let you pass.', 14, 0, 100, 0, 0, 38889, 0, 75032, 0, '');
INSERT INTO `creature_text` VALUES (71965, 9, 0, 'The light cleanses; but it is not gentle.  Gird yourselves and be purified!', 14, 0, 100, 0, 0, 38880, 0, 75034, 0, '');
INSERT INTO `creature_text` VALUES (71965, 10, 0, 'You have been judged, and proven yourselves worthy. But be warned, what lies beyond will try your souls to their utmost.  When you are fully prepared, you may enter the chamber.', 14, 0, 100, 0, 0, 38882, 0, 74958, 0, '');
INSERT INTO `creature_text` VALUES (71965, 11, 0, 'The corruption is amplifying.  The fragments must be purged before it becomes too great.', 14, 0, 100, 0, 0, 38903, 0, 74962, 0, '');
INSERT INTO `creature_text` VALUES (72779, 0, 0, '*Cough cough* Nyyyunnngggg...', 12, 0, 100, 0, 0, 38307, 0, 74997, 0, '');
INSERT INTO `creature_text` VALUES (72872, 6, 0, 'Taran Zhu!', 14, 0, 100, 0, 0, 38144, 0, 74993, 0, '');
INSERT INTO `creature_text` VALUES (72779, 1, 0, 'Cho... the outsiders... THEY did this... we should never have let them in...', 12, 0, 100, 0, 0, 38308, 0, 74998, 0, '');
INSERT INTO `creature_text` VALUES (72872, 7, 0, 'Don\'t speak, friend, I will take you to the healers.', 12, 0, 100, 0, 0, 38145, 0, 74994, 0, '');
INSERT INTO `creature_text` VALUES (72872, 8, 0, 'I will find help!  Try to hold off the Sha for as long as you can!', 14, 0, 100, 0, 0, 38146, 0, 74995, 0, '');
INSERT INTO `creature_text` VALUES (71965, 12, 0, 'It did not matter. It comes.  Steel your hearts and prepare your souls.', 14, 0, 100, 0, 0, 38904, 0, 74963, 0, '');
INSERT INTO `creature_text` VALUES (67833, 0, 0, 'The Mercurial Guardian comes to life!', 41, 0, 100, 0, 0, 0, 0, 69235, 0, '');
INSERT INTO `creature_text` VALUES (67833, 1, 0, 'The Mercurial Guardian\'s skin becomes as hard as silver!', 41, 0, 100, 0, 0, 0, 0, 69260, 0, '');
INSERT INTO `creature_text` VALUES (67834, 0, 0, 'If I am correct, $n, then this... Divine Bell... is exactly the type of weapon that Garrosh is looking for. Return to your other duties for now. I will bring this information to the Warchief myself.', 12, 0, 100, 0, 0, 0, 0, 69264, 0, '');
INSERT INTO `creature_text` VALUES (73647, 0, 0, 'Exotic, aromatic, a brew good for both body and spirit!', 12, 0, 100, 1, 0, 0, 0, 77055, 0, '');
INSERT INTO `creature_text` VALUES (73647, 0, 1, 'Brave the brew that\'s truly unique! Invigorating, satisfying... amber-brew!', 12, 0, 100, 25, 0, 0, 0, 77058, 0, '');
INSERT INTO `creature_text` VALUES (73647, 0, 2, 'Exquisite amber-brew from beyond the Serpent\'s Spine!', 12, 0, 100, 22, 0, 0, 0, 77057, 0, '');
INSERT INTO `creature_text` VALUES (73815, 0, 0, 'HOT AND HEARTY! Come try Yaungolian noodles!', 12, 0, 100, 5, 0, 0, 0, 77053, 0, '');
INSERT INTO `creature_text` VALUES (73815, 0, 1, 'Bold flavor, bold heat! No other noodles come close!', 12, 0, 100, 1, 0, 0, 0, 77054, 0, '');
INSERT INTO `creature_text` VALUES (73815, 0, 2, 'Why settle for weak, flavorless noodles? Try them yaungolian style!', 12, 0, 100, 6, 0, 0, 0, 77052, 0, '');
INSERT INTO `creature_text` VALUES (73816, 0, 0, 'You will find only the best noodles in all Pandaria here.', 12, 0, 100, 1, 0, 0, 0, 77037, 0, '');
INSERT INTO `creature_text` VALUES (73816, 0, 1, 'Please come enjoy my fine Golden Dragon Noodles.', 12, 0, 100, 2, 0, 0, 0, 77036, 0, '');
INSERT INTO `creature_text` VALUES (73816, 0, 2, 'Golden Dragon Noodles! The very BEST!', 12, 0, 100, 22, 0, 0, 0, 77039, 0, '');
INSERT INTO `creature_text` VALUES (73814, 0, 0, 'Monmon study MANY weeks with master noodle chef.', 12, 0, 100, 1, 0, 0, 0, 77034, 0, '');
INSERT INTO `creature_text` VALUES (73814, 0, 1, 'I got good noodles here!', 12, 0, 100, 3, 0, 0, 0, 77032, 0, '');
INSERT INTO `creature_text` VALUES (73814, 0, 2, 'BUY NOODLES!', 12, 0, 100, 4, 0, 0, 0, 77030, 0, '');
INSERT INTO `creature_text` VALUES (73656, 0, 0, 'Arms and armor repaired!', 12, 0, 100, 22, 0, 0, 0, 77026, 0, '');
INSERT INTO `creature_text` VALUES (73656, 0, 1, 'Don\'t be caught unprepared! Let Crafter Kwon see to your gear!', 12, 0, 100, 396, 0, 0, 0, 77028, 0, '');
INSERT INTO `creature_text` VALUES (73656, 0, 2, 'Blades sharpened! Armor mended!', 12, 0, 100, 22, 0, 0, 0, 77027, 0, '');
INSERT INTO `creature_text` VALUES (73622, 0, 0, 'Delicious food and drink at  reasonable prices!', 12, 0, 100, 22, 0, 0, 0, 77023, 0, '');
INSERT INTO `creature_text` VALUES (73622, 0, 1, 'Hungry? Thirsty? Tired? Let Graceful Swan take care of you!', 12, 0, 100, 1, 0, 0, 0, 77025, 0, '');
INSERT INTO `creature_text` VALUES (73622, 0, 2, 'Come rest your weary feet, adventurer!', 12, 0, 100, 3, 0, 0, 0, 77024, 0, '');
INSERT INTO `creature_text` VALUES (73618, 0, 0, 'You there! Come and try some Greenstone Brew!', 12, 0, 100, 25, 0, 0, 0, 77103, 0, '');
INSERT INTO `creature_text` VALUES (73618, 0, 1, 'Greenstone Brew! Straight from the heart of the Jade Forest.', 12, 0, 100, 1, 0, 0, 0, 77100, 0, '');
INSERT INTO `creature_text` VALUES (73618, 0, 2, 'Nothing beats the bold bite of Greenstone Brew!', 12, 0, 100, 4, 0, 0, 0, 77102, 0, '');
INSERT INTO `creature_text` VALUES (73651, 0, 0, 'It\'s Stormstout time!', 12, 0, 100, 22, 0, 0, 0, 77092, 0, '');
INSERT INTO `creature_text` VALUES (73651, 0, 1, 'Stormstout. Probably the best brew in Azeroth.', 12, 0, 100, 273, 0, 0, 0, 77099, 0, '');
INSERT INTO `creature_text` VALUES (73651, 0, 2, 'When you say Stormstout, you\'ve said it all.', 12, 0, 100, 1, 0, 0, 0, 77093, 0, '');
