INSERT INTO `creature_text` VALUES (73435, 0, 0, 'Dare ta drink a Dragon P.I.E.! It\'ll put hair on yer chest, and then hair on THAT!', 12, 0, 100, 4, 0, 0, 0, 77080, 0, '');
