INSERT INTO `creature_text` VALUES (73435, 0, 2, 'Ye won\'t find anythin\' like it elsewhere. Come and try!', 12, 0, 100, 1, 0, 0, 0, 77079, 0, '');
INSERT INTO `creature_text` VALUES (73435, 0, 3, 'The perfect mix of northern and southern styles!', 12, 0, 100, 5, 0, 0, 0, 77077, 0, '');
INSERT INTO `creature_text` VALUES (73657, 0, 0, 'Come, sit down, enjoy a meal!', 12, 0, 100, 70, 0, 0, 0, 77069, 0, '');
INSERT INTO `creature_text` VALUES (73657, 0, 1, 'Delicious dishes and fresh-made island delicacies!', 12, 0, 100, 22, 0, 0, 0, 77073, 0, '');
INSERT INTO `creature_text` VALUES (73657, 0, 2, 'For the finest food, look no further!', 12, 0, 100, 396, 0, 0, 0, 77068, 0, '');
INSERT INTO `creature_text` VALUES (73812, 0, 0, 'Steaming goat noodle: NUMBER ONE!', 12, 0, 100, 51, 0, 0, 0, 77060, 0, '');
INSERT INTO `creature_text` VALUES (73812, 0, 1, 'Come fill belly with noodle, yes! Very good!', 12, 0, 100, 1, 0, 0, 0, 77061, 0, '');
