INSERT INTO `creature_text` VALUES (71504, 0, 0, 'The sound of grinding metal reverberates through the halls as the lock on the Nexus doors releases.', 41, 0, 100, 0, 0, 0, 0, 76794, 2, '');
INSERT INTO `creature_text` VALUES (71954, 0, 0, 'Today, you will face many trainers, who have honed their skills and proven themselves.  Will your pets tell the same story about you?  We shall see!  Good luck!', 14, 0, 100, 0, 0, 38032, 0, 75311, 2, '');
INSERT INTO `creature_text` VALUES (71954, 1, 0, 'Can you stand on the tallest peak, winds and sleet buffeting your skin, until the trees wither and the mountains fall into the sea?', 14, 0, 100, 0, 0, 38757, 0, 75890, 2, '');
INSERT INTO `creature_text` VALUES (71954, 2, 0, 'Though you will be surrounded by foes greater than you can imagine, your fortitude shall allow you to endure. Remember this in the times ahead.', 14, 0, 100, 0, 0, 38756, 0, 75896, 2, '');
INSERT INTO `creature_text` VALUES (71954, 3, 0, 'You must persevere!', 14, 0, 100, 0, 0, 38758, 0, 75895, 2, '');
INSERT INTO `creature_text` VALUES (71954, 4, 0, 'The winds may be strong, and the sleet may sting.', 14, 0, 100, 0, 0, 38759, 0, 75892, 2, '');
INSERT INTO `creature_text` VALUES (71954, 5, 0, 'You are the mountain, unmovable by all but time.', 14, 0, 100, 0, 0, 38760, 0, 75893, 2, '');
INSERT INTO `creature_text` VALUES (71954, 6, 0, 'Be vigilent in your stand, or you will never achieve your goals.', 14, 0, 100, 0, 0, 38761, 0, 75894, 2, '');
INSERT INTO `creature_text` VALUES (71952, 0, 0, 'Then let us begin.', 14, 0, 100, 0, 0, 38024, 0, 75898, 2, '');
INSERT INTO `creature_text` VALUES (71952, 1, 0, 'When faced with challenges the like you have never seen, what do you hope for? What is the future you seek?', 14, 0, 100, 0, 0, 38026, 0, 75897, 2, '');
INSERT INTO `creature_text` VALUES (71952, 2, 0, 'Your hope shines brightly, and even more brightly when you work together to overcome. It will ever light your way in even the darkest of places.', 14, 0, 100, 0, 0, 38025, 0, 75903, 2, '');
INSERT INTO `creature_text` VALUES (71952, 3, 0, 'Do not give up on yourself!', 14, 0, 100, 0, 0, 38027, 0, 75902, 2, '');
INSERT INTO `creature_text` VALUES (71952, 4, 0, 'Believe in one another, and let others carry hope for you!', 14, 0, 100, 0, 0, 38028, 0, 75899, 2, '');
INSERT INTO `creature_text` VALUES (71952, 5, 0, 'Without hope, there is no brightness in the future!', 14, 0, 100, 0, 0, 38029, 0, 75900, 2, '');
INSERT INTO `creature_text` VALUES (71952, 6, 0, 'Create the destiny you seek!', 14, 0, 100, 0, 0, 38030, 0, 75901, 2, '');
INSERT INTO `creature_text` VALUES (71955, 0, 0, 'The trial begins!', 14, 0, 100, 0, 0, 38222, 0, 75905, 2, '');
INSERT INTO `creature_text` VALUES (71955, 1, 0, 'The lesson of true wisdom lies within you dear heroes, and in the choices you make. When faced with overwhelming darkness, will you make the right decisions?', 14, 0, 100, 0, 0, 38224, 0, 75904, 2, '');
INSERT INTO `creature_text` VALUES (71955, 2, 0, 'Your wisdom has seen you through this trial. May it ever light your way out of dark places.', 14, 0, 100, 0, 0, 38223, 0, 75910, 2, '');
INSERT INTO `creature_text` VALUES (71955, 3, 0, 'Learn from your mistakes!', 14, 0, 100, 0, 0, 38225, 0, 75909, 2, '');
INSERT INTO `creature_text` VALUES (71955, 4, 0, 'Listen to your inner voice, and seek out the truth!', 14, 0, 100, 0, 0, 38226, 0, 75906, 2, '');
INSERT INTO `creature_text` VALUES (71955, 5, 0, 'Do not let your judgement be clouded in trying times!', 14, 0, 100, 0, 0, 38227, 0, 75907, 2, '');
INSERT INTO `creature_text` VALUES (71955, 6, 0, 'Always consider the consequences of your actions!', 14, 0, 100, 0, 0, 38228, 0, 75908, 2, '');
INSERT INTO `creature_text` VALUES (71953, 0, 0, 'Ha ha! The trial commences!', 14, 0, 100, 0, 0, 38211, 0, 75912, 2, '');
INSERT INTO `creature_text` VALUES (71953, 1, 0, 'Strength. It is far more than simple physical prowess. When you are truly tested, will you be able to tell the difference between strength and power?', 14, 0, 100, 0, 0, 38213, 0, 75911, 2, '');
INSERT INTO `creature_text` VALUES (71953, 2, 0, 'You are strong, stronger even than you realize. Carry this thought with you into the darkness ahead, and let it shield you.', 14, 0, 100, 0, 0, 38212, 0, 75917, 2, '');
INSERT INTO `creature_text` VALUES (71953, 3, 0, 'Return twice as powerful!', 14, 0, 100, 0, 0, 38214, 0, 75916, 2, '');
INSERT INTO `creature_text` VALUES (71953, 4, 0, 'Believe in your strength!', 14, 0, 100, 0, 0, 38215, 0, 65757, 2, '');
INSERT INTO `creature_text` VALUES (71953, 5, 0, 'You have the power to change your destiny!', 14, 0, 100, 0, 0, 38216, 0, 75914, 2, '');
INSERT INTO `creature_text` VALUES (71953, 6, 0, 'Do not mistake the power that darkness offers for true strength!', 14, 0, 100, 0, 0, 38217, 0, 75915, 2, '');
INSERT INTO `creature_text` VALUES (72438, 0, 0, 'Now is my chance!', 12, 0, 100, 0, 0, 0, 0, 76988, 0, '');
INSERT INTO `creature_text` VALUES (72455, 0, 0, 'Have anything to say for yourself, Pandaren?', 14, 0, 100, 1, 0, 39051, 0, 74921, 0, '');
INSERT INTO `creature_text` VALUES (72455, 1, 0, 'Do you see this burning candle? You have until it burns out to tell us about the Pandaren artifacts! Now start talking!', 14, 0, 100, 397, 0, 39052, 0, 74922, 0, '');
INSERT INTO `creature_text` VALUES (72438, 1, 0, 'Oh no, Ji! They are going to execute him!', 12, 0, 100, 0, 0, 38556, 0, 74925, 0, '');
INSERT INTO `creature_text` VALUES (72438, 2, 0, 'They have practically beaten him unconscious! I have to do something!', 12, 0, 100, 0, 0, 38557, 0, 74926, 0, '');
INSERT INTO `creature_text` VALUES (72438, 3, 0, 'Ji, I am so sorry, we can be together now, please, let us just go back to the turtle, please do not die.', 12, 0, 100, 0, 0, 38560, 0, 74929, 0, '');
INSERT INTO `creature_text` VALUES (72438, 4, 0, 'I am getting him out of here.', 12, 0, 100, 0, 0, 38561, 0, 74930, 0, '');
INSERT INTO `creature_text` VALUES (72492, 0, 0, 'We didn\'t want to do this!', 12, 0, 100, 5, 0, 39044, 0, 74916, 0, '');
INSERT INTO `creature_text` VALUES (72492, 1, 0, 'I owe you my life!', 12, 0, 100, 1, 0, 39045, 0, 66956, 0, '');
INSERT INTO `creature_text` VALUES (72432, 0, 0, 'The Warchief ignored my council. I discovered he was building a compound beneath the caverns of Ragefire.', 12, 0, 100, 0, 0, 38564, 0, 74822, 0, '');
INSERT INTO `creature_text` VALUES (72432, 1, 0, 'Thrall and Saurfang have already gone down below.', 12, 0, 100, 0, 0, 38565, 0, 74823, 0, '');
INSERT INTO `creature_text` VALUES (72432, 2, 0, 'Go with caution.  Hellscream has been assembling an arsenal down there - goblin mercenaries, pandaren artifacts, and some sort of darkness I can feel in my very soul.', 12, 0, 100, 0, 0, 38567, 0, 74824, 0, '');
