INSERT INTO `creature_text` VALUES (72248, 0, 0, 'Fools! These docks are under Dragonmaw control! Your ships will burn, and you with them!', 14, 0, 100, 0, 0, 38400, 0, 75190, 0, '');
INSERT INTO `creature_text` VALUES (72248, 1, 0, 'Reinforcements to the docks!  Lok\'tar Ogar!', 14, 0, 100, 0, 0, 38401, 0, 75191, 0, '');
INSERT INTO `creature_text` VALUES (72248, 2, 0, 'You think you have won? We are orcs! Pure of blood, commanded by the greatest Warchief who ever lived! Your blood will fill this bay!', 14, 0, 100, 0, 0, 38402, 0, 75192, 0, '');
INSERT INTO `creature_text` VALUES (72311, 0, 0, 'Well done! Landing parties, form up! Footmen to the front!', 12, 0, 100, 25, 0, 38953, 0, 75223, 0, '');
INSERT INTO `creature_text` VALUES (72302, 0, 0, 'The Dragonmaw are supporting the Warchief.', 12, 0, 100, 1, 0, 38097, 0, 75205, 0, '');
INSERT INTO `creature_text` VALUES (72311, 1, 0, 'Yes. My scouts learned all about this one.\"Warlord Zaela.\" Bring her proto-drake down and the rest of the Dragonmaw will crumble.', 12, 0, 100, 1, 0, 38954, 0, 75224, 0, '');
INSERT INTO `creature_text` VALUES (72302, 1, 0, 'We\'re going to need some serious firepower.', 12, 0, 100, 1, 0, 38098, 0, 75206, 0, '');
INSERT INTO `creature_text` VALUES (72311, 2, 0, 'Commandeer those towers! Turn the anti-air batteries around and blast her from the sky!', 12, 0, 100, 25, 0, 38955, 0, 75225, 0, '');
INSERT INTO `creature_text` VALUES (72311, 3, 0, 'Some of you will need to stay behind to hold the beachhead.', 12, 0, 100, 1, 0, 38170, 0, 75248, 0, '');
INSERT INTO `creature_text` VALUES (72248, 3, 0, 'Dragonmaw clan, retake the docks and push them into the sea!  In the name of Hellscream and the True Horde!', 14, 0, 100, 25, 0, 38391, 0, 75193, 0, '');
INSERT INTO `creature_text` VALUES (73551, 0, 0, 'Wait for me!', 12, 0, 100, 4, 0, 0, 0, 76171, 0, '');
INSERT INTO `creature_text` VALUES (72302, 2, 0, 'Here they come!', 14, 0, 100, 1, 0, 38099, 0, 1250, 0, '');
INSERT INTO `creature_text` VALUES (73550, 0, 0, 'Not the red wire, the blue one!', 12, 0, 100, 0, 0, 0, 0, 76170, 0, '');
INSERT INTO `creature_text` VALUES (73550, 1, 0, 'Steady hands..', 12, 0, 100, 0, 0, 0, 0, 76167, 0, '');
INSERT INTO `creature_text` VALUES (73551, 1, 0, 'Hurry up!', 12, 0, 100, 0, 0, 0, 0, 76169, 0, '');
INSERT INTO `creature_text` VALUES (72248, 4, 0, 'Dragonmaw, advance!', 14, 0, 100, 0, 0, 38394, 0, 75195, 0, '');
INSERT INTO `creature_text` VALUES (73550, 2, 0, 'Blue wire with the green stripe, or black wire with the yellow stripe? I can never remember.', 12, 0, 100, 0, 0, 0, 0, 76168, 0, '');
INSERT INTO `creature_text` VALUES (72302, 3, 0, 'I\'ve conjured a Blizzard, lead them into it!', 14, 0, 100, 0, 0, 38100, 0, 75216, 0, '');
INSERT INTO `creature_text` VALUES (72248, 5, 0, 'For Hellscream!', 14, 0, 100, 5, 0, 38395, 0, 41589, 0, '');
INSERT INTO `creature_text` VALUES (72248, 6, 0, 'The door barring the South Tower has been breached!', 41, 0, 100, 0, 0, 0, 0, 75327, 2, '');
INSERT INTO `creature_text` VALUES (72248, 7, 0, 'Warlord Zaela orders a |cFFFF0404|hKor\'kron Demolisher|h|r to assault the tower!', 41, 0, 100, 0, 0, 0, 0, 76900, 2, '');
INSERT INTO `creature_text` VALUES (72248, 8, 0, 'They\'ve breached the tower, bring it down!', 14, 0, 100, 0, 0, 38397, 0, 75198, 0, '');
INSERT INTO `creature_text` VALUES (72248, 9, 0, 'Turn the siege weapons to attack that tower, and bury them alive!', 14, 0, 100, 0, 0, 38399, 0, 75200, 0, '');
INSERT INTO `creature_text` VALUES (72357, 0, 0, 'You dare try to take my tower?  Hahaha...', 14, 0, 100, 574, 0, 38737, 0, 75279, 0, '');
INSERT INTO `creature_text` VALUES (72357, 1, 0, 'You... cannot win... this war...', 14, 0, 100, 0, 0, 38738, 0, 75281, 0, '');
INSERT INTO `creature_text` VALUES (72302, 4, 0, 'She\'s turning her siege weapons on her own towers!', 14, 0, 100, 0, 0, 38085, 0, 75211, 0, '');
INSERT INTO `creature_text` VALUES (72248, 10, 0, 'Players capture control of the South Tower Anti-Air Turret!', 41, 0, 100, 0, 0, 0, 0, 76735, 2, '');
INSERT INTO `creature_text` VALUES (72302, 5, 0, 'One down, one to go!', 14, 0, 100, 0, 0, 38086, 0, 75212, 0, '');
INSERT INTO `creature_text` VALUES (72248, 11, 0, 'The door barring the North Tower has been breached!', 41, 0, 100, 0, 0, 0, 0, 75329, 2, '');
INSERT INTO `creature_text` VALUES (72356, 0, 0, 'Light the fuses! They will not leave this tower alive!', 14, 0, 100, 5, 0, 38740, 0, 75276, 0, '');
INSERT INTO `creature_text` VALUES (72356, 1, 0, 'I die... with honor... Lok\'tar Ogar...', 14, 0, 100, 0, 0, 38193, 0, 75926, 0, '');
INSERT INTO `creature_text` VALUES (72248, 12, 0, 'Players capture control of the North Tower Anti-Air Turret!', 41, 0, 100, 0, 0, 0, 0, 75433, 2, '');
INSERT INTO `creature_text` VALUES (72311, 4, 0, 'That\'s both of them!  Fire the cannons!', 14, 0, 100, 22, 0, 38944, 0, 75231, 0, '');
INSERT INTO `creature_text` VALUES (72248, 13, 0, 'Galakras is hit! Nice shot!', 41, 0, 100, 0, 0, 0, 0, 76070, 2, '');
INSERT INTO `creature_text` VALUES (72248, 14, 0, '%s is blasted off of her mount!', 41, 0, 100, 0, 0, 0, 0, 75343, 2, '');
INSERT INTO `creature_text` VALUES (72248, 15, 0, 'Ahhhhhhh!', 14, 0, 100, 0, 0, 38392, 0, 59901, 0, '');
INSERT INTO `creature_text` VALUES (72302, 6, 0, 'We\'ve done it!', 12, 0, 100, 0, 0, 38088, 0, 75214, 0, '');
INSERT INTO `creature_text` VALUES (72311, 5, 0, 'Ha!  The bigger they are...', 12, 0, 100, 153, 0, 38946, 0, 75233, 0, '');
INSERT INTO `creature_text` VALUES (72302, 7, 0, 'We\'ll hold the docks for the remaining landing parties. Follow the path to the gates of Orgrimmar, and make contact with Vol\'jin and his revolutionaries.', 12, 0, 100, 0, 0, 38089, 0, 75215, 0, '');
INSERT INTO `creature_text` VALUES (72311, 6, 0, 'We will see if he has managed to keep up his end of the bargain.', 12, 0, 100, 1, 0, 38947, 0, 75234, 0, '');
INSERT INTO `creature_text` VALUES (73859, 0, 0, 'Send in da reserves! Charge!', 14, 0, 100, 0, 0, 0, 0, 76899, 0, '');
INSERT INTO `creature_text` VALUES (73941, 0, 0, 'Our casualties are too heavy!', 14, 0, 100, 5, 0, 38262, 0, 76623, 0, '');
INSERT INTO `creature_text` VALUES (73939, 0, 0, 'Don\'t be foolish, Vol\'jin! You have no siege weapons left! You cannot win this battle!', 14, 0, 100, 5, 0, 38205, 0, 75920, 0, '');
INSERT INTO `creature_text` VALUES (73942, 0, 0, 'Ain\'t no other way, mon. We gotta stop Garrosh here an\' now, else we be runnin\' the rest of our lives.', 12, 0, 100, 1, 0, 39151, 0, 76610, 0, '');
INSERT INTO `creature_text` VALUES (73941, 1, 0, 'Perhaps our newfound reinforcements will aid us.', 12, 0, 100, 1, 0, 38263, 0, 76624, 0, '');
INSERT INTO `creature_text` VALUES (73941, 2, 0, 'Perhaps there is hope after all.', 12, 0, 100, 1, 0, 38264, 0, 76625, 0, '');
INSERT INTO `creature_text` VALUES (73942, 1, 0, 'Dey be heroes, no doubt about dat.', 14, 0, 100, 1, 0, 39144, 0, 76612, 0, '');
INSERT INTO `creature_text` VALUES (73941, 3, 0, 'Blessings of the Earth Mother upon you.', 14, 1, 100, 0, 0, 0, 0, 6724, 0, '');
INSERT INTO `creature_text` VALUES (73942, 2, 0, 'Da beast be slain!', 14, 0, 100, 5, 0, 39145, 0, 76613, 0, '');
INSERT INTO `creature_text` VALUES (73939, 1, 0, 'You may have defeated my war machine, but the gate still stands. You will not take Orgrimmar today.', 14, 0, 100, 274, 0, 38206, 0, 75921, 0, '');
INSERT INTO `creature_text` VALUES (73962, 0, 0, 'Anu\'dora!', 14, 0, 100, 5, 0, 38309, 0, 76620, 0, '');
INSERT INTO `creature_text` VALUES (73939, 2, 0, 'What? Kor\'kron, to the gates! Now!', 14, 0, 100, 397, 0, 38207, 0, 75922, 0, '');
INSERT INTO `creature_text` VALUES (73962, 1, 0, 'At them, my sisters! For Kalimdor!', 14, 0, 100, 5, 0, 38310, 0, 76621, 0, '');
INSERT INTO `creature_text` VALUES (73942, 3, 0, 'Tyrande...', 12, 0, 100, 1, 0, 39153, 0, 76614, 0, '');
INSERT INTO `creature_text` VALUES (73962, 2, 0, 'Don\'t think we\'re here to save you, troll. We\'re here for Kalimdor.  Get forces inside the gates while we distract the enemy!', 14, 0, 100, 1, 0, 38311, 0, 76622, 0, '');
INSERT INTO `creature_text` VALUES (72191, 0, 0, 'Our enemies have finally arrived!  Demolishers, fire at will!', 14, 0, 100, 0, 0, 38287, 0, 74795, 0, '');
INSERT INTO `creature_text` VALUES (72145, 0, 0, 'I will NOT burn like the rest! Face the demons you fear, traitors!', 14, 0, 100, 0, 0, 0, 0, 74817, 0, '');
INSERT INTO `creature_text` VALUES (71515, 0, 0, 'No alliance hand has ever struck me down. Do you think you are seasoned enough?', 14, 0, 100, 0, 0, 38204, 0, 75923, 0, '');
INSERT INTO `creature_text` VALUES (71515, 1, 0, 'I am Nazgrim, fist of the Warchief and General of the Horde Army. If I must die today, I will die with honor, in battle, for the horde!', 14, 0, 100, 0, 0, 38191, 0, 75924, 0, '');
INSERT INTO `creature_text` VALUES (71515, 2, 0, '|TInterface\\Icons\\ability_warrior_offensivestance.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143589|h[Battle Stance]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74348, 0, '');
INSERT INTO `creature_text` VALUES (71515, 3, 0, 'All Kor\'kron... under my command... kill them... NOW!', 14, 0, 100, 0, 0, 38203, 0, 74510, 0, '');
INSERT INTO `creature_text` VALUES (71515, 4, 0, '|TInterface\\Icons\\ability_racial_avatar.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143594|h[Berserker Stance]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74350, 0, '');
INSERT INTO `creature_text` VALUES (71515, 5, 0, 'I die... with honor... Lok\'tar Ogar...', 12, 0, 100, 0, 0, 38193, 0, 75926, 0, '');
INSERT INTO `creature_text` VALUES (71515, 6, 0, 'So, it has come to this. Together, we have learned and grown over the years, and now we find ourselves face to face on the battlefield. Do not think I will go easy on you, nor do I expect any quarter. What we do now, we do for the horde, both of us.', 14, 0, 100, 0, 0, 38208, 0, 75931, 0, '');
INSERT INTO `creature_text` VALUES (71515, 7, 0, 'It is an honor to finally face you in combat. Lok\'tar Ogar, for Honor, and for the Horde!', 14, 0, 100, 0, 0, 38192, 0, 75932, 0, '');
INSERT INTO `creature_text` VALUES (71515, 8, 0, 'Ah... you have learned much... and learned well... an honorable battle.', 12, 0, 100, 0, 0, 38196, 0, 75928, 0, '');
INSERT INTO `creature_text` VALUES (71515, 9, 0, 'Kor\'kron, at my side!', 14, 0, 100, 0, 0, 38198, 0, 74505, 0, '');
INSERT INTO `creature_text` VALUES (71515, 9, 1, 'Next squad, to the front!', 14, 0, 100, 0, 0, 38200, 0, 74507, 0, '');
INSERT INTO `creature_text` VALUES (71515, 10, 0, '|TInterface\\Icons\\ability_warrior_defensivestance.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143593|h[Defensive Stance]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74349, 0, '');
INSERT INTO `creature_text` VALUES (71967, 0, 0, 'You have been judged, and proven yourselves worthy. But be warned, what lies beyond will try your souls to their utmost.  When you are fully prepared, you may enter the chamber.', 14, 0, 100, 0, 0, 38882, 0, 74958, 0, '');
INSERT INTO `creature_text` VALUES (71967, 1, 0, 'The corruption is amplifying.  The fragments must be purged before it becomes too great.', 14, 0, 100, 0, 0, 38903, 0, 74962, 0, '');
INSERT INTO `creature_text` VALUES (71967, 2, 0, 'It did not matter. It comes.  Steel your hearts and prepare your souls.', 14, 0, 100, 0, 0, 38904, 0, 74963, 0, '');
INSERT INTO `creature_text` VALUES (71734, 0, 0, 'Come, face me. Give in to your pride. Show me your \"Greatness.\"', 14, 0, 100, 0, 0, 38899, 0, 75737, 0, '');
INSERT INTO `creature_text` VALUES (71734, 1, 0, 'So foolish...', 14, 0, 100, 0, 0, 38896, 0, 75738, 0, '');
INSERT INTO `creature_text` VALUES (71734, 2, 0, 'You\'ve let your pride cloud your vision, Titan puppet! You can never contain ME!', 14, 0, 100, 0, 0, 38898, 0, 75744, 0, '');
INSERT INTO `creature_text` VALUES (71967, 3, 0, 'You... must... contain... it...', 14, 0, 100, 0, 0, 38902, 0, 74964, 0, '');
INSERT INTO `creature_text` VALUES (73598, 0, 0, 'So Hellscream\'s arrogance unleashed the last of the sha. I am not surprised.', 12, 0, 100, 0, 0, 38090, 0, 77045, 0, '');
INSERT INTO `creature_text` VALUES (73605, 0, 0, 'Look here! He left his weapon behind.', 14, 0, 100, 0, 0, 38158, 0, 77040, 0, '');
INSERT INTO `creature_text` VALUES (73598, 1, 0, 'Gorehowl.', 12, 0, 100, 0, 0, 38091, 0, 76297, 0, '');
INSERT INTO `creature_text` VALUES (73605, 1, 0, 'This means he\'s completely unhinged.', 12, 0, 100, 0, 0, 38159, 0, 77041, 0, '');
INSERT INTO `creature_text` VALUES (73598, 2, 0, 'News to no one, Regent Lord. King Wrynn\'s fleet is converging on Orgrimmar as we speak.', 12, 0, 100, 0, 0, 38092, 0, 77047, 0, '');
INSERT INTO `creature_text` VALUES (73605, 2, 0, 'Likewise. Sylvanas and I have both sent ships to support Vol\'jin\'s revolution.', 12, 0, 100, 0, 0, 38160, 0, 77042, 0, '');
INSERT INTO `creature_text` VALUES (73598, 3, 0, 'I\'m warning you, Lor\'themar. The Alliance is besieging the city and we will destroy Hellscream. Your people had best stay out of our way.', 12, 0, 100, 0, 0, 38093, 0, 77048, 0, '');
INSERT INTO `creature_text` VALUES (73605, 3, 0, 'It is always a pleasure to see you, Lady Proudmoore.', 12, 0, 100, 0, 0, 38161, 0, 77043, 0, '');
INSERT INTO `creature_text` VALUES (73598, 4, 0, 'Come heroes, through the portal! The siege of Orgrimmar begins!', 12, 0, 100, 0, 0, 38094, 0, 77049, 0, '');
INSERT INTO `creature_text` VALUES (64072, 0, 0, 'How\'s about another round?', 12, 0, 100, 1, 0, 0, 0, 76713, 0, '');
INSERT INTO `creature_text` VALUES (64072, 0, 1, 'Well played, sir.', 12, 0, 100, 1, 0, 0, 0, 76712, 0, '');
INSERT INTO `creature_text` VALUES (63013, 0, 0, 'I will give you a free meal if you can get this dwarf off of my new chair!', 12, 0, 100, 1, 0, 0, 0, 66752, 0, '');
INSERT INTO `creature_text` VALUES (64164, 0, 0, 'Fear not if your weapon leaves your grasp. Your body is an ample tool against any foe.', 12, 0, 100, 1, 0, 0, 0, 66774, 0, '');
INSERT INTO `creature_text` VALUES (64149, 0, 0, 'I have the softest featherbeds you can find for a thousand miles. Would you like to try one?', 12, 0, 100, 1, 0, 0, 0, 64897, 0, '');
INSERT INTO `creature_text` VALUES (64115, 0, 0, 'Hrmph. Nice card.', 12, 0, 100, 1, 0, 0, 0, 76716, 0, '');
INSERT INTO `creature_text` VALUES (64115, 0, 1, 'Alright, alright, you win.', 12, 0, 100, 1, 0, 0, 0, 76724, 0, '');
INSERT INTO `creature_text` VALUES (64115, 0, 2, 'Best two out of three!', 12, 0, 100, 1, 0, 0, 0, 76719, 0, '');
INSERT INTO `creature_text` VALUES (64071, 0, 0, 'Please, partake in the buffet laid out by the Golden Lotus as a thanks to your people for their aid in protecting the Vale of Eternal Blossoms.', 12, 0, 100, 1, 0, 0, 0, 66877, 0, '');
INSERT INTO `creature_text` VALUES (72561, 1, 0, 'My arrows alone cannot bring that monstrosity down.', 12, 0, 100, 1, 0, 38302, 0, 75264, 0, '');
INSERT INTO `creature_text` VALUES (72560, 1, 0, 'Strategy, Dark Lady. We should repurpose the shore defenses.', 12, 0, 100, 1, 0, 38168, 0, 75246, 0, '');
INSERT INTO `creature_text` VALUES (72560, 2, 0, 'Heroes, take the towers, turn the guns onto that proto-drake, and blast it from the sky!', 12, 0, 100, 5, 0, 38169, 0, 75247, 0, '');
INSERT INTO `creature_text` VALUES (72560, 3, 0, 'Some of you should stay behind to hold the landing zone.', 12, 0, 100, 1, 0, 38956, 0, 75226, 0, '');
INSERT INTO `creature_text` VALUES (72561, 2, 0, 'Bring her down quick so I can wrap my fingers around her neck.', 14, 0, 100, 1, 0, 38303, 0, 75265, 0, '');
INSERT INTO `creature_text` VALUES (72560, 4, 0, 'She seems to be assaulting her own towers!', 14, 0, 100, 5, 0, 38152, 0, 75252, 0, '');
INSERT INTO `creature_text` VALUES (72560, 5, 0, 'We are halfway there!', 14, 0, 100, 5, 0, 38153, 0, 75253, 0, '');
INSERT INTO `creature_text` VALUES (73909, 0, 0, 'I\'m summoning a Flamestrike, lead them into it!', 14, 0, 100, 0, 0, 0, 0, 75256, 0, '');
INSERT INTO `creature_text` VALUES (72561, 3, 0, 'And there goes the other one.', 14, 0, 100, 0, 0, 38293, 0, 77005, 0, '');
INSERT INTO `creature_text` VALUES (72560, 6, 0, 'Sometimes I impress even myself. And down goes the dragon!', 14, 0, 100, 6, 0, 38154, 0, 77004, 0, '');
INSERT INTO `creature_text` VALUES (72561, 4, 0, 'You do realize we are directly below it, right?', 14, 0, 100, 0, 0, 38294, 0, 75270, 0, '');
INSERT INTO `creature_text` VALUES (72560, 7, 0, 'Finally!  I will never get all of this dust out of my robes.', 14, 0, 100, 5, 0, 38155, 0, 75254, 0, '');
INSERT INTO `creature_text` VALUES (72561, 5, 0, 'Oh, you\'re still here?  I had kind of hoped that you had perished.  You would make a very attractive corpse.', 14, 0, 100, 0, 0, 38295, 0, 75271, 0, '');
INSERT INTO `creature_text` VALUES (72560, 8, 0, 'I will take that as a compliment.  We\'ll hold the docks for now and marshall the forces. Vol\'jin should await you at the gates of Orgrimmar.', 14, 0, 100, 1, 0, 38156, 0, 75255, 0, '');
INSERT INTO `creature_text` VALUES (72561, 6, 0, 'If he\'s not dead.', 14, 0, 100, 0, 0, 38296, 0, 75272, 0, '');
INSERT INTO `creature_text` VALUES (71889, 0, 0, 'Activating GB-11010 \"Armageddon\"-class defense systems. Hey, does anybody know if we\'re zoned for this?', 14, 0, 100, 0, 0, 38482, 0, 75754, 0, '');
INSERT INTO `creature_text` VALUES (71889, 1, 0, '\"Armageddon\"-class defense systems fully on-line. Here comes the explosions!', 14, 0, 100, 0, 0, 38484, 0, 75766, 0, '');
INSERT INTO `creature_text` VALUES (71889, 2, 0, 'Module 1\'s all prepared for system reset.', 14, 0, 100, 0, 0, 38474, 0, 75757, 0, '');
INSERT INTO `creature_text` VALUES (71889, 3, 0, 'Module 2\'s all prepared for system reset.', 14, 0, 100, 0, 0, 38476, 0, 75759, 0, '');
INSERT INTO `creature_text` VALUES (71889, 4, 0, 'Security system deactivated.  Welcome back Siegecrafter Blackfuse.', 14, 0, 100, 0, 0, 38478, 0, 75761, 0, '');
INSERT INTO `creature_text` VALUES (71454, 8, 0, '|TInterface\\Icons\\spell_shadow_shadesofdarkness.blp:20|t%s begins to cast |cFFF00000|Hspell:142842|h[Breath of Y\'Shaarj]|h|r!', 41, 0, 100, 0, 0, 0, 0, 75033, 0, '');
INSERT INTO `creature_text` VALUES (71454, 9, 0, '|TInterface\\Icons\\racial_orc_berserkerstrength.blp:20|t%s enters a |cFFF00000|Hspell:142879|h[Blood Rage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 75186, 0, '');
INSERT INTO `creature_text` VALUES (71454, 10, 0, '|TInterface\\Icons\\racial_orc_berserkerstrength.blp:20|t%s\'s |cFFF00000|Hspell:142879|h[Blood Rage]|h|r subsides!', 41, 0, 100, 0, 0, 0, 0, 75189, 0, '');
INSERT INTO `creature_text` VALUES (71466, 0, 0, '%s shifts into Siege Mode!', 41, 0, 100, 0, 0, 0, 0, 74585, 2, '');
INSERT INTO `creature_text` VALUES (71466, 1, 0, '%s shifts back into Assault Mode!', 41, 0, 100, 0, 0, 0, 0, 74584, 2, '');
INSERT INTO `creature_text` VALUES (72249, 0, 0, 'Galakras staggers, but recovers from the blast!', 41, 0, 100, 0, 0, 0, 0, 75342, 2, '');
INSERT INTO `creature_text` VALUES (72249, 1, 0, 'Galakras is hit! Nice shot!', 41, 0, 100, 0, 0, 0, 0, 76070, 2, '');
INSERT INTO `creature_text` VALUES (63217, 0, 0, 'This stone tells of a great hozen warlord who lived here twenty-five hundred years ago. What are your leaders like? Poets? Warriors? I should like to meet them someday.', 12, 0, 100, 6, 0, 30072, 0, 62703, 0, '');
INSERT INTO `creature_text` VALUES (63217, 1, 0, 'Prepare yourself! Saurok raiders approach!', 12, 0, 100, 430, 0, 30073, 0, 62705, 0, '');
INSERT INTO `creature_text` VALUES (63217, 2, 0, 'The saurok have plagued this land since the time of the mogu. They are impossible to negotiate with. But we never stop trying! We fight them only as a last resort.', 12, 0, 100, 396, 0, 30074, 0, 62712, 0, '');
INSERT INTO `creature_text` VALUES (63217, 3, 0, 'Thank you for your help - these notes are important to my studies. Your people value knowledge too, yes? I assume so, only because you arrived on a great airship.', 12, 0, 100, 396, 0, 30075, 0, 62716, 0, '');
INSERT INTO `creature_text` VALUES (72151, 0, 0, 'Almost... got... it...', 12, 0, 100, 0, 0, 0, 0, 74603, 0, '');
INSERT INTO `creature_text` VALUES (72151, 1, 0, 'Well that was unexpected!', 12, 0, 100, 0, 0, 0, 0, 74604, 0, '');
INSERT INTO `creature_text` VALUES (71869, 0, 0, 'Good deal. Welcome to the spirit world. Kinda. Let\'s check out the chests for any goodies, and see if you can find my body somewhere!', 12, 0, 100, 0, 0, 0, 0, 74489, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 0, 'I thought about askin\' her out but I didn\'t have the guts.', 12, 0, 100, 0, 0, 0, 0, 74486, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 1, 'What kind of spirit haunts a chicken coop? A poultry geist!', 12, 0, 100, 0, 0, 0, 0, 74467, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 2, 'I\'m not THAT snarky, am I?', 12, 0, 100, 0, 0, 0, 0, 74464, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 3, 'Want to hear somethin\' funny? Coccyx.', 12, 0, 100, 0, 0, 0, 0, 74476, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 4, 'Sorry for ribbin\' ya so much.', 12, 0, 100, 0, 0, 0, 0, 74473, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 5, 'Sticks and stones may break my bones...', 12, 0, 100, 0, 0, 0, 0, 74485, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 6, 'Who just called me a numbskull?', 12, 0, 100, 0, 0, 0, 0, 74471, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 7, 'I love you wit\' every bone in my body!', 12, 0, 100, 0, 0, 0, 0, 74482, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 8, 'I\'m not sure I have the stomach for this...', 12, 0, 100, 0, 0, 0, 0, 74487, 0, '');
INSERT INTO `creature_text` VALUES (71869, 1, 9, 'I know my jokes ain\'t so good... I lost my funny bone!', 12, 0, 100, 0, 0, 0, 0, 74468, 0, '');
INSERT INTO `creature_text` VALUES (73547, 0, 0, 'Hahahahaha!', 14, 0, 100, 0, 0, 0, 0, 55333, 0, '');
INSERT INTO `creature_text` VALUES (73547, 1, 0, 'I live again!', 14, 0, 100, 0, 0, 0, 0, 76173, 0, '');
INSERT INTO `creature_text` VALUES (73547, 2, 0, 'The South Seas are mine to pillage for all eternity!', 14, 0, 100, 0, 0, 0, 0, 76175, 0, '');
INSERT INTO `creature_text` VALUES (73547, 3, 0, 'Come, Vazuvius, rise from the depths!', 14, 0, 100, 0, 0, 0, 0, 76176, 0, '');
INSERT INTO `creature_text` VALUES (73547, 4, 0, 'Kill them all! No survivors!', 14, 0, 100, 0, 0, 0, 0, 76388, 0, '');
INSERT INTO `creature_text` VALUES (73666, 0, 0, 'Your challenge has been answered, outsider. Ordon, claim this area in the name of the Burning God!', 14, 0, 100, 0, 0, 0, 0, 76582, 0, '');
INSERT INTO `creature_text` VALUES (73547, 5, 0, 'This cannot be! The Vazuvius could never fall to the likes of you!', 14, 0, 100, 0, 0, 0, 0, 76778, 0, '');
INSERT INTO `creature_text` VALUES (71876, 0, 0, 'Hey, meat sack! If you got five minutes, come talk to me! I got no body!', 12, 0, 100, 0, 0, 0, 0, 74488, 0, '');
INSERT INTO `creature_text` VALUES (72970, 0, 0, 'The eons have awakened me.', 14, 0, 100, 0, 0, 0, 0, 75695, 0, '');
