INSERT INTO `creature_text` VALUES (71889, 5, 0, 'Hey, we recording?  Yeah?  Okay.  Goblin-Titan control module starting up, please stand back.', 14, 0, 100, 0, 0, 38479, 0, 75750, 0, '');
INSERT INTO `creature_text` VALUES (71889, 6, 0, 'Checking out the datas, hold on a second will ya.', 14, 0, 100, 0, 0, 38480, 0, 75751, 0, '');
INSERT INTO `creature_text` VALUES (71889, 7, 0, 'Oh, now you did it, that was really really really not the correct input code.', 14, 0, 100, 0, 0, 38481, 0, 75752, 0, '');
INSERT INTO `creature_text` VALUES (71658, 0, 0, 'Intruders! I will lock you in a cell with the rest of the filth.', 14, 0, 100, 5, 0, 39032, 0, 74433, 0, '');
INSERT INTO `creature_text` VALUES (71763, 0, 0, 'Burn them all! Burn them to ash!', 14, 0, 100, 0, 0, 39142, 0, 74441, 0, '');
INSERT INTO `creature_text` VALUES (71529, 0, 0, '%s reels in pain and leers angrily at $n.', 41, 0, 100, 0, 0, 0, 0, 74449, 0, '');
INSERT INTO `creature_text` VALUES (71529, 1, 0, '%s detects a cluster of injured enemies and goes into a  |cFFFF0404|Hspell:143440|h[Blood Frenzy]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74269, 0, '');
INSERT INTO `creature_text` VALUES (71749, 0, 0, 'Thank you, friends. I will lend what aid I can.', 14, 0, 100, 1, 0, 38933, 0, 74448, 0, '');
INSERT INTO `creature_text` VALUES (71742, 0, 0, 'Stop, my brothers! Do you not see the beast of burden before you?', 14, 0, 100, 5, 0, 38927, 0, 74383, 0, '');
INSERT INTO `creature_text` VALUES (71742, 1, 0, 'Do you not see the light of Kros? He has been defiled!', 14, 0, 100, 5, 0, 38928, 0, 74390, 0, '');
INSERT INTO `creature_text` VALUES (71742, 2, 0, 'By the blood of Kros!', 14, 0, 100, 0, 0, 0, 0, 74387, 0, '');
INSERT INTO `creature_text` VALUES (72694, 0, 0, 'Time to mix business with pleasure.', 14, 0, 100, 0, 0, 38445, 0, 74627, 0, '');
INSERT INTO `creature_text` VALUES (72694, 1, 0, 'Unfinished weapons begin to roll out on the assembly line.', 41, 0, 100, 0, 0, 0, 0, 74677, 2, '');
INSERT INTO `creature_text` VALUES (72694, 2, 0, 'You son of a...', 14, 0, 100, 0, 0, 38450, 0, 75818, 0, '');
INSERT INTO `creature_text` VALUES (72694, 3, 0, 'The SD-5 Automated Shredder defense unit. A labor of love.', 14, 0, 100, 0, 0, 38457, 0, 74669, 0, '');
INSERT INTO `creature_text` VALUES (72694, 4, 0, 'An Automated Shredder draws near!', 41, 0, 100, 0, 0, 0, 0, 74827, 2, '');
INSERT INTO `creature_text` VALUES (72694, 5, 0, 'My legacy... will live on...', 14, 0, 100, 0, 0, 38448, 0, 75819, 0, '');
INSERT INTO `creature_text` VALUES (71161, 0, 0, 'Look brothers, the Wakener is here!', 14, 0, 100, 0, 0, 38657, 0, 75130, 0, '');
INSERT INTO `creature_text` VALUES (71158, 0, 0, 'They made it this far? I told you the others needed some micro-sonic genetic alterations.', 14, 0, 100, 0, 0, 38916, 0, 75136, 0, '');
INSERT INTO `creature_text` VALUES (71160, 0, 0, 'How illogical, did they not hear your warning Kil\'ruk?', 14, 0, 100, 0, 0, 38621, 0, 75090, 0, '');
INSERT INTO `creature_text` VALUES (71157, 0, 0, 'We Mantid serve a power far greater than they or their gods.', 14, 0, 100, 0, 0, 38707, 0, 75123, 0, '');
INSERT INTO `creature_text` VALUES (71156, 0, 0, 'It matters not, the Old One will not suffer their intrusion.', 14, 0, 100, 0, 0, 38646, 0, 75107, 0, '');
INSERT INTO `creature_text` VALUES (71155, 0, 0, 'We have endured the Usurpers, their children, the loss of the Old One, and soon... you.', 14, 0, 100, 0, 0, 38670, 0, 75099, 0, '');
INSERT INTO `creature_text` VALUES (71154, 0, 0, 'We will slay you all as easily as we slew the Pandaren of old.', 14, 0, 100, 0, 0, 38637, 0, 75079, 0, '');
INSERT INTO `creature_text` VALUES (71152, 0, 0, 'And then the Old One will remake this world for the Mantid!', 14, 0, 100, 0, 0, 38677, 0, 75072, 0, '');
INSERT INTO `creature_text` VALUES (71153, 0, 0, 'Come children of the Titans, you face the Paragons.', 14, 0, 100, 0, 0, 38609, 0, 75116, 0, '');
INSERT INTO `creature_text` VALUES (71158, 1, 0, 'Yes... new test subjects!', 14, 0, 100, 0, 0, 38913, 0, 75137, 0, '');
INSERT INTO `creature_text` VALUES (71158, 2, 0, 'Perhaps I could have used... some enhancements... of my own...', 14, 0, 100, 0, 0, 38914, 0, 75142, 0, '');
INSERT INTO `creature_text` VALUES (71153, 1, 0, 'The hunter... becomes... the hunted....', 14, 0, 100, 0, 0, 38607, 0, 75122, 0, '');
INSERT INTO `creature_text` VALUES (71154, 1, 0, 'You move too slowly, how does your kind catch any prey?', 14, 0, 100, 0, 0, 38634, 0, 75080, 0, '');
INSERT INTO `creature_text` VALUES (71155, 1, 0, 'You now face the Prime. Bow down to your superior!', 14, 0, 100, 0, 0, 38667, 0, 75100, 0, '');
INSERT INTO `creature_text` VALUES (71152, 1, 0, 'Avenge me, brothers!', 14, 0, 100, 0, 0, 38675, 0, 75077, 0, '');
INSERT INTO `creature_text` VALUES (71160, 1, 0, 'Chaos is order unrecognized by a lesser mind!', 14, 0, 100, 0, 0, 38618, 0, 75091, 0, '');
INSERT INTO `creature_text` VALUES (71155, 2, 0, 'Return me to the amber...', 14, 0, 100, 0, 0, 38668, 0, 75106, 0, '');
INSERT INTO `creature_text` VALUES (71160, 2, 0, 'How about a game?', 14, 0, 100, 0, 0, 38623, 0, 75092, 0, '');
INSERT INTO `creature_text` VALUES (71154, 2, 0, 'Curse this chitinous shell, I was not fast enough!', 14, 0, 100, 0, 0, 38635, 0, 75088, 0, '');
INSERT INTO `creature_text` VALUES (71157, 1, 0, 'Have you come to try my new concoctions, Wakener?  I assure you these are nothing like you\'ve tasted before.', 14, 0, 100, 0, 0, 38704, 0, 75124, 0, '');
INSERT INTO `creature_text` VALUES (71157, 2, 0, 'So many wonderful varieties, which concoction shall I give you?', 14, 0, 100, 0, 0, 38709, 0, 75125, 0, '');
INSERT INTO `creature_text` VALUES (71156, 1, 0, 'Come little Kunchongs, it\'s feeding time!', 14, 0, 100, 0, 0, 38790, 0, 75108, 0, '');
INSERT INTO `creature_text` VALUES (71160, 3, 0, 'My calculations, incorrect? Perhaps another 800 years...', 14, 0, 100, 0, 0, 38619, 0, 75098, 0, '');
INSERT INTO `creature_text` VALUES (71161, 1, 0, 'I will carry you one last time Wakener... to your doom!', 14, 0, 100, 0, 0, 38654, 0, 75131, 0, '');
INSERT INTO `creature_text` VALUES (71157, 3, 0, 'I never discovered... how to put a stopper... in death...', 14, 0, 100, 0, 0, 38705, 0, 75129, 0, '');
INSERT INTO `creature_text` VALUES (71156, 2, 0, 'Kovok... come to me... I need...', 14, 0, 100, 0, 0, 38643, 0, 75114, 0, '');
INSERT INTO `creature_text` VALUES (71161, 2, 0, 'Well fought Wakener.  We will meet again...', 14, 0, 100, 0, 0, 38655, 0, 75135, 0, '');
INSERT INTO `creature_text` VALUES (23682, 5, 0, 'This end have I reached before.  What new adventure lies in store?', 12, 0, 100, 0, 0, 11964, 0, 23455, 3, 'headless horseman SAY_DEATH');
INSERT INTO `creature_text` VALUES (23543, 1, 0, 'Fire consumes! You\'ve tried and failed.  Let there be no doubt, justice prevailed!', 14, 0, 100, 0, 0, 11967, 0, 22026, 1, 'shade of the horseman - HORSEMAN_EVENT_FAIL');
INSERT INTO `creature_text` VALUES (23543, 2, 0, 'My flames have died, left not a spark. I shall send you myself, to the lifeless dark.', 14, 0, 100, 0, 0, 11968, 0, 22027, 1, 'shade of the horseman - HORSEMAN_PASS_EVENT');
INSERT INTO `creature_text` VALUES (23543, 3, 0, 'The sky is dark.  The fire burns.  You strive in vain as Fate\'s wheel turns.', 14, 0, 100, 0, 0, 12570, 0, 22034, 1, 'shade of the horseman - HORSEMAN_SAY_1');
INSERT INTO `creature_text` VALUES (23543, 4, 0, 'The town still burns, a cleansing fire!  Time is short, I\'ll soon retire!', 14, 0, 100, 0, 0, 12571, 0, 22035, 1, 'shade of the horseman - HORSEMAN_SAY_2');
INSERT INTO `creature_text` VALUES (23543, 5, 0, 'So eager you are, for my blood to spill. Yet to vanquish me, \'tis my head you must kill!', 14, 0, 100, 0, 0, 11969, 0, 22757, 1, 'shade of the horseman -  NYI');
INSERT INTO `creature_text` VALUES (72694, 6, 0, 'Goro\'dan! You\'re on deck!', 14, 0, 100, 0, 0, 38449, 0, 74654, 2, '');
INSERT INTO `creature_text` VALUES (72964, 0, 0, 'You\'re Screwed!', 14, 0, 100, 0, 0, 0, 0, 77113, 2, '');
INSERT INTO `creature_text` VALUES (72694, 7, 0, 'Alright, Shanna. You\'re up.', 14, 0, 100, 0, 0, 38452, 0, 74657, 2, '');
INSERT INTO `creature_text` VALUES (72694, 8, 0, 'Looks like I\'m in the market for a new assistant.', 14, 0, 100, 0, 0, 38453, 0, 74659, 2, '');
INSERT INTO `creature_text` VALUES (44866, 0, 0, 'I\'m willing to help you out, but you have to promise not to mention this to anyone else. Even talking about this kind of thing can hurt my reputation!', 12, 0, 100, 1, 0, 0, 0, 52761, 0, '');
INSERT INTO `creature_text` VALUES (54146, 0, 0, 'There\'s no point in denying it, Dran! That thief knew exactly where to bring his stolen goods.', 12, 0, 100, 1, 0, 0, 0, 52777, 0, '');
INSERT INTO `creature_text` VALUES (54146, 1, 0, 'Shall we see what the grunts have to say about your stolen antiquities?', 12, 0, 100, 1, 0, 0, 0, 52781, 0, '');
INSERT INTO `creature_text` VALUES (54146, 2, 0, 'Now we\'re getting somewhere...', 12, 0, 100, 1, 0, 0, 0, 52782, 0, '');
INSERT INTO `creature_text` VALUES (54146, 3, 0, 'And you, what\'s your interest in this? Let\'s have a word...', 12, 0, 100, 6, 0, 0, 0, 52783, 0, '');
INSERT INTO `creature_text` VALUES (6986, 0, 0, 'I don\'t know what you\'re talking about, elf, and I don\'t appreciate you pokin\' around in my business!', 12, 0, 100, 1, 0, 0, 0, 52778, 0, '');
INSERT INTO `creature_text` VALUES (6986, 1, 0, 'I do seem to have mistakenly come into possesion of some artifacts from Uldum. Perhaps the Reliquary is interested in those?', 12, 0, 100, 1, 0, 0, 0, 52779, 0, '');
INSERT INTO `creature_text` VALUES (6986, 2, 0, 'Fine, fine. I may have helped him contact an interested buyer. But keep your voice down! You\'ll scare away my customers.', 12, 0, 100, 1, 0, 0, 0, 52780, 0, '');
INSERT INTO `creature_text` VALUES (54072, 0, 0, 'Archmage Xylem does not bargain with the likes of you, ruffian. There shall be no payment!', 12, 0, 100, 5, 0, 0, 0, 52710, 0, '');
INSERT INTO `creature_text` VALUES (54072, 1, 0, 'What\'s this, an intruder? Let\'s see what the artifacts inside this box can do, shall we?', 12, 0, 100, 11, 0, 0, 0, 52711, 0, '');
INSERT INTO `creature_text` VALUES (54072, 2, 0, 'This isn\'t supposed to happen!', 12, 0, 100, 431, 0, 0, 0, 52717, 0, '');
INSERT INTO `creature_text` VALUES (8719, 0, 0, 'Naturally, I told him that we don\'t deal in stolen goods. This is the Stormwind auction house, not some smoke-filled, back alley swapmeet!', 12, 0, 100, 5, 0, 0, 0, 52640, 0, '');
INSERT INTO `creature_text` VALUES (54021, 0, 0, 'Stop evading the question, Quincy! I know the thief was here with that crate.', 12, 0, 100, 5, 0, 0, 0, 52661, 0, '');
INSERT INTO `creature_text` VALUES (54021, 1, 0, 'The contents of that crate are very dangerous and they belong in a secure vault, not in the hands of some well-to-do, but ignorant collector!', 12, 0, 100, 1, 0, 0, 0, 52662, 0, '');
INSERT INTO `creature_text` VALUES (54021, 2, 0, 'When the guards pay you a visit, I\'m sure you\'ll have no problem explaining how you got your hands on those Uldum artifacts or that lovely painting.', 12, 0, 100, 1, 0, 0, 0, 52666, 0, '');
INSERT INTO `creature_text` VALUES (54021, 3, 0, 'That\'s a start. And what about you, newcomer? You wouldn\'t be here if you weren\'t after the crate as well.', 12, 0, 100, 1, 0, 0, 0, 52670, 0, '');
INSERT INTO `creature_text` VALUES (53991, 0, 0, 'Keep it down, will you? I\'m trying to run a business here...', 12, 0, 100, 5, 0, 0, 0, 52663, 0, '');
INSERT INTO `creature_text` VALUES (53991, 1, 0, 'If you\'re willing to forget this crate thing, I can give you a good price on some recent arrivals from Uldum.', 12, 0, 100, 6, 0, 0, 0, 52664, 0, '');
INSERT INTO `creature_text` VALUES (53991, 2, 0, 'Fine, fine. I might\'ve given him the name of a private collector interested in that crate\'s contents.', 12, 0, 100, 1, 0, 0, 0, 52665, 0, '');
INSERT INTO `creature_text` VALUES (59546, 0, 0, 'When man asks favor of a fish...', 12, 0, 100, 0, 0, 0, 0, 59187, 0, '');
INSERT INTO `creature_text` VALUES (59546, 1, 0, '\'Tis hard to decipher his true wish...', 12, 0, 100, 0, 0, 0, 0, 59188, 0, '');
INSERT INTO `creature_text` VALUES (59546, 2, 0, 'He often seeks a lordly dish...', 12, 0, 100, 0, 0, 0, 0, 59189, 0, '');
INSERT INTO `creature_text` VALUES (59546, 3, 0, 'To serve upon his table.', 12, 0, 100, 0, 0, 0, 0, 59190, 0, '');
INSERT INTO `creature_text` VALUES (73483, 0, 0, 'It is not too late, Garrosh. Lay down the mantle of Warchief. We can end this here, now, with no more bloodshed.', 14, 0, 100, 0, 0, 38934, 0, 74698, 0, '');
INSERT INTO `creature_text` VALUES (71865, 12, 0, 'Ha! Do you remember nothing of Honor? Of glory on the battlefield?  You who would parlay with the humans, who allowed warlocks to practice their dark magics right under our feet.  You are weak.', 14, 0, 100, 0, 0, 38059, 0, 74701, 0, '');
INSERT INTO `creature_text` VALUES (71865, 13, 0, 'We are the Orcish Horde, the True Horde.  We die, bloody and thrashing on the field of battle, like true orcs SHOULD.', 14, 0, 100, 0, 0, 38060, 0, 74702, 0, '');
INSERT INTO `creature_text` VALUES (71865, 14, 0, 'You are an Orc no longer, and speak for none but yourself. You betrayed our people to forge your fragile alliances, and I will take great pleasure in tearing them apart.', 14, 0, 100, 0, 0, 38061, 0, 74703, 0, '');
INSERT INTO `creature_text` VALUES (73483, 1, 0, 'Then you have forced my hand.  I will correct the mistake I made long ago.  Spirits of the wind, the earth, the water, hear my call!  Come to my aid!', 14, 0, 100, 0, 0, 38935, 0, 74699, 0, '');
INSERT INTO `creature_text` VALUES (71865, 15, 0, 'Fool. My Dark Shaman have twisted and tortured the elements for miles around. They cannot hear you now. Once again you prove too weak and powerless to do anything.', 14, 0, 100, 0, 0, 38062, 0, 74704, 0, '');
INSERT INTO `creature_text` VALUES (73483, 2, 0, 'Never powerless Garrosh. And never alone.', 14, 0, 100, 0, 0, 38936, 0, 74700, 0, '');
INSERT INTO `creature_text` VALUES (71865, 16, 0, 'So, you wish to face off against a real Orc Warchief.  So be it.', 14, 0, 100, 0, 0, 38063, 0, 74705, 0, '');
INSERT INTO `creature_text` VALUES (71987, 0, 0, 'Ooo oOOo ooOoO', 12, 0, 100, 0, 0, 0, 0, 74556, 0, '');
INSERT INTO `creature_text` VALUES (71987, 0, 1, 'ooo OoOooO oo', 12, 0, 100, 0, 0, 0, 0, 74559, 0, '');
INSERT INTO `creature_text` VALUES (71987, 0, 2, 'o OOooO OoooO', 12, 0, 100, 0, 0, 0, 0, 74560, 0, '');
INSERT INTO `creature_text` VALUES (71987, 0, 3, 'oOO OOooO', 12, 0, 100, 0, 0, 0, 0, 74562, 0, '');
INSERT INTO `creature_text` VALUES (74158, 0, 0, 'Heh, sounds like Malkorok just tore apart those intruders.', 12, 0, 100, 0, 0, 0, 0, 75720, 2, '');
INSERT INTO `creature_text` VALUES (74158, 1, 0, 'I don\'t know, I\'ve got a bad feeling. It\'s way too quiet in there.', 12, 0, 100, 0, 0, 0, 0, 75721, 2, '');
INSERT INTO `creature_text` VALUES (72192, 0, 0, 'Hrnngg... thank you...', 12, 0, 100, 0, 0, 38276, 0, 74937, 0, '');
INSERT INTO `creature_text` VALUES (72192, 1, 0, 'I may be bruised and beaten, but the hatred boils inside me.', 12, 0, 100, 396, 0, 38277, 0, 74938, 0, '');
INSERT INTO `creature_text` VALUES (72192, 2, 0, 'Many times have I fallen at the hands of an Orc, and many times have I risen again.', 12, 0, 100, 396, 0, 38278, 0, 74939, 0, '');
INSERT INTO `creature_text` VALUES (72192, 3, 0, 'But never again.', 12, 0, 100, 274, 0, 38279, 0, 74940, 0, '');
INSERT INTO `creature_text` VALUES (72192, 4, 0, 'For every orc that struck me, I will cleave a thousand of their skulls!', 12, 0, 100, 5, 0, 38280, 0, 74941, 0, '');
INSERT INTO `creature_text` VALUES (72192, 5, 0, 'This ends here!', 14, 0, 100, 15, 0, 38281, 0, 74942, 0, '');
INSERT INTO `creature_text` VALUES (72192, 6, 0, 'I, Gamon, will save us!', 14, 0, 100, 71, 0, 38282, 0, 74943, 0, '');
INSERT INTO `creature_text` VALUES (72192, 7, 0, 'Taste my axe!', 14, 0, 100, 0, 0, 38271, 0, 74944, 0, '');
INSERT INTO `creature_text` VALUES (72192, 8, 0, 'Pay for your crimes!', 14, 0, 100, 0, 0, 38272, 0, 74945, 0, '');
INSERT INTO `creature_text` VALUES (72192, 9, 0, 'I am a storm of pain!', 14, 0, 100, 0, 0, 38283, 0, 74946, 0, '');
INSERT INTO `creature_text` VALUES (72192, 10, 0, 'You are an honorable Orc, Nazgrim. I regret what I must do.', 12, 0, 100, 0, 0, 38274, 0, 75887, 0, '');
INSERT INTO `creature_text` VALUES (72192, 11, 0, 'You fought with honor. Gamon will ensure that your legacy lives on. For the Horde, my friend.', 12, 0, 100, 0, 0, 38275, 0, 75888, 0, '');
INSERT INTO `creature_text` VALUES (73716, 0, 0, 'Ah, more of Vol\'jin\'s revolutionaries. You\'ve made it.', 12, 0, 100, 0, 0, 38920, 0, 76677, 0, '');
INSERT INTO `creature_text` VALUES (73716, 1, 0, 'Have you found Thrall? I am badly hurt, he insisted on going ahead alone.', 12, 0, 100, 0, 0, 38921, 0, 76678, 0, '');
INSERT INTO `creature_text` VALUES (73716, 2, 0, 'Tell me, how goes the battle up above? What of Nazgrim? Speak to me!', 12, 0, 100, 0, 0, 38922, 0, 76679, 0, '');
INSERT INTO `creature_text` VALUES (73716, 3, 0, 'Ah, Nazgrim. A great leader and a fine warrior. He valued his oath to the Warchief more than his life.', 12, 0, 100, 0, 0, 38923, 0, 76680, 0, '');
INSERT INTO `creature_text` VALUES (73716, 4, 0, 'I tried to tell him... to tell him that Hellscream betrayed us, cast aside a Warchief\'s responsibility to his own people.  But Nazgrim... too loyal... too proud.', 12, 0, 100, 0, 0, 38924, 0, 76681, 0, '');
INSERT INTO `creature_text` VALUES (73716, 5, 0, 'Damn Hellscream.  His ambition tore our Horde apart.', 12, 0, 100, 274, 0, 38925, 0, 76682, 0, '');
INSERT INTO `creature_text` VALUES (73716, 6, 0, 'Go on, find Thrall.  Finish this.  I will live.', 12, 0, 100, 1, 0, 38926, 0, 76683, 0, '');
INSERT INTO `creature_text` VALUES (59689, 0, 0, 'Who dares disturb my eternal slumber?!', 14, 0, 100, 0, 0, 0, 0, 59243, 0, '');
INSERT INTO `creature_text` VALUES (71859, 8, 0, '|TInterface\\Icons\\spell_shaman_totemrecall.blp:20|t The Dark Shaman drop |cFFFF0000|Hspell:144291|h[Rusted Iron Totems]|h|r!', 41, 0, 100, 0, 0, 0, 0, 74530, 2, '');
INSERT INTO `creature_text` VALUES (71859, 9, 0, 'Crumbling earth, plagued with rust!', 14, 0, 100, 0, 0, 39026, 0, 76945, 2, '');
INSERT INTO `creature_text` VALUES (17651, 0, 0, 'You\'ve got my attention, dragon.  You\'ll find I\'m not as easily scared as the villagers below.', 14, 0, 100, 0, 0, 0, 0, 14295, 0, '');
INSERT INTO `creature_text` VALUES (17651, 1, 0, 'You dare challenge me at my own dwelling?  Your arrogance is astounding, even for a dragon!', 14, 0, 100, 0, 0, 0, 0, 14297, 0, '');
INSERT INTO `creature_text` VALUES (17651, 2, 0, 'I do not know what you speak of, dragon... but  I will not be bullied by this display of insolence.  I\'ll leave Karazhan when it suits me!', 14, 0, 100, 0, 0, 0, 0, 14299, 0, '');
INSERT INTO `creature_text` VALUES (17651, 3, 0, '%s begins to cast a spell of great power, weaving his own essence into the magic.', 16, 0, 100, 0, 0, 0, 0, 14308, 0, '');
INSERT INTO `creature_text` VALUES (17651, 4, 0, 'He should not have angered me.  I must go... recover my strength now...', 14, 0, 100, 0, 0, 0, 0, 14316, 0, '');
INSERT INTO `creature_text` VALUES (17652, 0, 0, 'Your dabbling in the arcane has gone too far, Medivh.  You\'ve attracted the attention of powers beyond your understanding.  You must leave Karazhan at once!', 14, 0, 100, 0, 0, 0, 0, 14296, 0, '');
INSERT INTO `creature_text` VALUES (17652, 1, 0, 'A dark power seeks to use you, Medivh!  If you stay, dire days will follow.  You must hurry, we don\'t have much time!', 14, 0, 100, 0, 0, 0, 0, 14298, 0, '');
INSERT INTO `creature_text` VALUES (17652, 2, 0, 'You leave me no alternative.  I will stop you by force if you won\'t listen to reason!', 14, 0, 100, 0, 0, 0, 0, 14300, 0, '');
INSERT INTO `creature_text` VALUES (17652, 3, 0, 'What have you done, wizard?  This cannot be!  I\'m burning from... within!', 14, 0, 100, 0, 0, 0, 0, 14294, 0, '');
INSERT INTO `creature_text` VALUES (16816, 0, 0, '', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'echo_of_medivh EMOTE_LIFT_CURSE');
