INSERT INTO `creature_text` VALUES (60408, 0, 0, 'She is trying to throw you off, hold on tight!$b$b|TInterface\\Icons\\inv_misc_desecrated_leatherglove.blp:32|t Hold On', 42, 0, 100, 0, 0, 0, 0, 59885, 0, '');
INSERT INTO `creature_text` VALUES (55597, 0, 0, 'Na Lek imbues $p with gratitude and returns to the waters of the pool.', 16, 0, 100, 0, 0, 0, 0, 56967, 0, '');
INSERT INTO `creature_text` VALUES (58606, 0, 0, 'Halt, supplicant! These waters belong to the Thunder King. Your presence is unwelcome.', 14, 0, 100, 5, 0, 0, 0, 56958, 0, '');
INSERT INTO `creature_text` VALUES (58606, 1, 0, 'Guardians! Kill these intruders.', 14, 0, 100, 0, 0, 0, 0, 56959, 0, '');
INSERT INTO `creature_text` VALUES (58206, 0, 0, 'Look at that. The little guy left you some purified water.', 12, 0, 100, 0, 0, 0, 0, 56971, 0, '');
INSERT INTO `creature_text` VALUES (65478, 0, 0, 'Give me the resonating crystal you puny little bark eater!', 14, 0, 100, 0, 0, 0, 0, 66524, 0, '');
INSERT INTO `creature_text` VALUES (65475, 0, 0, 'Is he gone? Is it over?', 12, 0, 100, 0, 0, 0, 0, 66531, 0, '');
INSERT INTO `creature_text` VALUES (42680, 0, 0, 'The rally is about to begin!', 42, 0, 100, 0, 0, 0, 0, 42569, 0, '');
INSERT INTO `creature_text` VALUES (42680, 1, 0, 'Gather, brothers and sisters! Come, all, and listen!', 14, 0, 100, 22, 0, 0, 0, 42570, 0, '');
INSERT INTO `creature_text` VALUES (42680, 2, 0, 'Brothers. Sisters. We are ABANDONED - the orphaned children of Stormwind.', 14, 0, 100, 5, 0, 0, 0, 42571, 0, '');
INSERT INTO `creature_text` VALUES (42680, 3, 0, 'Our \"king\" sits atop his throne made of gold and shrugs at our plight!', 14, 0, 100, 5, 0, 0, 0, 42572, 0, '');
INSERT INTO `creature_text` VALUES (42680, 4, 0, 'Meanwhile, our children die of starvation on these very streets!', 14, 0, 100, 5, 0, 0, 0, 42573, 0, '');
INSERT INTO `creature_text` VALUES (42680, 5, 0, 'HIS war, not ours, cost us our livelihood. WE paid for the Alliance\'s victories with our blood and the blood of our loved ones!', 14, 0, 100, 5, 0, 0, 0, 42574, 0, '');
INSERT INTO `creature_text` VALUES (42680, 6, 0, 'The time has come, brothers and sisters, to stop this injustice!', 14, 0, 100, 5, 0, 0, 0, 42575, 0, '');
INSERT INTO `creature_text` VALUES (42680, 7, 0, 'The government of Stormwind, of the ALLIANCE, must be made accountable for what it has done to us!', 14, 0, 100, 5, 0, 0, 0, 42576, 0, '');
INSERT INTO `creature_text` VALUES (42680, 8, 0, 'Today, we are reborn! Today, we take a stand as men and women, not nameless, faceless numbers!', 14, 0, 100, 5, 0, 0, 0, 42577, 0, '');
INSERT INTO `creature_text` VALUES (42750, 0, 0, 'I don\'t like this, $n. Stay alert!', 12, 0, 100, 1, 0, 0, 0, 42655, 0, '');
INSERT INTO `creature_text` VALUES (42749, 0, 0, 'You bastards will burn for what you did.', 12, 0, 100, 25, 0, 0, 0, 42656, 0, '');
INSERT INTO `creature_text` VALUES (42750, 1, 0, 'Hope! Wha..', 12, 0, 100, 5, 0, 0, 0, 42657, 0, '');
INSERT INTO `creature_text` VALUES (42749, 1, 0, 'Hope?  Like the hope Fandral had for bringing our people  back to glory?  Back to immortality?', 12, 0, 100, 1, 0, 0, 0, 44048, 0, '');
INSERT INTO `creature_text` VALUES (42749, 2, 0, 'Hope is a cruel joke, played upon us by a harsh and uncaring world. There is no Hope, there is only Vanessa. Vanessa VanCleef.', 12, 0, 100, 1, 0, 0, 0, 42659, 0, '');
INSERT INTO `creature_text` VALUES (42749, 3, 0, 'RISE UP BROTHERHOOD! THE DAWNING DAY IS UPON US!', 14, 0, 100, 22, 0, 0, 0, 42660, 0, '');
INSERT INTO `creature_text` VALUES (42749, 4, 0, 'Tie them up.', 12, 0, 100, 25, 0, 0, 0, 42661, 0, '');
INSERT INTO `creature_text` VALUES (42749, 5, 0, 'Admiral, your hat.', 12, 0, 100, 1, 0, 0, 0, 42662, 0, '');
INSERT INTO `creature_text` VALUES (42748, 0, 0, 'Thank you, my dear.', 12, 0, 100, 1, 0, 0, 0, 42663, 0, '');
INSERT INTO `creature_text` VALUES (42749, 6, 0, 'And you, $n, I will spare your life. You have done much to help our cause, albeit unwittingly, but the next time we meet it will be as enemies. ', 12, 0, 100, 25, 0, 0, 0, 42673, 0, '');
INSERT INTO `creature_text` VALUES (42744, 0, 0, 'Just tell me one thing, Vanessa.', 12, 0, 100, 0, 0, 0, 0, 42674, 0, '');
INSERT INTO `creature_text` VALUES (42744, 1, 0, 'Why\'d you have the Furlbrows killed?', 12, 0, 100, 0, 0, 0, 0, 42675, 0, '');
INSERT INTO `creature_text` VALUES (42749, 7, 0, 'I had no choice, lieutenant. They recognized me. The only people in the world who even knew I existed, recognized my face from when I was an infant.', 12, 0, 100, 1, 0, 0, 0, 42676, 0, '');
INSERT INTO `creature_text` VALUES (42749, 8, 0, 'I took no pleasure in their deaths.', 12, 0, 100, 1, 0, 0, 0, 42677, 0, '');
INSERT INTO `creature_text` VALUES (42749, 9, 0, 'Leave nothing but ashes in your wake, brothers! Burn Sentinel Hill to the ground!', 14, 0, 100, 22, 0, 0, 0, 42672, 0, '');
INSERT INTO `creature_text` VALUES (42750, 2, 0, '$n, get to Stormwind. Tell King Wrynn everything. EVERYTHING! GO NOW!', 12, 0, 100, 0, 0, 0, 0, 42681, 0, '');
