INSERT INTO `creature_text` VALUES (71865, 22, 0, '|TInterface\\Icons\\ability_vehicle_demolisherflamecatapult.blp:20|tThe Horde Fleet begins their |cFFFF0000|Hspell:147088|h[Bombardment]|h|r!', 41, 0, 100, 0, 0, 0, 0, 75795, 0, '');
INSERT INTO `creature_text` VALUES (71865, 23, 0, 'I WILL DESTROY EVERYTHING YOU HAVE EVER LOVED.', 14, 0, 100, 0, 0, 38050, 0, 75823, 0, '');
INSERT INTO `creature_text` VALUES (46768, 0, 0, 'Active.', 12, 0, 100, 0, 0, 0, 0, 50356, 0, '');
INSERT INTO `creature_text` VALUES (46769, 0, 0, 'Online.', 12, 0, 100, 0, 0, 0, 0, 50359, 0, '');
INSERT INTO `creature_text` VALUES (68740, 0, 0, 'It\'s about time ye showed up. Doomsday is a comin\'.', 12, 0, 100, 0, 0, 0, 0, 70648, 1, '');
INSERT INTO `creature_text` VALUES (68740, 1, 0, 'Garrosh is amassing his troops on the northern side of Kun-Lai Summit.', 12, 0, 100, 0, 0, 0, 0, 70649, 1, '');
INSERT INTO `creature_text` VALUES (68740, 2, 0, 'He\'s going to use the Divine Bell, and Light help us if we can\'t stop \'im in time.', 12, 0, 100, 0, 0, 0, 0, 70650, 1, '');
INSERT INTO `creature_text` VALUES (68740, 3, 0, 'Anduin went on ahead. That cheeky, light-spoutin\', clod-brained fool!', 12, 0, 100, 0, 0, 0, 0, 70651, 1, '');
INSERT INTO `creature_text` VALUES (68740, 4, 0, 'Boy\'s naive enough ta think he can change the Horde \'an save the world... but... he\'s still just a child.', 12, 0, 100, 0, 0, 0, 0, 70652, 1, '');
INSERT INTO `creature_text` VALUES (68740, 5, 0, 'Ye\'ve either got to stop the boy or help him... one o\' the two!', 12, 0, 100, 0, 0, 0, 0, 70653, 1, '');
INSERT INTO `creature_text` VALUES (68740, 6, 0, 'We\'re amassin\' an army to head Garrosh off, but that will take time, and the Prince may already be in danger.', 12, 0, 100, 0, 0, 0, 0, 70654, 1, '');
INSERT INTO `creature_text` VALUES (68740, 7, 0, 'Oh, why can\'t this dang kite go faster?', 12, 0, 100, 0, 0, 0, 0, 70655, 1, '');
INSERT INTO `creature_text` VALUES (68740, 8, 0, 'Quick - they haven\'t spotted us yet.', 12, 0, 100, 0, 0, 0, 0, 70656, 1, '');
INSERT INTO `creature_text` VALUES (68740, 9, 0, 'I\'ll go back fer reinforcements.', 12, 0, 100, 0, 0, 0, 0, 70657, 1, '');
INSERT INTO `creature_text` VALUES (68740, 10, 0, 'Whatever ye do today, hero, ye\'re doin\' fer all the Alliance.', 12, 0, 100, 0, 0, 0, 0, 70658, 1, '');
INSERT INTO `creature_text` VALUES (68718, 0, 0, 'I got a bad feelin\' about this.', 12, 0, 100, 0, 0, 0, 0, 70535, 1, '');
INSERT INTO `creature_text` VALUES (68718, 1, 0, 'Your Warchief has got that bell, and he\'s goin\' to use it to make the Horde stronger.', 12, 0, 100, 0, 0, 0, 0, 70536, 1, '');
INSERT INTO `creature_text` VALUES (68718, 2, 0, 'But I think that bell has got some power... and it\'s bad.', 12, 0, 100, 0, 0, 0, 0, 70537, 1, '');
INSERT INTO `creature_text` VALUES (68718, 3, 0, 'Nobody, not hozen, not human, not orc... should use the sha.', 12, 0, 100, 0, 0, 0, 0, 70539, 1, '');
INSERT INTO `creature_text` VALUES (68718, 4, 0, 'Be careful.', 12, 0, 100, 0, 0, 0, 0, 70541, 1, '');
INSERT INTO `creature_text` VALUES (68718, 5, 0, 'I hope I see you again, friend.', 12, 0, 100, 0, 0, 0, 0, 70542, 1, '');
INSERT INTO `creature_text` VALUES (43197, 0, 0, 'ROCK NOT SO HEAVY! PUNY HUMIES!', 12, 0, 100, 0, 0, 0, 0, 43218, 0, '');
INSERT INTO `creature_text` VALUES (43197, 1, 0, 'Where trow? TROW ON BRIDGE??', 12, 0, 100, 0, 0, 0, 0, 43220, 0, '');
INSERT INTO `creature_text` VALUES (43197, 2, 0, 'OK! Me trow in water!', 12, 0, 100, 0, 0, 0, 0, 43222, 0, '');
INSERT INTO `creature_text` VALUES (2545, 0, 0, 'This is humiliating.', 12, 0, 100, 18, 0, 0, 0, 50833, 0, '');
INSERT INTO `creature_text` VALUES (2545, 1, 0, 'I can\'t believe it\'s come to this.', 12, 0, 100, 38, 0, 0, 0, 50834, 0, '');
INSERT INTO `creature_text` VALUES (2545, 2, 0, 'A mop?  Really?', 12, 0, 100, 38, 0, 0, 0, 50835, 0, '');
INSERT INTO `creature_text` VALUES (2545, 3, 0, 'I suppose sometimes y\'need to hit rock bottom before y\'can crawl yer way back up.', 12, 0, 100, 38, 0, 0, 0, 50836, 0, '');
INSERT INTO `creature_text` VALUES (2545, 4, 0, 'Maybe I need a new nickname.', 12, 0, 100, 6, 0, 0, 0, 50837, 0, '');
INSERT INTO `creature_text` VALUES (64315, 0, 0, 'Oh, I HAVE heard of the ancestors\' stair. That\'s what some folks call those mountains to the north.', 12, 0, 100, 0, 0, 0, 0, 64255, 0, '');
INSERT INTO `creature_text` VALUES (64327, 0, 0, 'What echoes and what\'s dark? It\'s simple! A cave!', 12, 0, 100, 0, 0, 0, 0, 64253, 0, '');
INSERT INTO `creature_text` VALUES (64313, 0, 0, 'The maiden, eh? Hmm, I remember that\'s what my father used to call that river to the north. Other than that, I wouldn\'t have the slightest clue.', 12, 0, 100, 0, 0, 0, 0, 64252, 0, '');
INSERT INTO `creature_text` VALUES (64312, 0, 0, 'Hmmm, I reckon I don\'t know, $r. All I can understand is the part about the valley - that\'s right here! The Valley of the Four Winds!', 12, 0, 100, 0, 0, 0, 0, 64254, 0, '');
INSERT INTO `creature_text` VALUES (71479, 4, 0, '|TInterface\\Icons\\ability_gouge:20|t%s attempts to |cFFFF0000|Hspell:143330|h[Gouge]|h|r you, turn away!', 42, 0, 100, 0, 0, 0, 0, 76177, 0, '');
INSERT INTO `creature_text` VALUES (71515, 11, 0, 'In the end, I stood by the warchief, because it was my duty, and I am glad that it was you who struck me down. May your strength... lead the horde... into a new era of prosperity...', 12, 0, 100, 0, 0, 38197, 0, 75929, 2, '');
