INSERT INTO `creature_text` VALUES (45878, 0, 0, 'Lorna, is that how you greet old friends? ', 12, 0, 100, 6, 0, 0, 0, 46073, 0, '');
INSERT INTO `creature_text` VALUES (45997, 1, 0, 'Godfrey? So the rumors are true.', 12, 0, 100, 397, 0, 0, 0, 46074, 0, '');
INSERT INTO `creature_text` VALUES (45997, 2, 0, 'You would betray your kingdom out of spite?', 12, 0, 100, 6, 0, 0, 0, 46075, 0, '');
INSERT INTO `creature_text` VALUES (45878, 1, 0, 'My kingdom? My kingdom is no more.', 12, 0, 100, 6, 0, 0, 0, 46076, 0, '');
INSERT INTO `creature_text` VALUES (45878, 2, 0, 'Your father and Greymane saw to that... No, Gilneas died when they allowed murderous beasts to reign free. Unchecked.', 12, 0, 100, 25, 0, 0, 0, 46077, 0, '');
INSERT INTO `creature_text` VALUES (45878, 3, 0, 'Now lay down your arms and surrender. We are not here to kill you. Not yet.', 12, 0, 100, 1, 0, 0, 0, 46078, 0, '');
INSERT INTO `creature_text` VALUES (45997, 3, 0, 'HAH! You\'ll have to kill me! DIE!', 12, 0, 100, 11, 0, 0, 0, 46079, 0, '');
INSERT INTO `creature_text` VALUES (45997, 4, 0, '%s falls unconscious.', 16, 0, 100, 0, 0, 0, 0, 14444, 0, '');
INSERT INTO `creature_text` VALUES (45910, 0, 0, 'Is it safe?', 12, 0, 100, 0, 0, 0, 0, 45937, 0, '');
INSERT INTO `creature_text` VALUES (45910, 0, 1, 'Are... are they all dead?', 12, 0, 100, 0, 0, 0, 0, 45938, 0, '');
INSERT INTO `creature_text` VALUES (45910, 0, 2, 'They struck from the shadows. We didn\'t stand a chance.', 12, 0, 100, 0, 0, 0, 0, 45939, 0, '');
INSERT INTO `creature_text` VALUES (45910, 0, 3, 'I thought I was done for!', 12, 0, 100, 0, 0, 0, 0, 27897, 0, '');
INSERT INTO `creature_text` VALUES (45910, 0, 4, 'You must be the hero that everyone is talking about. Thank you.', 12, 0, 100, 0, 0, 0, 0, 45941, 0, '');
INSERT INTO `creature_text` VALUES (2120, 0, 0, 'Destroy Arcane Fiends near Archmage Ataeric to overload the shield!', 42, 0, 100, 0, 0, 0, 0, 46925, 0, '');
INSERT INTO `creature_text` VALUES (2120, 1, 0, 'Minions, destroy the interloper! The shield must not come down!', 14, 0, 100, 0, 0, 0, 0, 45824, 0, '');
INSERT INTO `creature_text` VALUES (2120, 2, 0, 'You will not succeed, monster!', 14, 0, 100, 0, 0, 0, 0, 45825, 0, '');
INSERT INTO `creature_text` VALUES (2120, 3, 0, 'We will... never... give up!', 14, 0, 100, 0, 0, 0, 0, 45826, 0, '');
INSERT INTO `creature_text` VALUES (2120, 4, 0, 'The armies of the Alliance will march upon your bones!', 14, 0, 100, 0, 0, 0, 0, 45827, 0, '');
INSERT INTO `creature_text` VALUES (2120, 5, 0, 'Lordaeron belongs to the Alliance. You only delay the inevitable...', 14, 0, 100, 0, 0, 0, 0, 45828, 0, '');
INSERT INTO `creature_text` VALUES (2120, 6, 0, 'The power...is too much...I cannot hold...any longer...', 14, 0, 100, 0, 0, 0, 0, 46922, 0, '');
INSERT INTO `creature_text` VALUES (2120, 7, 0, 'RUN! DO NOT LET THEM TURN YOU!', 14, 0, 100, 0, 0, 0, 0, 45829, 0, '');
INSERT INTO `creature_text` VALUES (66185, 0, 0, 'Father!', 12, 0, 100, 0, 0, 0, 0, 29767, 0, '');
INSERT INTO `creature_text` VALUES (66185, 1, 0, 'Shut up.', 12, 0, 100, 0, 0, 0, 0, 27002, 0, '');
INSERT INTO `creature_text` VALUES (66185, 2, 0, 'You raised me to be a killer.  How am I doing, daddy?', 12, 0, 100, 0, 0, 0, 0, 39141, 0, '');
INSERT INTO `creature_text` VALUES (66185, 3, 0, 'But wait... I remember now.  You taught me to only kill the undead.  So do you want me to kill myself, daddy?', 12, 0, 100, 0, 0, 0, 0, 39143, 0, '');
INSERT INTO `creature_text` VALUES (66185, 4, 0, 'Then again, why kill myself... when I can kill YOU instead!', 12, 0, 100, 0, 0, 0, 0, 39145, 0, '');
INSERT INTO `creature_text` VALUES (38981, 0, 0, 'DIE, YOU WRETCHES!', 14, 0, 100, 0, 0, 0, 0, 39166, 0, '');
INSERT INTO `creature_text` VALUES (42298, 0, 0, 'With the power within the amulets of Razelikh...', 12, 0, 100, 0, 0, 0, 0, 42217, 0, '');
INSERT INTO `creature_text` VALUES (42298, 1, 0, '... I bind you to his lair!', 12, 0, 100, 0, 0, 0, 0, 42218, 0, '');
INSERT INTO `creature_text` VALUES (7506, 0, 0, 'With the power within the amulets of Razelikh...', 12, 0, 100, 0, 0, 0, 0, 42217, 0, '');
INSERT INTO `creature_text` VALUES (7506, 1, 0, '... I bind you to his lair!', 12, 0, 100, 0, 0, 0, 0, 42218, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 0, 'This is... worse... than death...', 12, 0, 100, 0, 0, 0, 0, 47498, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 1, 'Ugh!  Why are you WET!?', 12, 0, 100, 0, 0, 0, 0, 47499, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 2, 'Light... fading... dying?  No, just sleeping...', 12, 0, 100, 0, 0, 0, 0, 47501, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 3, 'Where... did... he... come from...', 12, 0, 100, 0, 0, 0, 0, 47506, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 4, 'But... but...', 12, 0, 100, 0, 0, 0, 0, 47509, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 5, 'That\'s... one... giant... keester...', 12, 0, 100, 0, 0, 0, 0, 47510, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 6, 'Why did it have to be... a smelly ogre...', 12, 0, 100, 0, 0, 0, 0, 47512, 0, '');
INSERT INTO `creature_text` VALUES (5840, 0, 7, 'I can\'t feel my legs...', 12, 0, 100, 0, 0, 0, 0, 47514, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 0, 'Here, I stealed this from little dwarf.', 12, 0, 100, 0, 0, 0, 0, 47525, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 1, 'He will be sleepy for a while.  Lunk know.  Lunk do before.', 12, 0, 100, 0, 0, 0, 0, 47526, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 2, 'Here, you can have dwarf\'s things.', 12, 0, 100, 0, 0, 0, 0, 47527, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 3, 'You take dwarf\'s things.  No need to kill.', 12, 0, 100, 0, 0, 0, 0, 47528, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 4, 'Sitting easier than killing.', 12, 0, 100, 0, 0, 0, 0, 47529, 0, '');
INSERT INTO `creature_text` VALUES (47333, 0, 5, 'Here you go.', 12, 0, 100, 0, 0, 0, 0, 47530, 0, '');
INSERT INTO `creature_text` VALUES (58745, 0, 0, 'And now for the moment of truth.', 12, 0, 100, 0, 0, 0, 0, 57893, 0, '');
INSERT INTO `creature_text` VALUES (58745, 1, 0, 'Aaaaaaaaahhh!', 14, 0, 100, 0, 0, 0, 0, 57894, 0, '');
INSERT INTO `creature_text` VALUES (58745, 2, 0, 'That... hurt...', 12, 0, 100, 0, 0, 0, 0, 57895, 0, '');
INSERT INTO `creature_text` VALUES (58735, 0, 0, 'Father! Are you okay?', 12, 0, 100, 0, 0, 0, 0, 57896, 0, '');
INSERT INTO `creature_text` VALUES (58970, 0, 0, 'I spent so long trying to find a way to extend my life that I forgot to live it.', 12, 0, 100, 0, 0, 0, 0, 57760, 0, '');
INSERT INTO `creature_text` VALUES (58970, 1, 0, 'And now this... folly... would cost my daughter her life.', 12, 0, 100, 0, 0, 0, 0, 57762, 0, '');
INSERT INTO `creature_text` VALUES (58970, 2, 0, 'The water may not create life, but at least it will let me transfer it.', 12, 0, 100, 0, 0, 0, 0, 57764, 0, '');
INSERT INTO `creature_text` VALUES (58970, 3, 0, 'I love you, my little Lyalia.', 12, 0, 100, 0, 0, 0, 0, 57763, 0, '');
INSERT INTO `creature_text` VALUES (58833, 0, 0, 'F... father? No! Not for me! Not for this!', 12, 0, 100, 0, 0, 0, 0, 57918, 0, '');
INSERT INTO `creature_text` VALUES (68828, 0, 0, 'Just in time, $p! We need your help!', 14, 0, 100, 0, 0, 0, 0, 70852, 0, '');
INSERT INTO `creature_text` VALUES (46193, 0, 0, 'Shotgun!', 12, 0, 100, 275, 0, 0, 0, 45576, 0, '');
INSERT INTO `creature_text` VALUES (46191, 0, 0, 'Hey now!  Why do you get to sit in front?', 12, 0, 100, 6, 0, 0, 0, 45577, 0, '');
INSERT INTO `creature_text` VALUES (46193, 1, 0, 'Because I won our bet.  And because I called shotgun.', 12, 0, 100, 1, 0, 0, 0, 45578, 0, '');
INSERT INTO `creature_text` VALUES (46191, 1, 0, 'Who says you won the bet?  We haven\'t compared tallies yet.', 12, 0, 100, 6, 0, 0, 0, 45579, 0, '');
INSERT INTO `creature_text` VALUES (46193, 2, 0, 'Fine.  I killed five zombies.  And they were huge.  How many undead did you kill?', 12, 0, 100, 1, 0, 0, 0, 45580, 0, '');
INSERT INTO `creature_text` VALUES (46191, 2, 0, 'I\'m pretty sure I killed six.', 12, 0, 100, 24, 0, 0, 0, 45581, 0, '');
INSERT INTO `creature_text` VALUES (46193, 3, 0, 'I\'m going to bet that you only killed four.', 12, 0, 100, 1, 0, 0, 0, 45582, 0, '');
INSERT INTO `creature_text` VALUES (46191, 3, 0, 'H... how did you know that?', 12, 0, 100, 5, 0, 0, 0, 45583, 0, '');
INSERT INTO `creature_text` VALUES (46193, 4, 0, 'I didn\'t.  You just told me!', 12, 0, 100, 5, 0, 0, 0, 45584, 0, '');
INSERT INTO `creature_text` VALUES (46193, 5, 0, 'So seriously, how did your undead hunting go?  I want to hear stories.', 12, 0, 100, 1, 0, 0, 0, 45585, 0, '');
INSERT INTO `creature_text` VALUES (46193, 6, 0, 'Gidwin?  Are you ignoring me?', 12, 0, 100, 6, 0, 0, 0, 45586, 0, '');
INSERT INTO `creature_text` VALUES (46193, 7, 0, 'Giddy... you\'re mad at me, aren\'t you?', 12, 0, 100, 1, 0, 0, 0, 45587, 0, '');
INSERT INTO `creature_text` VALUES (46193, 8, 0, 'Remember when we were kids, Gidwin?  We would spend all day pretending to fight the trolls, or the murlocs, or the girls.', 12, 0, 100, 1, 0, 0, 0, 45588, 0, '');
INSERT INTO `creature_text` VALUES (46191, 4, 0, 'Don\'t do this, Tarenar.', 12, 0, 100, 274, 0, 0, 0, 45589, 0, '');
INSERT INTO `creature_text` VALUES (46193, 9, 0, 'We had a name we called ourselves... what was it?', 12, 0, 100, 6, 0, 0, 0, 45590, 0, '');
INSERT INTO `creature_text` VALUES (46191, 5, 0, 'You remember damn well enough what it was.', 12, 0, 100, 22, 0, 0, 0, 45591, 0, '');
INSERT INTO `creature_text` VALUES (46193, 10, 0, 'I\'m dead serious.  I\'ve completely forgotten.', 12, 0, 100, 274, 0, 0, 0, 45592, 0, '');
INSERT INTO `creature_text` VALUES (46191, 6, 0, '<Sigh>... \"The Paladin Pals\".', 12, 0, 100, 24, 0, 0, 0, 45593, 0, '');
INSERT INTO `creature_text` VALUES (46192, 0, 0, 'Gidwin, you can sit in front with me next time.', 12, 0, 100, 1, 0, 0, 0, 45594, 0, '');
INSERT INTO `creature_text` VALUES (46191, 7, 0, 'Maybe now I don\'t want to.  Maybe I like it better sitting back here with my new friend.  It smells like horses up there.', 12, 0, 100, 1, 0, 0, 0, 45595, 0, '');
INSERT INTO `creature_text` VALUES (46192, 1, 0, 'There\'s a tower coming up on our left.  What do you say we stop and rest a bit?', 12, 0, 100, 1, 0, 0, 0, 45596, 0, '');
INSERT INTO `creature_text` VALUES (46191, 8, 0, 'We just rested a few minutes ago!  Let\'s just go to Light\'s Hope Chapel!', 12, 0, 100, 5, 0, 0, 0, 45597, 0, '');
INSERT INTO `creature_text` VALUES (46192, 2, 0, 'The roads here are rough, Gidwin.  I don\'t want to risk losing a horse.', 12, 0, 100, 1, 0, 0, 0, 45598, 0, '');
INSERT INTO `creature_text` VALUES (46191, 9, 0, 'We won\'t lose a horse.  Look at them!  They look exactly like they did when we started.', 12, 0, 100, 5, 0, 0, 0, 45599, 0, '');
INSERT INTO `creature_text` VALUES (46193, 11, 0, 'Actually, I agree with Fiona.', 12, 0, 100, 274, 0, 0, 0, 45600, 0, '');
INSERT INTO `creature_text` VALUES (46193, 12, 0, 'The Argent Crusade can wait for us a little while longer.  Besides... our guest may want to get off the caravan soon.', 12, 0, 100, 274, 0, 0, 0, 45601, 0, '');
INSERT INTO `creature_text` VALUES (46192, 3, 0, 'Here we are!', 12, 0, 100, 1, 0, 0, 0, 45602, 0, '');
INSERT INTO `creature_text` VALUES (72726, 0, 0, 'Ah, new customers. Welcome!', 14, 0, 100, 0, 0, 0, 0, 75286, 0, '');
INSERT INTO `creature_text` VALUES (72726, 1, 0, 'Leaving so soon? Do come again!', 14, 0, 100, 0, 0, 0, 0, 75289, 0, '');
INSERT INTO `creature_text` VALUES (72726, 2, 0, 'Time to close already? Well, I guess I should get home to Chao-Wei.', 14, 0, 100, 0, 0, 0, 0, 75291, 0, '');
INSERT INTO `creature_text` VALUES (72726, 3, 0, 'The secret to good noodle soup begins with strong, thick noodles!', 14, 0, 100, 0, 0, 0, 0, 75287, 0, '');
INSERT INTO `creature_text` VALUES (72726, 4, 0, 'The soup must be piping hot to warm the soul!', 14, 0, 100, 0, 0, 0, 0, 75288, 0, '');
INSERT INTO `creature_text` VALUES (50782, 0, 0, 'You die!', 12, 0, 100, 0, 0, 0, 0, 63056, 0, '');
INSERT INTO `creature_text` VALUES (50783, 0, 0, 'Stab stab stab stab stab!', 12, 0, 100, 0, 0, 0, 0, 66132, 0, '');
INSERT INTO `creature_text` VALUES (50805, 0, 0, 'No! My gold!!', 12, 0, 100, 0, 0, 0, 0, 66136, 0, '');
INSERT INTO `creature_text` VALUES (50332, 0, 0, 'I am seeing red!', 12, 0, 100, 0, 0, 0, 0, 66097, 0, '');
INSERT INTO `creature_text` VALUES (50333, 0, 0, 'You have woken the bull, now face the horns!', 12, 0, 100, 0, 0, 0, 0, 66098, 0, '');
INSERT INTO `creature_text` VALUES (50336, 0, 0, 'You! What are you doing here?', 12, 0, 100, 0, 0, 0, 0, 66101, 0, '');
INSERT INTO `creature_text` VALUES (50341, 0, 0, 'Feel the fist of darkness!', 12, 0, 100, 0, 0, 0, 0, 66105, 0, '');
INSERT INTO `creature_text` VALUES (50344, 0, 0, 'Your end is near.', 12, 0, 100, 0, 0, 0, 0, 66106, 0, '');
INSERT INTO `creature_text` VALUES (50791, 0, 0, 'I will wear your skin as a hat!', 12, 0, 100, 0, 0, 0, 0, 66134, 0, '');
INSERT INTO `creature_text` VALUES (50820, 0, 0, 'I must give in to the anger! It is the only way!', 12, 0, 100, 0, 0, 0, 0, 66142, 0, '');
INSERT INTO `creature_text` VALUES (50831, 0, 0, 'Gonna hit you in the wikket!', 12, 0, 100, 0, 0, 0, 0, 66148, 0, '');
INSERT INTO `creature_text` VALUES (50832, 0, 0, 'You has ook for brains!', 12, 0, 100, 0, 0, 0, 0, 66149, 0, '');
INSERT INTO `creature_text` VALUES (50364, 0, 0, 'Far from home, aren\'t you?', 12, 0, 100, 0, 0, 0, 0, 66117, 0, '');
INSERT INTO `creature_text` VALUES (50768, 0, 0, 'You disrupt my meditations? You will pay...', 12, 0, 100, 0, 0, 0, 0, 66125, 0, '');
INSERT INTO `creature_text` VALUES (50816, 0, 0, 'My vision is clouded, why have you done this to us?', 12, 0, 100, 0, 0, 0, 0, 66140, 0, '');
INSERT INTO `creature_text` VALUES (50830, 0, 0, 'Gimme dem munchins!!', 12, 0, 100, 0, 0, 0, 0, 66147, 0, '');
INSERT INTO `creature_text` VALUES (50822, 0, 0, 'The darkness in my soul screams for release!', 12, 0, 100, 0, 0, 0, 0, 66144, 0, '');
INSERT INTO `creature_text` VALUES (50811, 0, 0, 'This darkness in my heart, it is your doing!', 12, 0, 100, 0, 0, 0, 0, 66139, 0, '');
INSERT INTO `creature_text` VALUES (73316, 0, 0, 'Welcome, $n. I\'ve had the market area cleared to make room for Halfhill\'s first noodle stand, run by you!', 12, 0, 100, 3, 0, 0, 0, 75385, 0, '');
INSERT INTO `creature_text` VALUES (73316, 1, 0, 'Customers will line up and wait for you to tell them to sit down. They will only wait for a short while before leaving so be sure to get them to a seat before they leave.', 12, 0, 100, 0, 0, 0, 0, 75387, 0, '');
INSERT INTO `creature_text` VALUES (73316, 2, 0, 'Click on a patron who is waiting in line. You should see them wave to you when you have successfully interacted with them.', 12, 0, 100, 0, 0, 0, 0, 75997, 0, '');
INSERT INTO `creature_text` VALUES (73316, 3, 0, 'When a customer has been selected, click on an open seat and the customer will go sit there.', 12, 0, 100, 0, 0, 0, 0, 75999, 0, '');
INSERT INTO `creature_text` VALUES (73316, 4, 0, 'Once seated, a customer will wait a short while for you to deliver their noodle soup. As with waiting in line, be sure to get the customer their food before they decide to leave.', 12, 0, 100, 0, 0, 0, 0, 75388, 0, '');
INSERT INTO `creature_text` VALUES (73316, 5, 0, 'Make some noodle soup for your customer. This is a scholar so they like the simplicity of a standard noodle soup. Try making one now.', 12, 0, 100, 0, 0, 0, 0, 76005, 0, '');
INSERT INTO `creature_text` VALUES (73316, 6, 0, 'Now that the soup is made, pick it up by clicking on it. Soups will go bad if left out too long so only make what you need for your customers.', 12, 0, 100, 0, 0, 0, 0, 76006, 0, '');
INSERT INTO `creature_text` VALUES (73316, 7, 0, 'Excellent! Now your customer will happily eat the noodle soup you\'ve made for them. When they\'re done they\'ll leave happy.', 12, 0, 100, 4, 0, 0, 0, 76008, 0, '');
INSERT INTO `creature_text` VALUES (73316, 8, 0, 'Some tips: You can always tell what a customer wants by their outfit.', 12, 0, 100, 0, 0, 0, 0, 75389, 0, '');
INSERT INTO `creature_text` VALUES (73316, 9, 0, 'Some customers are hungry, they\'ll want a second bowl after they finish their first.', 12, 0, 100, 0, 0, 0, 0, 75390, 0, '');
INSERT INTO `creature_text` VALUES (73316, 10, 0, 'Other customers are impatient, they won\'t wait as long; but are really happy when they are served quickly.', 12, 0, 100, 0, 0, 0, 0, 75392, 0, '');
INSERT INTO `creature_text` VALUES (73316, 11, 0, 'Now get out there and make some noodle soup!', 12, 0, 100, 0, 0, 0, 0, 75393, 0, '');
INSERT INTO `creature_text` VALUES (72916, 0, 0, 'Wow, I have quite an appetite today!', 12, 0, 100, 0, 0, 0, 0, 75394, 0, '');
INSERT INTO `creature_text` VALUES (72916, 1, 0, 'I heard they discovered the lost secret of the Lin family\'s secret ingredient noodle soup.', 12, 0, 100, 0, 0, 0, 0, 75356, 0, '');
INSERT INTO `creature_text` VALUES (73316, 12, 0, 'Well done!', 12, 0, 100, 4, 0, 0, 0, 3287, 0, '');
INSERT INTO `creature_text` VALUES (45741, 0, 0, 'I will be goin\' now.  I have no reason to remain here.  I may be undead, but my will is my own.', 12, 0, 100, 0, 0, 0, 0, 45810, 0, '');
INSERT INTO `creature_text` VALUES (45741, 1, 0, 'I will see you in green fields on the other side, $n.', 12, 0, 100, 0, 0, 0, 0, 45811, 0, '');
INSERT INTO `creature_text` VALUES (46191, 10, 0, 'This hardly seems fair!!', 12, 0, 100, 5, 0, 0, 0, 46269, 0, '');
INSERT INTO `creature_text` VALUES (46191, 11, 0, 'Why do I have to sit up top with all the stuff?!', 12, 0, 100, 15, 0, 0, 0, 46270, 0, '');
INSERT INTO `creature_text` VALUES (46192, 4, 0, 'Because you\'re the bravest, dear.', 12, 0, 100, 1, 0, 0, 0, 46271, 0, '');
INSERT INTO `creature_text` VALUES (46193, 13, 0, 'Because you\'re short.', 12, 0, 100, 1, 0, 0, 0, 46272, 0, '');
INSERT INTO `creature_text` VALUES (46191, 12, 0, 'Hey!  I oughtta bounce one of these trunks off of your head, Tarenar.', 12, 0, 100, 1, 0, 0, 0, 46273, 0, '');
INSERT INTO `creature_text` VALUES (46192, 5, 0, 'I know I promised you the front seat, Giddy, but we have an elderly guest.  I\'m sorry.', 12, 0, 100, 1, 0, 0, 0, 46274, 0, '');
INSERT INTO `creature_text` VALUES (46193, 14, 0, 'Fiona, I must warn you: Gidwin only wants to sit in front because he wants to kiss you.', 12, 0, 100, 1, 0, 0, 0, 46275, 0, '');
INSERT INTO `creature_text` VALUES (46191, 13, 0, 'Oh shut up, you dolt.  YOU probably want to kiss her a hundred times.', 12, 0, 100, 1, 0, 0, 0, 46276, 0, '');
INSERT INTO `creature_text` VALUES (46193, 15, 0, 'Do not!', 14, 0, 100, 25, 0, 0, 0, 46277, 0, '');
INSERT INTO `creature_text` VALUES (46194, 0, 0, 'Ahem!  I, ah, appreciate the ride, Madam Fiona.  It\'s nice to get off my feet for once.', 12, 0, 100, 1, 0, 0, 0, 46278, 0, '');
INSERT INTO `creature_text` VALUES (46192, 6, 0, 'Do you normally walk these roads alone?', 12, 0, 100, 6, 0, 0, 0, 46279, 0, '');
INSERT INTO `creature_text` VALUES (46194, 1, 0, 'Yes, I do, though it was easier when I was younger.  These days, I have to be more careful.', 12, 0, 100, 1, 0, 0, 0, 46280, 0, '');
INSERT INTO `creature_text` VALUES (46191, 14, 0, 'If you don\'t mind my askin\', what do you do that requires walking so much?', 12, 0, 100, 6, 0, 0, 0, 46281, 0, '');
INSERT INTO `creature_text` VALUES (46194, 2, 0, 'I service the five Argent towers.  Mostly repairs and maintenance, and some minor magical work.', 12, 0, 100, 1, 0, 0, 0, 46282, 0, '');
INSERT INTO `creature_text` VALUES (46194, 3, 0, 'Without proper upkeep, buildings within the plaguelands tend to... waste away.', 12, 0, 100, 1, 0, 0, 0, 46283, 0, '');
INSERT INTO `creature_text` VALUES (46193, 16, 0, 'Noble work.', 12, 0, 100, 273, 0, 0, 0, 46284, 0, '');
INSERT INTO `creature_text` VALUES (46194, 4, 0, 'I find it satisfying.  I enjoy working with my hands.', 12, 0, 100, 1, 0, 0, 0, 46285, 0, '');
INSERT INTO `creature_text` VALUES (46194, 5, 0, 'By the way, if any of you need anything repaired, let me know.  I am at your service.', 12, 0, 100, 1, 0, 0, 0, 46286, 0, '');
INSERT INTO `creature_text` VALUES (46191, 15, 0, 'So you\'re part of the Argent Crusade, eh?', 12, 0, 100, 6, 0, 0, 0, 46287, 0, '');
INSERT INTO `creature_text` VALUES (46191, 16, 0, 'Think you can put in the good word for us?  We\'re looking to join the order.', 12, 0, 100, 1, 0, 0, 0, 46288, 0, '');
INSERT INTO `creature_text` VALUES (46194, 6, 0, 'Certainly.  I don\'t make it out to Light\'s Hope very often anymore, but when you arrive, tell Max Tyrosus that I sent you.', 12, 0, 100, 1, 0, 0, 0, 46289, 0, '');
INSERT INTO `creature_text` VALUES (46193, 17, 0, 'Whoah... he lets you call him Max?', 12, 0, 100, 1, 0, 0, 0, 46290, 0, '');
INSERT INTO `creature_text` VALUES (46194, 7, 0, 'Ma\'am, would you mind dropping me off at Light\'s Shield Tower, just up ahead?  I have to stop here.', 12, 0, 100, 1, 0, 0, 0, 46291, 0, '');
INSERT INTO `creature_text` VALUES (46192, 7, 0, 'Of course I\'m not just going to drop you off, Master Highbeacon.  We\'ll be happy to wait for you.', 12, 0, 100, 1, 0, 0, 0, 46292, 0, '');
INSERT INTO `creature_text` VALUES (46191, 17, 0, 'NOOOOOOO!  Stopped again!', 12, 0, 100, 15, 0, 0, 0, 46293, 0, '');
INSERT INTO `creature_text` VALUES (46194, 8, 0, 'Light\'s Hope isn\'t far, young paladin.  Might I suggest walking?', 12, 0, 100, 1, 0, 0, 0, 46294, 0, '');
INSERT INTO `creature_text` VALUES (46191, 18, 0, 'Walking, eh...?', 12, 0, 100, 1, 0, 0, 0, 46295, 0, '');
INSERT INTO `creature_text` VALUES (43245, 0, 0, 'Jin\'do the Hexxer!  It\'s been a while since I\'ve seen that mask.', 12, 0, 100, 1, 0, 0, 0, 43362, 0, '');
INSERT INTO `creature_text` VALUES (43245, 1, 0, 'What brings you out to my part of Stranglethorn?  I\'m not accustomed to taking visitors from the esteemed Zul\'Gurub.', 12, 0, 100, 1, 0, 0, 0, 43363, 0, '');
INSERT INTO `creature_text` VALUES (43246, 0, 0, 'You know well enough about why I\'m here, Zanzil.', 12, 0, 100, 1, 0, 0, 0, 43364, 0, '');
INSERT INTO `creature_text` VALUES (43246, 1, 0, 'The Gurubashi have fallen on hard times.  I live, and Mandokir has returned... but the rest of us are gone.', 12, 0, 100, 1, 0, 0, 0, 43365, 0, '');
INSERT INTO `creature_text` VALUES (43245, 2, 0, 'What a shame... but not a concern for an old exile like Zanzil.', 12, 0, 100, 1, 0, 0, 0, 43366, 0, '');
INSERT INTO `creature_text` VALUES (43246, 2, 0, 'Well, Zanzil, I\'d like to make it your concern.  I\'ve recovered the bodies of High Priestess Jeklik and High Priest Venoxis.', 12, 0, 100, 1, 0, 0, 0, 43367, 0, '');
INSERT INTO `creature_text` VALUES (43246, 3, 0, 'I remember what you can do with those elixirs of yours.  Can your elixirs help out an old friend?', 12, 0, 100, 1, 0, 0, 0, 43368, 0, '');
INSERT INTO `creature_text` VALUES (43245, 3, 0, 'Friend?  You were among those who spit and threw stones, Jin\'do.  But let\'s let bygones be bygones, eh?', 12, 0, 100, 1, 0, 0, 0, 43369, 0, '');
INSERT INTO `creature_text` VALUES (43245, 4, 0, 'So your bat and your snake need Zanzil\'s help?  Very well.  I\'ll see you inside Zul\'Gurub... old friend.', 12, 0, 100, 1, 0, 0, 0, 43370, 0, '');
INSERT INTO `creature_text` VALUES (1449, 0, 0, 'Get ready everyone!  Here come de apes!', 14, 0, 100, 5, 0, 0, 0, 43550, 0, '');
INSERT INTO `creature_text` VALUES (71788, 0, 0, '|TInterface\\Icons\\Ability_Siege_Engineer_Detonate.blp:20|tA Crawler Mine has targeted you!', 42, 0, 100, 0, 0, 0, 0, 74801, 0, '');
INSERT INTO `creature_text` VALUES (48397, 0, 0, 'Ugh, the lok\'tar must have been cooking up a turd - which would explain the smell.', 12, 0, 100, 0, 0, 0, 0, 48625, 0, '');
INSERT INTO `creature_text` VALUES (48397, 1, 0, 'By Hellscream\'s axe!', 12, 0, 100, 5, 0, 0, 0, 48627, 0, '');
INSERT INTO `creature_text` VALUES (48403, 0, 0, 'Oy, \'Linda, where are we meetin\' up with this Ivar Bloodfang fella? An\' what in Bronzebeard\'s name is that terrible smell?', 12, 0, 100, 1, 0, 0, 0, 48635, 0, '');
INSERT INTO `creature_text` VALUES (48407, 0, 0, 'They are massing at Purgation Isle, just west of us here, off the coast. We are to meet them there, general.', 12, 0, 100, 1, 0, 0, 0, 48639, 0, '');
INSERT INTO `creature_text` VALUES (48407, 1, 0, 'As for the smell... *Balinda sniffs at the air* I think it\'s coming from over there...', 12, 0, 100, 1, 0, 0, 0, 48640, 0, '');
INSERT INTO `creature_text` VALUES (48403, 1, 0, 'Look there, \'Linda! What in the...', 12, 0, 100, 0, 0, 0, 0, 48641, 0, '');
INSERT INTO `creature_text` VALUES (48397, 2, 0, 'You face Kingslayer Orkus now, dwarf!', 12, 0, 100, 5, 0, 0, 0, 48643, 0, '');
INSERT INTO `creature_text` VALUES (48397, 3, 0, 'KASHA! Your master needs you!', 14, 0, 100, 22, 0, 0, 0, 48644, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 0, 'Yoo hoo!  I am here to slay Sylvanas!', 12, 0, 100, 0, 0, 0, 0, 52154, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 1, 'Hey, look, there are people here.  Nothing at all like that tree city that I, a night elf, live in.', 12, 0, 100, 0, 0, 0, 0, 52155, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 2, 'I\'m so angry that we lost Wintergrasp, again!  I will try to kill your leader, the all powerful, Sylvanas, instead.', 12, 0, 100, 0, 0, 0, 0, 52156, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 3, 'Our loss in Tol Barad will be avenged!  I will try to bring death to this grand city.  Cower as I try to remember a battle cry!', 12, 0, 100, 0, 0, 0, 0, 52157, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 4, 'Tremble in fear you mighty members of the Horde, the Alliance is here!', 12, 0, 100, 0, 0, 0, 0, 52158, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 5, 'Where am I?  I crashed with the Exodar and now stumbled into this imposing city...', 12, 0, 100, 0, 0, 0, 0, 52159, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 6, 'This city is so big, to me, a tiny, pathetic gnome.', 12, 0, 100, 0, 0, 0, 0, 52160, 0, '');
INSERT INTO `creature_text` VALUES (53590, 0, 7, 'I am drunk, like many of my fellow dwarves.  I\'ve dug a tunnel and wound up here.  Who wants to drink?', 12, 0, 100, 0, 0, 0, 0, 52161, 0, '');
INSERT INTO `creature_text` VALUES (42671, 0, 1, 'This baby is all mine! I call her, \"The Throttler.\"', 12, 0, 100, 0, 0, 0, 0, 42556, 0, '');
INSERT INTO `creature_text` VALUES (42671, 0, 2, 'Ready for inspection! ...Be gentle.', 12, 0, 100, 0, 0, 0, 0, 42557, 0, '');
INSERT INTO `creature_text` VALUES (46986, 0, 0, 'Some have spent decades meditating upon this mystery, Avuun. I do not wish the same fate upon you.', 12, 0, 100, 0, 0, 0, 0, 47041, 0, '');
INSERT INTO `creature_text` VALUES (46986, 1, 0, 'The Light wishes suffering on none, my child. But it does not reign unopposed in our realm.', 12, 0, 100, 396, 0, 0, 0, 47042, 0, '');
INSERT INTO `creature_text` VALUES (46986, 2, 0, 'Even now, the true battle between the forces of Light and Darkness approaches. We will all be called to join, and in the face of this conflict, all mortal suffering will be meaningless.', 12, 0, 100, 396, 0, 0, 0, 47043, 0, '');
INSERT INTO `creature_text` VALUES (46986, 3, 0, 'Magtoor, you have endured your plight bravely. Are you ready to return to the embrace of the Light?', 12, 0, 100, 396, 0, 0, 0, 47044, 0, '');
INSERT INTO `creature_text` VALUES (46986, 4, 0, 'Then may the Light save you and raise you up.', 12, 0, 100, 0, 0, 0, 0, 47045, 0, '');
INSERT INTO `creature_text` VALUES (46986, 5, 0, 'Remember, Avuun. Not all who wander are lost.', 12, 0, 100, 396, 0, 0, 0, 47046, 0, '');
INSERT INTO `creature_text` VALUES (47001, 0, 0, 'Even now, nothing. Why? Why would the Light forsake the innocent?', 12, 0, 100, 6, 0, 0, 0, 47047, 0, '');
INSERT INTO `creature_text` VALUES (47001, 1, 0, 'Prophet Velen!', 12, 0, 100, 5, 0, 0, 0, 47048, 0, '');
INSERT INTO `creature_text` VALUES (47001, 2, 0, '...thank you, Prophet. You have given both of us great comfort. I will continue my work, in faith and memory of Magtoor.', 12, 0, 100, 273, 0, 0, 0, 47049, 0, '');
INSERT INTO `creature_text` VALUES (1776, 0, 0, 'Yes...', 12, 0, 100, 0, 0, 0, 0, 47051, 0, '');
INSERT INTO `creature_text` VALUES (48752, 0, 0, 'Begone infidels, you are not welcome here!', 12, 0, 100, 0, 0, 19735, 0, 44791, 0, '');
INSERT INTO `creature_text` VALUES (48752, 0, 1, 'Death is only the beginning!', 12, 0, 100, 0, 0, 19733, 0, 44792, 0, '');
INSERT INTO `creature_text` VALUES (48752, 0, 2, 'May peace find you now.', 12, 0, 100, 0, 0, 19738, 0, 44793, 0, '');
INSERT INTO `creature_text` VALUES (48752, 0, 3, 'The heavens take you!', 12, 0, 100, 0, 0, 19736, 0, 44794, 0, '');
INSERT INTO `creature_text` VALUES (62491, 10, 0, 'Search the island to collect brew for Bo\'s cauldron! But be sure to leave one person nearby to defend Bo!', 41, 0, 100, 1, 0, 0, 0, 65966, 2, '');
INSERT INTO `creature_text` VALUES (62491, 11, 0, 'You can use the bombs and cannons nearby to blast the hozen away!', 41, 0, 100, 5, 0, 0, 0, 65666, 2, '');
INSERT INTO `creature_text` VALUES (62491, 12, 0, 'A hozen bandit is stealing the brew! Stop him!', 41, 0, 100, 1, 0, 0, 0, 65572, 2, '');
INSERT INTO `creature_text` VALUES (62491, 13, 0, 'Hozen ships appear in the distance!', 41, 0, 100, 5, 0, 0, 0, 62210, 2, '');
INSERT INTO `creature_text` VALUES (62491, 14, 0, 'Captain Ook is comin\' down with the scurvy! Keep the orange away from him!', 41, 0, 100, 0, 0, 0, 0, 61859, 2, '');
INSERT INTO `creature_text` VALUES (62491, 15, 0, '$n has got the orange!', 41, 0, 100, 0, 0, 0, 0, 61879, 2, '');
INSERT INTO `creature_text` VALUES (62491, 16, 0, '$n tossed the orange!', 41, 0, 100, 0, 0, 0, 0, 61881, 2, '');
INSERT INTO `creature_text` VALUES (62491, 17, 0, 'You are carrying too much brew. Return to the Brewing Cauldron to turn in what you have.', 42, 0, 100, 0, 0, 0, 0, 67595, 2, '');
INSERT INTO `creature_text` VALUES (70605, 0, 0, 'Newt, get that door open.', 12, 0, 100, 0, 0, 0, 0, 73081, 2, '');
INSERT INTO `creature_text` VALUES (70606, 0, 0, 'Yes, sir!', 12, 0, 100, 0, 0, 0, 0, 3690, 2, '');
INSERT INTO `creature_text` VALUES (70605, 1, 0, 'Grit, Ticker and Volt, you three back her up.', 12, 0, 100, 0, 0, 0, 0, 73087, 2, '');
INSERT INTO `creature_text` VALUES (70609, 0, 0, 'On it.', 12, 0, 100, 0, 0, 0, 0, 36710, 2, '');
INSERT INTO `creature_text` VALUES (70605, 2, 0, 'Recruit $p, you\'re with me.', 12, 0, 100, 0, 0, 0, 0, 73082, 2, '');
INSERT INTO `creature_text` VALUES (70605, 3, 0, 'We\'ve tracked the recent disappearances in Durotar to this abandoned section of Ragefire.', 12, 0, 100, 0, 0, 0, 0, 73083, 2, '');
INSERT INTO `creature_text` VALUES (70605, 4, 0, 'It\'s our job to find out who\'s behind these abductions and put a stop to them.', 12, 0, 100, 0, 0, 0, 0, 73086, 2, '');
INSERT INTO `creature_text` VALUES (70605, 5, 0, 'Stay close.', 12, 0, 100, 0, 0, 0, 0, 70754, 2, '');
INSERT INTO `creature_text` VALUES (70605, 6, 0, 'Newt, we need this door open now.', 12, 0, 100, 0, 0, 0, 0, 73089, 2, '');
INSERT INTO `creature_text` VALUES (70606, 1, 0, 'I\'m setting the last charge now, sir!', 12, 0, 100, 0, 0, 0, 0, 73090, 2, '');
INSERT INTO `creature_text` VALUES (70606, 2, 0, 'The charges are all set; hit the detonator as soon as you\'re ready!', 12, 0, 100, 0, 0, 0, 0, 73091, 2, '');
INSERT INTO `creature_text` VALUES (70605, 7, 0, 'Move! Move! Move!', 14, 0, 100, 0, 0, 0, 0, 73092, 2, '');
INSERT INTO `creature_text` VALUES (70605, 8, 0, 'What the...', 14, 0, 100, 0, 0, 0, 0, 7321, 2, '');
INSERT INTO `creature_text` VALUES (70683, 0, 0, 'Intruders! Stop them!', 14, 0, 100, 0, 0, 0, 0, 73107, 2, '');
INSERT INTO `creature_text` VALUES (70609, 1, 0, 'Sir, these Kor\'kron don\'t seem very friendly!', 14, 0, 100, 0, 0, 0, 0, 73181, 2, '');
INSERT INTO `creature_text` VALUES (70605, 9, 0, 'Gob Squad, defend yourselves!', 14, 0, 100, 0, 0, 0, 0, 73707, 2, '');
INSERT INTO `creature_text` VALUES (70683, 1, 0, 'Weaklings!', 14, 0, 100, 0, 0, 38177, 0, 73108, 2, '');
INSERT INTO `creature_text` VALUES (70683, 2, 0, 'Seal the gate, and inform the overseer immediately.', 14, 0, 100, 0, 0, 0, 0, 73429, 2, '');
INSERT INTO `creature_text` VALUES (70605, 10, 0, 'We aren\'t your enemy, Xorenth!', 14, 0, 100, 0, 0, 0, 0, 73447, 2, '');
INSERT INTO `creature_text` VALUES (70683, 3, 0, 'All lesser races are enemies of the true Horde!', 14, 0, 100, 0, 0, 0, 0, 73430, 2, '');
INSERT INTO `creature_text` VALUES (70605, 11, 0, 'Lesser? We\'ll show you who\'s lesser!', 14, 0, 100, 0, 0, 0, 0, 73712, 2, '');
INSERT INTO `creature_text` VALUES (70683, 4, 0, '|TInterface\\Icons\\ability_deathknight_remorselesswinters:20|t%s summons a |cFFF00000|Hspell:142320|h[Glacial Freeze Totem]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73914, 2, '');
INSERT INTO `creature_text` VALUES (70683, 5, 0, '|TInterface\\Icons\\spell_fire_windsofwoe:20|t%s begins to cast |cFFF00000|Hspell:142306|h[Ruined Earth]|h|r!', 41, 0, 100, 0, 0, 0, 0, 73915, 2, '');
INSERT INTO `creature_text` VALUES (70608, 0, 0, 'Sir, these are Garrosh\'s men.', 12, 0, 100, 0, 0, 0, 0, 73440, 2, '');
INSERT INTO `creature_text` VALUES (70605, 12, 0, 'This is bad.', 12, 0, 100, 0, 0, 0, 0, 58008, 2, '');
INSERT INTO `creature_text` VALUES (70605, 13, 0, 'All of you fan out and search the chamber. If Garrosh is behind this, we\'re going to need proof.', 12, 0, 100, 0, 0, 0, 0, 73446, 2, '');
INSERT INTO `creature_text` VALUES (70606, 3, 0, 'These are from the dig site in the Vale of Eternal Blossoms. What is Garrosh trying to find there?', 12, 0, 100, 0, 0, 0, 0, 73450, 2, '');
INSERT INTO `creature_text` VALUES (70607, 0, 0, 'Proto-drake eggs... is Garrosh working with the Dragonmaw? Orcs on proto-drakes sounds bad to me, $p. Very bad.', 12, 0, 100, 0, 0, 0, 0, 73449, 2, '');
INSERT INTO `creature_text` VALUES (70605, 14, 0, 'Quickly, $p! We need you over here!', 14, 0, 100, 0, 0, 0, 0, 73451, 2, '');
INSERT INTO `creature_text` VALUES (71030, 0, 0, 'Proto-Drake Whelps hatch from the nearby eggs!', 41, 0, 100, 0, 0, 0, 0, 74028, 2, '');
INSERT INTO `creature_text` VALUES (70605, 15, 0, 'Can you open this, Ticker?', 12, 0, 100, 0, 0, 0, 0, 73495, 2, '');
INSERT INTO `creature_text` VALUES (70609, 2, 0, 'I\'ve got just the thing!', 12, 0, 100, 0, 0, 0, 0, 73497, 2, '');
INSERT INTO `creature_text` VALUES (70609, 3, 0, 'I\'ve been saving this for a special occasion.', 12, 0, 100, 0, 0, 0, 0, 73496, 2, '');
INSERT INTO `creature_text` VALUES (70605, 16, 0, 'A mantid bomb?! Are you sure that won\'t bring down the whole cavern?', 12, 0, 100, 0, 0, 0, 0, 73498, 2, '');
INSERT INTO `creature_text` VALUES (70609, 4, 0, 'We\'ll need to make a few modifications, but it will get the job done.', 12, 0, 100, 0, 0, 0, 0, 73499, 2, '');
INSERT INTO `creature_text` VALUES (70609, 5, 0, 'Recruit, bring me that pool pony, three cannon balls, two batteries, and some of that proto-drake egg yolk.', 12, 0, 100, 0, 0, 0, 0, 73500, 2, '');
INSERT INTO `creature_text` VALUES (70609, 6, 0, 'You carried that with your hands?! Uh... you should probably see a medic when we get back...', 14, 0, 100, 0, 0, 0, 0, 73630, 2, '');
INSERT INTO `creature_text` VALUES (70609, 7, 0, 'That\'s exactly what we needed!', 12, 0, 100, 0, 0, 0, 0, 73627, 2, '');
INSERT INTO `creature_text` VALUES (70609, 8, 0, 'The most important ingredient!', 12, 0, 100, 0, 0, 0, 0, 73639, 2, '');
INSERT INTO `creature_text` VALUES (70609, 9, 0, 'Careful! This isn\'t a toy!', 12, 0, 100, 0, 0, 0, 0, 73628, 2, '');
INSERT INTO `creature_text` VALUES (70609, 10, 0, 'Just a little more power...', 12, 0, 100, 0, 0, 0, 0, 73641, 2, '');
INSERT INTO `creature_text` VALUES (71030, 1, 0, 'Embercores wake from the lava!', 41, 0, 100, 0, 0, 0, 0, 74118, 2, '');
INSERT INTO `creature_text` VALUES (70609, 11, 0, 'This should give us enough power to disable the stabilization matrix.', 12, 0, 100, 0, 0, 0, 0, 73640, 2, '');
INSERT INTO `creature_text` VALUES (70609, 12, 0, 'My masterpiece is finished!', 14, 0, 100, 0, 0, 0, 0, 73643, 2, '');
INSERT INTO `creature_text` VALUES (70608, 1, 0, 'It\'s... it\'s beautiful!', 12, 0, 100, 0, 0, 0, 0, 73644, 2, '');
INSERT INTO `creature_text` VALUES (70606, 4, 0, 'What the heck is the pool pony for?', 12, 0, 100, 0, 0, 0, 0, 73645, 2, '');
INSERT INTO `creature_text` VALUES (70609, 13, 0, 'Don\'t question my genius!', 12, 0, 100, 0, 0, 0, 0, 73646, 2, '');
INSERT INTO `creature_text` VALUES (70605, 17, 0, 'I don\'t care what it\'s for! Is it going to get this door open?', 12, 0, 100, 0, 0, 0, 0, 73647, 2, '');
INSERT INTO `creature_text` VALUES (70609, 14, 0, 'This thing could open Gallywix\'s personal safe!', 12, 0, 100, 0, 0, 0, 0, 73649, 2, '');
INSERT INTO `creature_text` VALUES (70609, 15, 0, 'Once the detonator goes off in... err... NOW!', 12, 0, 100, 0, 0, 0, 0, 73650, 2, '');
INSERT INTO `creature_text` VALUES (70608, 2, 0, 'Help!', 14, 0, 100, 0, 0, 0, 0, 485, 2, '');
INSERT INTO `creature_text` VALUES (70607, 1, 0, 'Warn us a little sooner next time!', 14, 0, 100, 0, 0, 0, 0, 73669, 2, '');
INSERT INTO `creature_text` VALUES (70608, 3, 0, 'I\'m on fire!', 14, 0, 100, 0, 0, 0, 0, 37859, 2, '');
INSERT INTO `creature_text` VALUES (70607, 2, 0, 'Calm down! I\'d hardly call that a fire!', 14, 0, 100, 0, 0, 0, 0, 73673, 2, '');
INSERT INTO `creature_text` VALUES (70606, 5, 0, 'Ticker?! Wake up!', 14, 0, 100, 0, 0, 0, 0, 73667, 2, '');
INSERT INTO `creature_text` VALUES (70605, 18, 0, 'That door didn\'t even budge...', 12, 0, 100, 0, 0, 0, 0, 73668, 2, '');
INSERT INTO `creature_text` VALUES (70605, 19, 0, 'Grit, setup the emergency teleporter! We need to report this to Vol\'jin immediately.', 12, 0, 100, 0, 0, 0, 0, 73677, 2, '');
INSERT INTO `creature_text` VALUES (70607, 3, 0, 'Right away, Sir!', 12, 0, 100, 0, 0, 0, 0, 25794, 2, '');
INSERT INTO `creature_text` VALUES (71030, 2, 0, 'You will never escape these caverns!', 14, 0, 100, 0, 0, 0, 0, 73679, 2, '');
INSERT INTO `creature_text` VALUES (71030, 3, 0, 'The true Horde, the pure Horde, cannot be stopped!', 14, 0, 100, 0, 0, 0, 0, 73680, 2, '');
INSERT INTO `creature_text` VALUES (70605, 20, 0, 'Recruits, you\'re the only ones left that can fight!', 14, 0, 100, 0, 0, 0, 0, 73683, 2, '');
INSERT INTO `creature_text` VALUES (71030, 4, 0, 'Destroy them! FOR THE HORDE!', 14, 0, 100, 0, 0, 0, 0, 73682, 2, '');
INSERT INTO `creature_text` VALUES (71030, 5, 0, '|TInterface\\Icons\\inv_misc_pocketwatch_01:20|t Bonus Objective Started: Defeat all enemies before time runs out!', 41, 0, 100, 0, 0, 0, 0, 74193, 2, '');
INSERT INTO `creature_text` VALUES (70605, 21, 0, 'Defend us until the teleporter is ready!', 14, 0, 100, 0, 0, 0, 0, 73684, 2, '');
INSERT INTO `creature_text` VALUES (71030, 6, 0, 'Fools! The true Horde cannot be stopped by the likes of you.', 14, 0, 100, 0, 0, 0, 0, 73838, 2, '');
INSERT INTO `creature_text` VALUES (70607, 4, 0, 'The teleporter is ready! Let\'s get out of here!', 14, 0, 100, 0, 0, 0, 0, 73839, 2, '');
INSERT INTO `creature_text` VALUES (70605, 22, 0, 'Everyone out before more of them show up!', 14, 0, 100, 0, 0, 0, 0, 73878, 2, '');
INSERT INTO `creature_text` VALUES (70799, 0, 0, 'This is where my agents tracked the Zandalari.', 12, 0, 100, 25, 0, 37328, 0, 73193, 2, '');
INSERT INTO `creature_text` VALUES (70279, 0, 0, 'Shimmer Ridge. I know it well. The Frostmane trolls breed and plot atop that snow-blown mesa.', 12, 0, 100, 273, 0, 37190, 0, 73200, 2, '');
INSERT INTO `creature_text` VALUES (70799, 1, 0, 'I can only imagine what foul business the Zandalari have with them...', 12, 0, 100, 1, 0, 37329, 0, 73194, 2, '');
INSERT INTO `creature_text` VALUES (70279, 1, 0, 'We\'ve lost many mountaineers to this freakish storm, or the trolls slithering about in it.', 12, 0, 100, 1, 0, 37191, 0, 73201, 2, '');
INSERT INTO `creature_text` VALUES (70799, 2, 0, 'Yes. This weather is not natural.', 12, 0, 100, 1, 0, 37330, 0, 73195, 2, '');
INSERT INTO `creature_text` VALUES (70279, 2, 0, 'Aye. I am loath to send my men into that. It reeks of ambush and bloody disaster.', 12, 0, 100, 273, 0, 37192, 0, 73202, 2, '');
INSERT INTO `creature_text` VALUES (70799, 3, 0, 'My champions will go. They will see what can be done, and rescue who they can.', 12, 0, 100, 1, 0, 37331, 0, 73196, 2, '');
INSERT INTO `creature_text` VALUES (70279, 3, 0, 'If they can stop that storm, I\'ll lead the Forgeguard to the top myself.', 12, 0, 100, 5, 0, 37193, 0, 73203, 2, '');
INSERT INTO `creature_text` VALUES (70799, 4, 0, 'It is heartening to see you here, Moira. You have my thanks.', 12, 0, 100, 2, 0, 37332, 0, 73197, 2, '');
INSERT INTO `creature_text` VALUES (70279, 4, 0, 'Indeed. With the aid of your champions, the Dark Irons will put an end to the Zandalari threat in Dun Morogh.', 12, 0, 100, 1, 0, 37195, 0, 73205, 2, '');
INSERT INTO `creature_text` VALUES (70279, 5, 0, 'We will prove our loyalty - to the Council, Ironforge, and the Alliance - here, today, and with our own blood.', 12, 0, 100, 5, 0, 0, 0, 73211, 2, '');
INSERT INTO `creature_text` VALUES (70799, 5, 0, 'Hrmmm. Fare well, Queen Regent.', 12, 0, 100, 66, 0, 37333, 0, 73199, 2, '');
INSERT INTO `creature_text` VALUES (70279, 6, 0, 'I\'ll see you when it\'s done, King of Stormwind.', 12, 0, 100, 2, 0, 37196, 0, 73206, 2, '');
INSERT INTO `creature_text` VALUES (70801, 0, 0, 'The nearest is just ahead. If ye can save him, he may know a thing or two.', 12, 0, 100, 0, 0, 37263, 0, 73219, 2, '');
INSERT INTO `creature_text` VALUES (70801, 1, 0, 'We\'ll cover ye from the brush: Point out a target, and we\'ll open fire.', 12, 0, 100, 396, 0, 0, 0, 74081, 2, '');
INSERT INTO `creature_text` VALUES (70602, 0, 0, 'Oh ho ho ho ho! By my beard, I thought I was done for! Oh, thank ye. Thank ye!', 14, 0, 100, 4, 0, 37296, 0, 73249, 2, '');
INSERT INTO `creature_text` VALUES (70602, 1, 0, 'Oh... right. Now: Did the human king send ye? Oh, his spies are all dead... and most of us are, too.', 12, 0, 100, 6, 0, 37297, 0, 73250, 2, '');
INSERT INTO `creature_text` VALUES (70602, 2, 0, 'I saw \'em draggin\' Boldbrew, kickin\' and bitin\', towards some hut south o\' here. Now if ye hurry, ye can help her, and she might know more.', 12, 0, 100, 25, 0, 0, 0, 73251, 2, '');
INSERT INTO `creature_text` VALUES (70602, 3, 0, 'BUT STAY AWAY FROM THE  BIG VILLAGE! There\'s dozens o\' mad ice trolls in there! Oh, that Zandalari priest has \'em whipped up into a great frenzy.', 12, 0, 100, 5, 0, 0, 0, 73252, 2, '');
INSERT INTO `creature_text` VALUES (70744, 0, 0, 'Oh, that was a CLOSE one, thank ye!', 14, 0, 100, 71, 0, 37292, 0, 73254, 2, '');
INSERT INTO `creature_text` VALUES (70744, 1, 0, 'There\'s only a few of us left; the ones they don\'t eat, they butcher at some big altar.', 12, 0, 100, 396, 0, 37293, 0, 73255, 2, '');
INSERT INTO `creature_text` VALUES (70744, 2, 0, 'It\'s just northwest o\' here; if ye hurry, ye might be able ta save some! Sorry I don\'t know anythin\' more...', 12, 0, 100, 397, 0, 0, 0, 73256, 2, '');
INSERT INTO `creature_text` VALUES (70745, 0, 0, 'Filthy monsters! I owe ye me life.', 14, 0, 100, 4, 0, 37294, 0, 73274, 2, '');
INSERT INTO `creature_text` VALUES (70745, 1, 0, 'Listen: The storm. We figured out what\'s makin\' it. That sneaky Zandalari has these ice trolls makin\' blood sacrifices ta elemental spirits.', 12, 0, 100, 1, 0, 37295, 0, 73275, 2, '');
INSERT INTO `creature_text` VALUES (70745, 2, 0, 'There\'s a cave, north o\' here, across a bridge. If ye can slay the elemental within, the storm ought ta stop.', 12, 0, 100, 25, 0, 0, 0, 73276, 2, '');
INSERT INTO `creature_text` VALUES (70474, 0, 0, '$n has been |cFFFF0000|Hspell:141407|h[Frozen Solid]|h|r. Break $ghim:her; free!', 41, 0, 100, 0, 0, 0, 0, 73289, 2, '');
INSERT INTO `creature_text` VALUES (70279, 7, 0, 'The king\'s champions have done it: The storm has ended! Forward, Dark Irons! TO BATTLE WE MARCH!', 14, 0, 100, 397, 0, 37197, 0, 73458, 2, '');
INSERT INTO `creature_text` VALUES (70544, 0, 0, 'You may have stopped the storm, but you cannot stop the will of Zandalar! You will DIE, here... ALL OF YOU!', 14, 0, 100, 0, 0, 37173, 0, 73452, 2, '');
INSERT INTO `creature_text` VALUES (70544, 1, 0, 'Trolls of the world will unite against you!', 14, 0, 100, 0, 0, 37174, 0, 73454, 2, '');
INSERT INTO `creature_text` VALUES (70544, 2, 0, 'We control the sand, the seas, the winds...', 14, 0, 100, 0, 0, 37175, 0, 73455, 2, '');
INSERT INTO `creature_text` VALUES (70544, 3, 0, 'We will bury you in your mountain!', 14, 0, 100, 0, 0, 37176, 0, 73456, 2, '');
INSERT INTO `creature_text` VALUES (70544, 4, 0, 'There... will be more! Zandalar... will never fall...', 14, 0, 100, 0, 0, 37172, 0, 73457, 2, '');
INSERT INTO `creature_text` VALUES (70799, 6, 0, 'Well fought. I see the Dark Irons are as formidable as ever.', 12, 0, 100, 1, 0, 37334, 0, 73461, 2, '');
INSERT INTO `creature_text` VALUES (70279, 8, 0, 'That they are, King Varian. And at the service of the Alliance, I might add.', 12, 0, 100, 273, 0, 37198, 0, 73464, 2, '');
INSERT INTO `creature_text` VALUES (70799, 7, 0, 'Hah! For that, I\'m glad. The Bronzebeards and Wildhammers will regret not having been here to share in your glory.', 12, 0, 100, 1, 0, 37335, 0, 73462, 2, '');
INSERT INTO `creature_text` VALUES (70279, 9, 0, 'Better they\'re grateful for the clansmen spared death on this miserable hillock. We sought to protect and impress, not to shame.', 12, 0, 100, 274, 0, 37199, 0, 73465, 2, '');
INSERT INTO `creature_text` VALUES (70799, 8, 0, 'Well, you\'ve done just that. Come; let us return to Ironforge, and announce your victory to the Council.', 12, 0, 100, 1, 0, 37336, 0, 73463, 2, '');
INSERT INTO `creature_text` VALUES (17878, 0, 0, 'I do not fear death at your hands, $r!', 14, 0, 100, 0, 0, 0, 0, 45874, 0, '');
INSERT INTO `creature_text` VALUES (17878, 0, 1, 'I will tell you nothing, $r-pig!', 14, 0, 100, 0, 0, 0, 0, 45873, 0, '');
INSERT INTO `creature_text` VALUES (17878, 0, 2, 'She will never return the dwarf to you!', 14, 0, 100, 0, 0, 0, 0, 45878, 0, '');
INSERT INTO `creature_text` VALUES (17878, 0, 3, 'The dwarf belongs to our kind now!  Not yours!', 14, 0, 100, 0, 0, 0, 0, 45875, 0, '');
INSERT INTO `creature_text` VALUES (17878, 1, 0, 'The Plaguewood is too big!  Your friend is gone!  You will never find him!', 14, 0, 100, 0, 0, 0, 0, 45877, 0, '');
INSERT INTO `creature_text` VALUES (17878, 2, 0, 'Foolish $r!  You seek the dwarf?  Baroness Anastari has hidden him well!', 14, 0, 100, 0, 0, 0, 0, 45879, 0, '');
INSERT INTO `creature_text` VALUES (37503, 0, 0, 'You must not approach the Frost Queen. Quickly, stop them!', 14, 0, 100, 0, 0, 17167, 0, 37105, 0, '');
