INSERT INTO `creature_text` VALUES (71436, 1, 0, 'Victory is ours!  Get in the cannon and press the attack!', 14, 0, 100, 0, 0, 0, 0, 74129, 2, '');
INSERT INTO `creature_text` VALUES (71445, 0, 0, 'Help us fend off these Alliance dogs!', 14, 0, 100, 0, 0, 0, 0, 74182, 2, '');
INSERT INTO `creature_text` VALUES (71445, 1, 0, 'More are swinging over.  It looks like this is the last of \'em!', 14, 0, 100, 0, 0, 0, 0, 74183, 2, '');
INSERT INTO `creature_text` VALUES (71445, 2, 0, 'We\'ve pushed them back to their ship.  Swing over and steal their explosives to blow it to smithereens!', 14, 0, 100, 0, 0, 0, 0, 73948, 2, '');
