INSERT INTO `creature_text` VALUES (71445, 3, 0, 'That\'s the last of them!  Get to the bottom of the ship and plant those explosives!', 14, 0, 100, 0, 0, 0, 0, 74135, 2, '');
INSERT INTO `creature_text` VALUES (71445, 4, 0, 'The ship is about to explode.  Run to the top deck and swing away!', 41, 0, 100, 0, 0, 0, 0, 68909, 2, '');
