INSERT INTO `creature_text` VALUES (71543, 3, 0, 'A soft hum ceases as the corruption creating new Puddles ebbs away with the death of the final Aqueous Defender.', 16, 0, 100, 0, 0, 0, 0, 77144, 2, '');
INSERT INTO `creature_text` VALUES (67260, 14, 0, 'Also entering the arena: Kirrawk, wind-wielding arakkoa of the Terokkar Forest!', 14, 0, 100, 22, 0, 0, 0, 69949, 0, '');
