INSERT INTO `creature_text` VALUES (67260, 4, 0, 'Now entering the arena, we have a very talented $c. Say hello to $n, a Rank 5 brawler!', 14, 0, 100, 22, 0, 0, 0, 68891, 0, '');
INSERT INTO `creature_text` VALUES (67260, 5, 0, 'Look out... here comes the deadly Rank 6 $r, $n!', 14, 0, 100, 22, 0, 0, 0, 68892, 0, '');
