INSERT INTO `creature_text` VALUES (67260, 10, 0, 'In the other corner, we have Vian, the flame-flinging follower of the Thunder King!', 14, 0, 100, 22, 0, 0, 0, 69937, 0, '');
INSERT INTO `creature_text` VALUES (63307, 4, 0, 'We Pandaren do not worship our ancestors, but we revere their memory and the wisdom that they have passed on to us.', 12, 0, 100, 396, 0, 31776, 0, 62760, 0, '');
INSERT INTO `creature_text` VALUES (63307, 3, 0, 'Ah, this is a special place. My great grandparents called this part of the forest their home. Come, this will take only a moment.', 12, 0, 100, 396, 0, 31774, 0, 62731, 0, '');
INSERT INTO `creature_text` VALUES (70126, 0, 2, 'I won a Brawler\'s Guild invitation just now! I\'ll surely be rank 8 by tonight!  I sure hope I don\'t lose my ticket before I get there.', 12, 0, 100, 0, 0, 0, 0, 72443, 0, '');
INSERT INTO `creature_text` VALUES (70126, 0, 1, 'Everyone look! I won a Brawler\'s Guild invite! I can\'t wait to try it out!', 12, 0, 100, 0, 0, 0, 0, 72442, 0, '');
INSERT INTO `creature_text` VALUES (70126, 0, 0, 'YES! Finally! I won a Brawler\'s Guild invite! Nobody can stop me now!', 12, 0, 100, 0, 0, 0, 0, 72440, 0, '');
