INSERT INTO `creature_text` VALUES (45006, 0, 0, 'I hope you have a strong stomach.  Travelling through the breach is not for the weak.', 12, 0, 100, 0, 0, 22606, 0, 45169, 0, '');
INSERT INTO `creature_text` VALUES (45006, 1, 0, 'We\'re at the center of the Maelstrom, the rift between Deepholm and Azeroth.  Watch as it threatens to tear your world apart!', 12, 0, 100, 0, 0, 22607, 0, 45170, 0, '');
INSERT INTO `creature_text` VALUES (45006, 2, 0, 'Let us hope Thrall and the others can contain it while you\'re inside.', 12, 0, 100, 0, 0, 22608, 0, 45171, 0, '');
INSERT INTO `creature_text` VALUES (45006, 3, 0, 'This is it!  Brace yourself!', 14, 0, 100, 0, 0, 22609, 0, 45172, 0, '');
INSERT INTO `creature_text` VALUES (45006, 4, 0, 'You made it!  Good!', 12, 0, 100, 4, 0, 22610, 0, 45175, 0, '');
INSERT INTO `creature_text` VALUES (45006, 5, 0, 'This is the Temple of Earth.  It\'s the most important structure in Deepholm.', 12, 0, 100, 0, 0, 22611, 0, 45176, 0, '');
INSERT INTO `creature_text` VALUES (45006, 6, 0, 'There was once a Titan-made stone inside.  It was known as the World Pillar.  The name was literal.  It bore the weight of all the elemental and magnetic forces in Deepholm.', 12, 0, 100, 0, 0, 22612, 0, 45180, 0, '');
INSERT INTO `creature_text` VALUES (45006, 7, 0, 'It was shattered when Deathwing broke through the dome above and created the rift between the two worlds.  All of Deepholm is going to collapse into Azeroth if we do not put the World Pillar back together.', 12, 0, 100, 0, 0, 22613, 0, 45182, 0, '');
INSERT INTO `creature_text` VALUES (45006, 8, 0, 'The first fragment was going to be delivered to us by a Horde gunship known as the Storm\'s Fury.  It never made it here.', 12, 0, 100, 0, 0, 22614, 0, 45186, 0, '');
INSERT INTO `creature_text` VALUES (45006, 9, 0, 'We\'ve taken control of the temple and we\'re containing as much of the damage as we can.  Go inside and speak to Maruut; he\'s in charge.', 12, 0, 100, 0, 0, 22615, 0, 45187, 0, '');
INSERT INTO `creature_text` VALUES (45006, 10, 0, 'We are all counting on you.', 12, 0, 100, 0, 0, 22616, 0, 45191, 0, '');
