INSERT INTO `creature_text` VALUES (35369, 0, 0, 'What\'s wrong with me??!', 14, 0, 100, 0, 0, 0, 0, 38736, 0, 'Josiah Avery');
INSERT INTO `creature_text` VALUES (35369, 1, 0, 'My face!  What\'s wrong with my face!', 14, 0, 100, 0, 0, 0, 0, 38737, 0, 'Josiah Avery');
