INSERT INTO `creature_text` VALUES (35369, 3, 0, 'The pain is unbearable!', 14, 0, 100, 0, 0, 0, 0, 9681, 0, 'Josiah Avery');
INSERT INTO `creature_text` VALUES (35369, 4, 0, 'Make it stop!', 14, 0, 100, 0, 0, 0, 0, 38740, 0, 'Josiah Avery');
INSERT INTO `creature_text` VALUES (35369, 5, 0, 'I can\'t fight it!', 14, 0, 100, 0, 0, 0, 0, 38741, 0, 'Josiah Avery');
INSERT INTO `creature_text` VALUES (64881, 0, 0, 'Why can\'t I understand any of the words?', 12, 0, 100, 1, 0, 0, 0, 65631, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 1, 0, 'We live on the back of a big turtle?!', 12, 0, 100, 5, 0, 0, 0, 65637, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 2, 0, 'I just thought it was weird that the mountains had flippers!', 12, 0, 100, 1, 0, 0, 0, 65639, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 3, 0, 'Does the turtle know we\'re here?', 12, 0, 100, 1, 0, 0, 0, 65640, 0, 'Hao');
INSERT INTO `creature_text` VALUES (53565, 0, 0, 'Such skill for a new trainee.', 12, 0, 100, 511, 0, 0, 0, 56431, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (64881, 4, 0, 'Hey!', 12, 0, 100, 1, 0, 0, 0, 16917, 0, 'Hao');
INSERT INTO `creature_text` VALUES (53565, 0, 1, 'That target didn\'t stand a chance.', 12, 0, 100, 511, 0, 0, 0, 56433, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 2, 'One day you will have to teach me your secrets.', 12, 0, 100, 509, 0, 0, 0, 56432, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 3, 'You are bound for a great things, trainee.', 12, 0, 100, 508, 0, 0, 0, 56435, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 4, 'I must train harder so I can be like you.', 12, 0, 100, 1, 0, 0, 0, 56437, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 5, 'I hope the instructors saw that!', 12, 0, 100, 1, 0, 0, 0, 56434, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 6, 'I must work as hard as you.', 12, 0, 100, 1, 0, 0, 0, 56436, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 0, 7, 'Your form is impeccable.', 12, 0, 100, 1, 0, 0, 0, 56430, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 1, 0, 'My money\'s on Lam. Did you see him take down Chu during training last week?', 12, 0, 100, 1, 0, 0, 0, 56420, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 2, 0, 'His stamina is nothing against Lam\'s strength.', 12, 0, 100, 0, 0, 0, 0, 56423, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 3, 0, 'He has the legs of an ox! Have you ever been kicked by him in training?', 12, 0, 100, 1, 0, 0, 0, 56424, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 4, 0, 'Raise you a keg of Stormstout Gold. It\'ll be your loss.', 12, 0, 100, 1, 0, 0, 0, 56428, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 5, 0, 'That\'s where you\'re mistaken, friend. Zhou is as sturdy as an oak.', 12, 0, 100, 1, 0, 0, 0, 56421, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 6, 0, 'Zhou has the stamina of three men, so he can out-stay most opponents.', 12, 0, 100, 0, 0, 0, 0, 56422, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (53565, 7, 0, 'Four coppers on Zhou.', 12, 0, 100, 0, 0, 0, 0, 56427, 0, 'Aspiring Trainee');
INSERT INTO `creature_text` VALUES (64881, 5, 0, 'Yeah, you should all be more like me.', 12, 0, 100, 1, 0, 0, 0, 65643, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 6, 0, 'Does that mean our moms and dads and grandparents were all explorers, too?', 12, 0, 100, 1, 0, 0, 0, 65648, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64875, 39, 0, 'Could you sing it once more, for the students who just arrived?', 12, 0, 100, 6, 0, 0, 0, 65622, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 38, 0, 'Thank you, Ruolin! That was beautiful.', 12, 0, 100, 21, 0, 0, 0, 65621, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 37, 0, 'We tell his story, so that he will always be remembered.', 12, 0, 100, 2, 0, 0, 0, 65620, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 36, 0, 'And I think he lived a very very good life. We owe everything we have to Liu Lang, the first pandaren explorer.', 12, 0, 100, 273, 0, 0, 0, 65619, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 35, 0, 'It\'s not a sad story! Liu Lang himself said, \"Never mourn a life well-lived.\"', 12, 0, 100, 1, 0, 0, 0, 65618, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 34, 0, 'Along with the budding staves of all the elders who came before us, growing now into giant trees.', 12, 0, 100, 274, 0, 0, 0, 65617, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 33, 0, 'Well, it\'s true. If you ever go to the Wood of Staves, you can see it.', 12, 0, 100, 1, 0, 0, 0, 65616, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 32, 0, 'His umbrella... sprouted! It grew roots, flowered, and became a tree!', 12, 0, 100, 5, 0, 0, 0, 65615, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 31, 0, 'He closed his eyes, and became one with the land. And then - do you know what happened?', 12, 0, 100, 2, 0, 0, 0, 65614, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 30, 0, 'He opened his umbrella, planted it in the ground, and sat underneath its cool shade.', 12, 0, 100, 6, 0, 0, 0, 65613, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 29, 0, 'Then, Liu Lang went up to the Wood of Staves, carrying with him a bamboo umbrella that he always took on his adventures.', 12, 0, 100, 1, 0, 0, 0, 65612, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 28, 0, 'Tired now, Liu Lang said goodbye to Shen-zin Su, his oldest friend.', 12, 0, 100, 1, 0, 0, 0, 65611, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 27, 0, 'Shen-zin Su has never returned to Pandaria.', 12, 0, 100, 274, 0, 0, 0, 65610, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 26, 0, 'But nobody else wanted to go exploring with him. So he left.', 12, 0, 100, 6, 0, 0, 0, 65609, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 25, 0, 'Eventually he grew very old, and visited Pandaria one final time.', 12, 0, 100, 1, 0, 0, 0, 65608, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 24, 0, 'I\'m glad you asked, Yin. Liu Lang explored the world his entire life, and raised many children here on the Wandering Isle.', 12, 0, 100, 1, 0, 0, 0, 65607, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 23, 0, 'Yes, like Chen and Li Li! They\'re out, exploring the world. Maybe someday you will, too.', 12, 0, 100, 21, 0, 0, 0, 65606, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 22, 0, 'Shen-zin Su hasn\'t made landfall for a long time. But that hasn\'t stopped some pandaren from exploring, anyway.', 12, 0, 100, 1, 0, 0, 0, 65605, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 21, 0, 'Yes Hao! Only the BRAVEST pandaren joined Liu Lang on his turtle to explore the world.', 12, 0, 100, 273, 0, 0, 0, 65604, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 20, 0, 'That\'s right! Liu Lang always had a way back home. Every five years, he would return, to pick up more explorers.', 12, 0, 100, 1, 0, 0, 0, 65603, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 19, 0, 'Does anyone remember how Liu Lang was always able to find it? Nan?', 12, 0, 100, 6, 0, 0, 0, 65602, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 18, 0, 'You are right, Yin. It IS hidden away, behind a cloak of mists.', 12, 0, 100, 274, 0, 0, 0, 65601, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 17, 0, 'He returned to the main continent of Pandaria once every five years.', 12, 0, 100, 1, 0, 0, 0, 65600, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 16, 0, 'Haha, no Yin. Liu Lang would eat wherever he explored.', 12, 0, 100, 11, 0, 0, 0, 65599, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 15, 0, 'Does anyone know how often Liu Lang went back to Pandaria?', 12, 0, 100, 1, 0, 0, 0, 65598, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 14, 0, 'Students! Students! Pay attention now.', 12, 0, 100, 22, 0, 0, 0, 65597, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 13, 0, '\"We should all be like children, for the world is our elder, and has many things to teach us.\"', 12, 0, 100, 1, 0, 0, 0, 65596, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 12, 0, 'Especially Hao! Liu Lang once said, \"Noble is he who always asks questions.\"', 12, 0, 100, 6, 0, 0, 0, 65595, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 11, 0, 'But I am certain that Shen-zin Su knows we are here. And he cares for every one of us!', 12, 0, 100, 66, 0, 0, 0, 65594, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 5, 0, 'That\'s right, the turtle was called Shen-zin Su. At first, he was only big enough for Liu Lang to sit on.', 12, 0, 100, 1, 0, 0, 0, 65588, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 4, 0, 'Does anyone remember the turtle\'s name? Yin?', 12, 0, 100, 25, 0, 0, 0, 65587, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 3, 0, 'Brave Liu Lang set out to explore the world on the back of a sea turtle.', 12, 0, 100, 1, 0, 0, 0, 65586, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 2, 0, 'It is all about his adventures.', 12, 0, 100, 273, 0, 0, 0, 65585, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 1, 0, 'The song is written in the old tongue, the language of emperors and scholars. Hardly anyone speaks it anymore.', 12, 0, 100, 6, 0, 0, 0, 65584, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 0, 0, 'This is the Song of Liu Lang, the first pandaren explorer.', 12, 0, 100, 1, 0, 0, 0, 65583, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 8, 0, 'Yes, Hao - our home is on Shen-zin Su\'s back.', 12, 0, 100, 273, 0, 0, 0, 65591, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 7, 0, 'Question, Hao?', 12, 0, 100, 25, 0, 0, 0, 65590, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 6, 0, 'He grew and grew and grew, SO big, that now some people call him \"The Wandering Isle.\"', 12, 0, 100, 1, 0, 0, 0, 65589, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 10, 0, 'That\'s a good question. Nobody has spoken to Shen-zin Su for many generations. He only ever spoke to Liu Lang.', 12, 0, 100, 1, 0, 0, 0, 65593, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (64875, 9, 0, 'Liu Lang discovered many things as he explored the world - oh - yes, Hao?', 12, 0, 100, 6, 0, 0, 0, 65592, 0, 'Lorewalker Amai');
INSERT INTO `creature_text` VALUES (55483, 0, 3, 'You no take carrot! You take turnip instead!', 12, 0, 100, 0, 0, 0, 0, 54877, 0, 'Plump Virmen');
INSERT INTO `creature_text` VALUES (55483, 0, 2, 'This virmen land!', 12, 0, 100, 0, 0, 0, 0, 54876, 0, 'Plump Virmen');
INSERT INTO `creature_text` VALUES (55483, 0, 1, 'AIIIIEEEEEEE!', 12, 0, 100, 0, 0, 0, 0, 54874, 0, 'Plump Virmen');
INSERT INTO `creature_text` VALUES (55483, 0, 0, 'Gimme all your vegetables!', 12, 0, 100, 0, 0, 0, 0, 54873, 0, 'Plump Virmen');
INSERT INTO `creature_text` VALUES (64881, 7, 0, 'I want to explore the world!', 12, 0, 100, 1, 0, 0, 0, 65651, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 8, 0, 'Oh. Well, forget it then.', 12, 0, 100, 1, 0, 0, 0, 65653, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 9, 0, 'What?', 12, 0, 100, 1, 0, 0, 0, 1338, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64881, 10, 0, 'That\'s un-possible!', 12, 0, 100, 1, 0, 0, 0, 65657, 0, 'Hao');
INSERT INTO `creature_text` VALUES (64880, 0, 0, 'What is the song about?', 12, 0, 100, 1, 0, 0, 0, 65634, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 1, 0, 'Sheesh, Hao. Haven\'t you ever paid attention?', 12, 0, 100, 1, 0, 0, 0, 65638, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 2, 0, 'Even Hao?', 12, 0, 100, 6, 0, 0, 0, 65641, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 3, 0, 'Blerch! I\'d rather jump off this bridge.', 12, 0, 100, 1, 0, 0, 0, 65644, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 4, 0, 'Because sea turtles always return to the beach where they were born!', 12, 0, 100, 1, 0, 0, 0, 65647, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 5, 0, 'Like Chen and Li Li Stormstout!', 12, 0, 100, 1, 0, 0, 0, 65650, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 6, 0, 'No you don\'t. It\'s full of trolls.', 12, 0, 100, 1, 0, 0, 0, 65652, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64880, 7, 0, 'What? What?', 12, 0, 100, 1, 0, 0, 0, 65656, 0, 'Nan');
INSERT INTO `creature_text` VALUES (64879, 0, 0, 'Shen-zin Su!', 12, 0, 100, 1, 0, 0, 0, 65635, 0, 'Yin');
INSERT INTO `creature_text` VALUES (64879, 1, 0, '...Whenever he got hungry?', 12, 0, 100, 1, 0, 0, 0, 65645, 0, 'Yin');
INSERT INTO `creature_text` VALUES (64879, 2, 0, 'But isn\'t Pandaria hidden somewhere? Nobody can find it!', 12, 0, 100, 1, 0, 0, 0, 65646, 0, 'Yin');
INSERT INTO `creature_text` VALUES (64879, 3, 0, 'But we don\'t explore very much anymore, do we?', 12, 0, 100, 1, 0, 0, 0, 65649, 0, 'Yin');
INSERT INTO `creature_text` VALUES (64879, 4, 0, 'Lorewalker! What ever happened to Liu Lang?', 12, 0, 100, 1, 0, 0, 0, 65654, 0, 'Yin');
INSERT INTO `creature_text` VALUES (64879, 5, 0, 'That\'s a sad story!', 12, 0, 100, 1, 0, 0, 0, 65658, 0, 'Yin');
INSERT INTO `creature_text` VALUES (40132, 0, 0, 'Magic is a tool, some would even say a gift. But you should never forget its fundamental nature.', 12, 0, 100, 1, 0, 0, 0, 40797, 0, 'Vestia Moonspear');
INSERT INTO `creature_text` VALUES (38225, 1, 0, 'She\'s drawing power from the fires! Stamp out the braziers, quickly!', 41, 0, 100, 0, 0, 0, 0, 38825, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38225, 2, 0, 'It be done. Our ancient enemy is defeated.', 12, 0, 100, 1, 0, 20121, 0, 38829, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38225, 3, 0, 'I been waitin\' a long time for a chance to avenge my father. A great weight has been lifted from my shoulders.', 12, 0, 100, 1, 0, 20122, 0, 38833, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (40132, 0, 1, 'Arcane magic twists the boundaries of our world. It weakens the laws of this realm to allow the impossible to happen, if only for a moment.', 12, 0, 100, 1, 0, 0, 0, 40798, 0, 'Vestia Moonspear');
INSERT INTO `creature_text` VALUES (40132, 0, 2, 'That single moment is all a demon needs to wreak havoc. The Burning Legion has watched us for millenia, always waiting for the smallest rift to slip through, the smallest stirring to whisper from.', 12, 0, 100, 1, 0, 0, 0, 40799, 0, 'Vestia Moonspear');
INSERT INTO `creature_text` VALUES (40132, 0, 3, 'Remember this, young ones. Every spell you cast, no matter how minor, cuts both ways. This is why sorcery must never be used frivolously.', 12, 0, 100, 1, 0, 0, 0, 40800, 0, 'Vestia Moonspear');
INSERT INTO `creature_text` VALUES (38225, 0, 0, 'Ya were foolish to come \'ere, Sea Witch. Ya escaped our vengeance once, but the Darkspear Tribe will not abide ya trespassin\' again.', 14, 0, 100, 1, 0, 20119, 0, 38318, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (1477, 2, 5, 'You are constantly surprising me with what a person can live through.', 12, 7, 100, 0, 0, 0, 0, 422, 0, 'Christoph Faral');
INSERT INTO `creature_text` VALUES (1477, 2, 6, 'Well, you\'re still here. I\'d say that is something worth drinking to.', 12, 7, 100, 0, 0, 0, 0, 423, 0, 'Christoph Faral');
INSERT INTO `creature_text` VALUES (1477, 2, 7, 'You can thank me anytime for making your life more interesting.', 12, 7, 100, 0, 0, 0, 0, 424, 0, 'Christoph Faral');
INSERT INTO `creature_text` VALUES (1478, 0, 0, 'Hey Reese, give me an\' Christoph another round.', 12, 7, 100, 0, 0, 0, 0, 431, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 1, 0, 'A warm tavern and a cold ale. What more could we ask for?', 12, 7, 100, 0, 0, 0, 0, 432, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 0, 'Remember when Danath gathered all the mercenaries of Stormwind together and we marched to fight at Nethergarde?', 12, 7, 100, 0, 0, 0, 0, 399, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 1, 'I tell ye, I don\'t miss the Great War at all. I remember when we fought at Darrowmere. All night in the fog, lying in a muddy trench.', 12, 7, 100, 0, 0, 0, 0, 400, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 2, 'I miss the dwarven ale we used to get at that inn in Lordaeron. Remember that fight we started there?', 12, 7, 100, 0, 0, 0, 0, 401, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 3, 'Less than a hundred of us, and over a thousand orcs. Only a handful of us managed to walk away from that one.', 12, 7, 100, 0, 0, 0, 0, 402, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 4, 'The best had to have been the look on Perenolde\'s face when our army comes marchin\' right up to his front door. What a battle!', 12, 7, 100, 0, 0, 0, 0, 403, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 5, 'Battling that band of Twilight Hammer in the Morass, I could think of better places for a war.', 12, 7, 100, 0, 0, 0, 0, 405, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 6, 'Ahh yes, now I remember, your wedding night wasn\'t it?', 12, 7, 100, 0, 0, 0, 0, 406, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 2, 7, 'Ahh the Glustewelt twins!', 12, 7, 100, 0, 0, 0, 0, 407, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 0, 'I still have the scars from that night.', 12, 7, 100, 0, 0, 0, 0, 408, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 1, 'Broke both me legs that night. How could I forget?', 12, 7, 100, 0, 0, 0, 0, 409, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 2, 'I woke up in a bed in Northshire three weeks later. Don\'t remember a damn thing.', 12, 7, 100, 0, 0, 0, 0, 410, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 3, 'Wasn\'t that the night we had to pick up my thumb and carry it in your smoke pouch?', 12, 7, 100, 0, 0, 0, 0, 411, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 4, 'I have a piece of iron in my back that will remind me of that night for the rest of my days.', 12, 7, 100, 0, 0, 0, 0, 413, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 5, 'Course I remember that night. Two inches to the left and you\'d be drinking with that elf, Morris. That was my best shirt, too.', 12, 7, 100, 0, 0, 0, 0, 414, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 6, 'Was that the third or fourth time you nearly got me gutted trying one of your crazy stunts?', 12, 7, 100, 0, 0, 0, 0, 415, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 3, 7, 'Aye and thanks for letting me carry my own hand back to the priests that night.', 12, 7, 100, 0, 0, 0, 0, 416, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 0, 'Hehe, wimp.', 12, 7, 100, 0, 0, 0, 0, 417, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 1, 'You screamed like a little girl, funniest thing I ever saw.', 12, 7, 100, 0, 0, 0, 0, 418, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 2, 'It all worked out in the end.', 12, 7, 100, 0, 0, 0, 0, 419, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 3, 'Let\'s not even begin comparing battle scars, my friend.', 12, 7, 100, 0, 0, 0, 0, 420, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 4, 'Never seen anyone move so fast in my whole life.', 12, 7, 100, 0, 0, 0, 0, 421, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 5, 'You are constantly surprising me with what a person can live through.', 12, 7, 100, 0, 0, 0, 0, 422, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 6, 'Well, you\'re still here. I\'d say that is something worth drinking to.', 12, 7, 100, 0, 0, 0, 0, 423, 0, 'Aedis Brom');
INSERT INTO `creature_text` VALUES (1478, 4, 7, 'You can thank me anytime for making your life more interesting.', 12, 7, 100, 0, 0, 0, 0, 424, 0, 'Aedis Brom');
