INSERT INTO `locales_battle_pay_entry` (`id`, `title_loc8`, `description_loc8`) VALUES
(1, "|cffff8000Продвижение", "Возможность «продвинуть» своего персонажа на текущей учетной записи или создать нового с некоторыми бонусами.\n\nВ качестве бонусов вы получите:\n- iLevel 408 экипировку\n- iLevel 408 оружие\n- iLevel 408 аксессуары и плащ\n- 1.000 золота\n- Мгновенный 90 уровень\n- Верхового животного: рыжий ветрокрыл (орда) или белоснежный грифон (альянс)"),
(2, "|cffff8000Продвижение", "Возможность «продвинуть» своего персонажа на текущей учетной записи или создать нового с некоторыми бонусами.\n\nВ качестве бонусов вы получите:\n- iLevel 408 экипировку\n- iLevel 408 оружие\n- iLevel 408 аксессуары и плащ\n- 1.000 золота\n- Мгновенный 90 уровень\n- Верхового животного: рыжий ветрокрыл (орда) или белоснежный грифон (альянс)"),
